INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (36,7389802,'d31c5c1d83f5f71ea314f95fa18982b3','','tsref:->split','a:1:{s:4:\"rows\";a:7:{i:0;a:6:{s:8:\"property\";s:5:\"token\";s:8:\"datatype\";s:12:\"str /stdWrap\";s:11:\"description\";s:51:\"string or character (token) used to split the value\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:3:\"max\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:20:\"max number of splits\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:3:\"min\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:21:\"min number of splits.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:9:\"returnKey\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:87:\"Instead of parsing the split result, just return this element of the index immediately.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:7:\"cObjNum\";s:8:\"datatype\";s:30:\"<i>cObjNum</i><br>+optionSplit\";s:11:\"description\";s:120:\"This is a pointer the array of this object (&quot;1,2,3,4&quot;), that should treat the items, resulting from the split.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:7:\"1,2,3,4\";s:8:\"datatype\";s:20:\"-&gt;CARRAY /stdWrap\";s:11:\"description\";s:537:\"The object that should treat the value. <b><br>NOTE: The &quot;current&quot;-value is set to the value of current item, when the objects are called. See &quot;stdWrap&quot; / current.</b><b><br>&nbsp;</b><b><br>Example (stdWrap used):</b><br>1.current = 1<br>1.wrap = &lt;B&gt; | &lt;/B&gt;<br>&nbsp;<b><br>Example (CARRAY used):</b><br>1 {<br>&nbsp;&nbsp;&nbsp;10 = TEXT<br>&nbs