INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (37,7389802,'c3abe7b3e50e8c5a3ec49c6fc37e7c6c','','tsref:->if','a:1:{s:4:\"rows\";a:10:{i:0;a:6:{s:8:\"property\";s:6:\"isTrue\";s:8:\"datatype\";s:12:\"str /stdWrap\";s:11:\"description\";s:70:\"If the content is &quot;true&quot;.... (not empty string and not zero)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:7:\"isFalse\";s:8:\"datatype\";s:12:\"str /stdWrap\";s:11:\"description\";s:54:\"If the content is &quot;false&quot;... (empty or zero)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:10:\"isPositive\";s:8:\"datatype\";s:22:\"int /stdWrap<br>+ calc\";s:11:\"description\";s:40:\"returns false if content is not positive\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:13:\"isGreaterThan\";s:8:\"datatype\";s:14:\"value /stdWrap\";s:11:\"description\";s:63:\"returns false if content is not greater than &quot;.value&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:10:\"isLessThan\";s:8:\"datatype\";s:14:\"value /stdWrap\";s:11:\"description\";s:60:\"returns false if content is not less than &quot;.value&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:6:\"equals\";s:8:\"datatype\";s:14:\"value /stdWrap\";s:11:\"description\";s:58:\"returns false if content does not equal &quot;.value&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:8:\"isInList\";s:8:\"datatype\";s:14:\"value /stdWrap\";s:11:\"description\";s:152:\"returns false if content is not in the comma-separated list &quot;.value&quot;.<br>The list in &quot;.value&quot; may not have spaces between elements!!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:5:\"value\";s:8:\"datatype\";s:14:\"value /stdWrap\";s:11:\"description\";s:56:\"&quot;value&quot; (the comparison value mentioned above)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:6:\"negate\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:125:\"This negates the result just before it exits. So if anything above returns true the overall returns ends up returning false!!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:12:\"directReturn\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:120:\"If this property exists the true/false of this value is returned. Could be used to set true/false by TypoScript constant\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (38,7389802,'8d5bffcf7ab541700c652a5edf894713','','tsref:->typolink','a:1:{s:4:\"rows\";a:15:{i:0;a:6:{s:8:\"property\";s:9:\"extTarget\";s:8:\"datatype\";s:15:\"target /stdWrap\";s:11:\"description\";s:30:\"target used for external links\";s:7:\"default\";s:4:\"_top\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"target\";s:8:\"datatype\";s:15:\"target /stdWrap\";s:11:\"description\";s:30:\"target used for internal links\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:8:\"no_cache\";s:8:\"datatype\";s:16:\"boolean /stdWrap\";s:11:\"description\";s:56:\"Adds a &quot;&amp;no_cache=1&quot;-parameter to the link\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:12:\"useCacheHash\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:532:\"If set, the additionalParams list is exploded and calculated into a hashstring appended to the url, like &#x201c;&amp;cHash=ae83fd7s87&#x201d;. When the caching mechanism sees this value, it calculates the same value on the server based on incoming values in HTTP_GET_VARS, excluding id,type,no_cache,ftu,cHash,MP values. If the incoming cHash value matches the calculated value, the page may be cached based on this.<br>The [SYS][encryptionKey] is included in the hash in order to make it unique for the server and non-predictable.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:16:\"additionalParams\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:556:\"This is parameters that are added to the end of the url. This must be code ready to insert after the last parameter. <br>&nbsp;<b><br>Example:</b><br>&#039;&amp;print=1&#039;<br>&#039;&amp;sword_list[]=word1&amp;sword_list[]=word2&#039;<br>&nbsp;<b><br>Applications:</b><br>This is very useful when linking to pages from a searchresult. The searchwords are stored in the register-key SWORD_PARAMS and can be insert directly like this:<br>.additionalParams.data = register:SWORD_PARAMS<br>&nbsp;<br>&nbsp;<b>NOTE:</b> This is only active for internal links!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:14:\"addQueryString\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:884:\"Add the QUERY_STRING to the start of the link. Notice that this does not check for any duplicate parameters! This is not a problem (only the last parameter of the same name will be applied), but enable &quot;config.uniqueLinkVars&quot; if you still don&#039;t like it.<br>&nbsp;<br>&nbsp;<b>.method: </b>If set to to GET or POST then then the parsed query arguments (GET or POST data) will be used. This settings are useful if you use URL processing extensions like Real URL, which translate part of the path into query arguments.<br>It&#039;s also possible to get both, POST and GET data, on setting this to<br>&quot;POST,GET&quot; or &quot;GET,POST&quot;. The last method in this sequence takes<br>precedence and overwrites the parts that are also present for the first<br>method.<br>&nbsp;<br>&nbsp;<b>.exclude: </b>List of query arguments to exclude from the link (eg L or cHash).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:16:\"Wraps the links.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:14:\"ATagBeforeWrap\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:91:\"If set, the link is first wrapped with &quot;<i>.wrap&quot;</i> and then the &lt;A&gt;-tag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:9:\"parameter\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:4199:\"This is the data, that -&gt;typolink uses to create the link. The value is trimmed and if it&#039;s empty, -&gt;typolink returns the input value untouched.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> If used from parseFunc, this value should be imported by:<br>typolink.parameter.data = parameters : allParams<br>&nbsp;<b><br>Examples:</b><br>Internal links:<br>integers (51): creates a link to page with uid = 51<br>filerefs (fileadmin/somedir/thedoc.html): creates a link to the file on the local server.<br>strings (some_alias): creates a link to the page with alias = &quot;some_alias&quot;<br>&nbsp;<br>External links:<br>email-adresses (name@email.com): creates a link to the email-addr.<br>domains (www.domain.com): creates link to http://-page<br>&nbsp;<b><br>The input is parsed like this:</b><br>First the parameter is splitted by character-space. This provides a way to pass more parameters. See &quot;target&quot; below here.<br>If a &quot;@&quot; is in the string, it&#039;s an email<br>If a period (.) is in the string AND if the period (.) is found before a slash (/) is found OR if a doubleslash is found, then it&#039;s a URL<br>If a slash (/) is found, it&#039;s a filereference. If the file/directory does not exist on the server, the link is NOT made!<br>&nbsp;<br>Now the input can be an alias or page-id. If the input is an integer it&#039;s a page-id, if it&#039;s two comma separated integers, it&#039;s a id/type pair, else it&#039;s an alias. For page-id&#039;s or aliases you can prepend a &quot;#&quot; mark with a number indication tt_content record number on the page to jump to! (if .section-property is present, it overrides this).<br>If you insert only &quot;#234&quot; where &quot;234&quot; is the tt_content record number, it links to the current page-id<i><br>Notice: The parameter can contain a keyword that hands over link generation to an external function. See example below this table!</i><b><br>&nbsp;</b><b><br>Target</b><br>Target is normally defined by the &quot;extTarget&quot; and &quot;target&quot; properties of typolink. But you may override this target by adding the new target after the parameter separated by a whitespace. Thus the target becomes the second parameter.<br>If the &#x201c;Target&#x201d; parameter is set to the &#x201c;-&#x201d; character, then it&#039;s the same as no target passed to the function. This feature enables you to still pass a class as third parameter and title as fourth parameter without setting the target also.<br>&nbsp;<b><br>Open in windows with fixed dimensions (JavaScript)</b><br>It is possible to open the link in a window opened by JavaScript (with &#x201c;window.open&#x201d;). For this, just set the target value to &#x201c;123x456&#x201d; where 123 is the window width and 456 is the window height. You can also specify additional parameters to the function by entering them separated from the width and height with a colon &#x201c;:&#x201d;. For instance &#x201c;230x450:resizable=0,location=1&#x201d; will disable resizing of the window and enable the location bar.<br>Also see property &#x201c;JSwindow&#x201d;.<br>&nbsp;<b><br>Class</b><br>If you specify a third parameter separated by whitespace in the parameter value this becomes the class-parameter of the link. This class parameter is inserted in the link-tag before any values from .ATagParams which means this class value will override any class value set in ATagParams (at least for MSIE). If set to &#x201c;-&#x201d;, then it&#039;s the same as no class passed to the function. This feature enables you to still pass a title as fourth parameter without setting the class also.<br>&nbsp;<b><br>Title</b><br>The title attribute is normally specified via .ATagParams or directly via the .title property. But you may override this value by adding the desired title as the fourth parameter (parameters separated by whitespace) to typolink.<br>&nbsp;<b><br>Examples of multiparameters:</b><br>Consider this .parameter value passed to this function:<br>&nbsp;<br>51 _blank blueLink<br>&nbsp;<br>&nbsp;This would result in a link approx like this:<br>&nbsp;<br>&lt;A href=&#x201d;?id=51&#x201d; target=&#x201d;_blank&#x201d; class=&#x201d;blueLink&#x201d;&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:5:\"title\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:38:\"Sets the title parameter of the A-tag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:15:\"JSwindow_params\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:170:\"Preset values for opening the window. This example lists almost all possible attributes:<br>status=1,menubar=1,scrollbars=1,resizable=1,location=1,directories=1,toolbar=1\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:10:\"returnLast\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:263:\"If set to &quot;url&quot; then it will return the URL of the link ($this-&gt;lastTypoLinkUrl)<br>If set to &quot;target&quot; it will return the target of the link.<br>So, in these two cases you will not get the value wrapped but the url or target value returned!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:7:\"section\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:221:\"If this value is present, it&#039;s prepended with a &quot;#&quot; and placed after any internal url to another page in TYPO3.<br>This is used create a link, which jumps from one page directly the section on another page.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:10:\"ATagParams\";s:8:\"datatype\";s:38:\"&nbsp;<i>&lt;A&gt;-params</i> /stdWrap\";s:11:\"description\";s:81:\"Additional parameters<br>&nbsp;<b><br>Example:</b><br>class=&#x201d;board&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:8:\"userFunc\";s:8:\"datatype\";s:13:\"function-name\";s:11:\"description\";s:954:\"This passes the link-data compiled by the typolink function to a user-defined function for final manipulation.<br>The $content variable passed to the user-function (first parameter) is an array with the keys &#x201c;TYPE&#x201d;, &#x201c;TAG&#x201d;, &#x201c;url&#x201d;, &#x201c;targetParams&#x201d; and &#x201c;aTagParams&#x201d;. <br>TYPE is an indication of link-kind: mailto, url, file, page<br>TAG is the full &lt;A&gt;-tag as generated and ready from the typolink function.<br>The latter three is combined into the &#039;TAG&#039; value after this formula:<br>&nbsp;<br>&lt;a href=&quot;&#039;.$finalTagParts[&#039;url&#039;].&#039;&quot;&#039;.<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$finalTagParts[&#039;targetParams&#039;].<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$finalTagParts[&#039;aTagParams&#039;].&#039;&gt;<br>&nbsp;<br>The userfunction must return an &lt;A&gt;-tag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (39,7389802,'80780565cc3f65439bc9a712a1a05942','','tsref:->textStyle','a:1:{s:4:\"rows\";a:11:{i:0;a:6:{s:8:\"property\";s:11:\"align.field\";s:8:\"datatype\";s:5:\"align\";s:11:\"description\";s:46:\"Set to fieldname from the $cObj-&gt;data-array\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:30:\"face.field<br>&nbsp;<br>&nbsp;\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:200:\"Set to fieldname from the $cObj-&gt;data-array<br>&nbsp;<br>[1] = &quot;Times New Roman&quot;;<br>[2] = &quot;Verdana,Arial,Helvetica,Sans serif&quot;;<br>[3] = &quot;Arial,Helvetica,Sans serif&quot;;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:12:\"face.default\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:24:\"[default] = User defined\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:30:\"size.field<br>&nbsp;<br>&nbsp;\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:144:\"Set to fieldname from the $cObj-&gt;data-array<br>&nbsp;<br>[1] = 1;<br>[2] = 2;<br>[3] = 3;<br>[10] = &quot;+1&quot;;<br>[11] = &quot;-1&quot;;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:12:\"size.default\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:24:\"[default] = User defined\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:41:\"color.field<br>&nbsp;<br>&nbsp;<br>&nbsp;\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:112:\"Set to fieldname from the $cObj-&gt;data-array<br>&nbsp;<br>See &quot;content.php&quot; for the colors available\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:13:\"color.default\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:24:\"[default] = User defined\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:18:\"color.1<br>color.2\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:22:\"[1],[2] = User defined\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:16:\"properties.field\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:249:\"Set to fieldname from the $cObj-&gt;data-array<br>&nbsp;<br>The property values goes like this:<br>bit 0: &lt;B&gt;<br>bit 1: &lt;I&gt;<br>bit 2: &lt;U&gt;<br>bit 3: (uppercase)<br>&nbsp;<br>Thus a value of 5 would result in bold and underlined text\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:18:\"properties.default\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:88:\"[default] = User defined (This value will be used whenever &quot;.field&quot; is false!)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:7:\"altWrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:268:\"If this value is set, the wrapping with a font-tag based on font,size and color is NOT done. Rather the element is wrapped with this value.<br>Use it to assign a stylesheet by setting this value to eg.<br>&nbsp;<br>&lt;div class=&#x201d;text&#x201d;&gt; | &lt;/div&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (40,7389802,'5117f2da4a9bf819b7f78f176d3ede8e','','tsref:->encapsLines','a:1:{s:4:\"rows\";a:9:{i:0;a:6:{s:8:\"property\";s:13:\"encapsTagList\";s:8:\"datatype\";s:15:\"list of strings\";s:11:\"description\";s:374:\"List of tags which qualify as encapsulating tags. Must be lowercase.<br>&nbsp;<b><br>Example:</b><br>encapsTagList = div, p<br>&nbsp;<br>This setting will recognize the red line below as encapsulated lines:<br>&nbsp;<br>First line of text<br>Some &lt;div&gt;text&lt;/div&gt;<br>&lt;p&gt;Some text&lt;/p&gt;<br>&lt;div&gt;Some text&lt;/div&gt;<br>&lt;B&gt;Some text&lt;/B&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:25:\"remapTag.[<i>tagname</i>]\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:440:\"Enter a new tag name here if you wish the tagname of any encapsulation to be unified to a single tag name.<br>&nbsp;<br>For instance, setting this value to &#x201c;remapTags.P=DIV&#x201d; would convert:<br>&nbsp;<br>&nbsp;&lt;p&gt;Some text&lt;/p&gt;<br>&lt;div&gt;Some text&lt;/div&gt;<br>&nbsp;<br>to <br>&nbsp;<br>&nbsp;&lt;div&gt;Some text&lt;/div&gt;<br>&lt;div&gt;Some text&lt;/div&gt;<br>&nbsp;<br>([<i>tagname</i>] is in uppercase.)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:30:\"addAttributes.[<i>tagname</i>]\";s:8:\"datatype\";s:16:\"array of strings\";s:11:\"description\";s:554:\"Attributes to set in the encapsulation tag.<b><br>&nbsp;</b><b><br>Example:</b><br>addAttributes.P {<br>&nbsp;&nbsp;&nbsp;style=padding-bottom:0px; margin-top:1px; margin-bottom:1px;<br>&nbsp;&nbsp;&nbsp;align=center<br>}<br>&nbsp;<br>([<i>tagname</i>] is in uppercase.)<br>&nbsp;<br>.setOnly = <br>exists : This will set the value ONLY if the property does not already exist<br>blank : This will set the value ONLY if the property does not already exist OR is blank (&#x201c;&#x201d;)<br>&nbsp;<br>Default is to always override/set the attributes value.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:14:\"removeWrapping\";s:8:\"datatype\";s:6:\"boolen\";s:11:\"description\";s:391:\"If set, then all existing wrapping will be removed.<br>&nbsp;<br>This:<br>&nbsp;<br>First line of text<br>Some &lt;div&gt;text&lt;/div&gt;<br>&lt;p&gt;Some text&lt;/p&gt;<br>&lt;div&gt;Some text&lt;/div&gt;<br>&lt;B&gt;Some text&lt;/B&gt;<br>&nbsp;<br>becomes this:<br>&nbsp;<br>First line of text<br>Some &lt;div&gt;text&lt;/div&gt;<br>Some text<br>Some text<br>&lt;B&gt;Some text&lt;/B&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:19:\"wrapNonWrappedLines\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:304:\"Wrapping for non-encapsulated lines<br>&nbsp;<b><br>Example:</b><br>.wrapNonWrappedLines = &lt;P&gt;|&lt;/P&gt;<br>&nbsp;<br>This:<br>&nbsp;<br>First line of text<br>&lt;p&gt;Some text&lt;/p&gt;<br>&nbsp;<br>becomes this:<br>&nbsp;<br>&lt;P&gt;First line of text&lt;/P&gt;<br>&lt;p&gt;Some text&lt;/p&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:16:\"innerStdWrap_all\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:73:\"Wraps the content inside all lines, whether they are encapsulated or not.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:35:\"encapsLinesStdWrap.[<i>tagname</i>]\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:87:\"Wraps the content inside all encapsulated lines.<br>([<i>tagname</i>] is in uppercase.)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:12:\"defaultAlign\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:156:\"If set, this value is set as the default &#x201c;align&#x201d; value of the wrapping tags, both from .encapsTagList, .bypassEncapsTagList and .nonWrappedTag\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:13:\"nonWrappedTag\";s:8:\"datatype\";s:7:\"tagname\";s:11:\"description\";s:343:\"For all non-wrapped lines, you can set here which tag it should be wrapped in. Example would be &#x201c;P&#x201d;. This is an alternative to .wrapNonWrappedLines and has the advantage that it&#039;s attributes are set by .addAttributes as well as defaultAlign. Thus you can easier match the wrapping tags used for nonwrapped and wrapped lines.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (41,7389802,'1678f0ea79537a75222cb478bbf92623','','tsref:->tableStyle','a:1:{s:4:\"rows\";a:7:{i:0;a:6:{s:8:\"property\";s:5:\"align\";s:8:\"datatype\";s:14:\"align /stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"border\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:11:\"cellspacing\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:11:\"cellpadding\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:11:\"color.field\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:46:\"Set to fieldname from the $cObj-&gt;data-array\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:35:\"color.default<br>color.1<br>color.2\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:42:\"[default],[1],[2] = User defined<br>&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:6:\"params\";s:8:\"datatype\";s:20:\"&lt;TABLE&gt;-params\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (42,7389802,'91a86be2eb43c2534b3785464471e0c3','','tsref:->addParams','a:1:{s:4:\"rows\";a:2:{i:0;a:6:{s:8:\"property\";s:7:\"_offset\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:143:\"Use this to define which tag you want to manipulate.<br>1 is the first tag in the input, 2 is the second, -1 is the last, -2 is the second last\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:25:\"<i>(array of strings)</i>\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:284:\"This defines the content of each added property to the tag.<br>If there is a tag-property with this name already (case-sensitive!) that property will be overridden! <br>If the returned value is a blank string (but not zero!) then the existing (if any) property will not be overridden.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (43,7389802,'f7e884f75020be6aeaf62ddc5e89bce0','','tsref:->filelink','a:1:{s:4:\"rows\";a:17:{i:0;a:6:{s:8:\"property\";s:4:\"path\";s:8:\"datatype\";s:13:\"path /stdWrap\";s:11:\"description\";s:45:\"<b>Example:</b><br>&quot;uploads/media/&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:4:\"icon\";s:8:\"datatype\";s:16:\"boolean /stdWrap\";s:11:\"description\";s:27:\"Set if icon should be shown\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:19:\"icon_image_ext_list\";s:8:\"datatype\";s:30:\"<i>list of imageextensions</i>\";s:11:\"description\";s:74:\"This is the extensions that should render as thumbsnails instead of icons.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:11:\"iconCObject\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:164:\"Enter a cObject to use alternatively for the icons, eg. IMAGE type.<br>If this is set, it&#039;ll substitute the use of the thumbs-script for display of thumbnails.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:9:\"icon_link\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:33:\"If the icon should be linked also\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:12:\"labelStdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:177:\"stdWrap options for the label (by default the label is the filename) before being wrapped with the A-tags.<br>Use this to eg. import another label from a database field or such.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:16:\"Wraps the links.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:14:\"ATagBeforeWrap\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:91:\"If set, the link is first wrapped with &quot;<i>.wrap&quot;</i> and then the &lt;A&gt;-tag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:4:\"file\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:97:\"stdWrap of the label (by default the label is the filename) after having been wrapped with A-tag!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:4:\"size\";s:8:\"datatype\";s:16:\"boolean /stdWrap\";s:11:\"description\";s:27:\"Set if size should be shown\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:7:\"jumpurl\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:949:\"Decides if the link should call the script with the jumpurl paramter in order to register any clicks in the stat.<br>This has the advantage that any clicks on the file will register in the stat.<br>The disadvantage is, that users cant right-click and select &quot;Save Target As&quot; in the browser.<b><br>&nbsp;</b><b><br>Properties:</b><br>.secure (boolean)If set, then the file pointed to by jumpurl is NOT redirected to, but rather it&#039;s read from the file and returned with a correct header. This option adds a hash and locationData to the url and there MUST be access to the record in order to download the file. If the fileposition on the server is furthermore secured by a .htaccess file preventing ANY access, you&#039;ve got secure download here!<br>&nbsp;<br>.secure.mimeTypes (list of mimetypes, syntax [ext] = [mimetype]<br>&nbsp;<b><br>Example:</b><br>.secure = 1<br>.secure.mimeTypes = pdf=application/pdf, doc=application/msword\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:6:\"target\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:10:\"ATagParams\";s:8:\"datatype\";s:38:\"&nbsp;<i>&lt;A&gt;-params</i> /stdWrap\";s:11:\"description\";s:81:\"Additional parameters<br>&nbsp;<b><br>Example:</b><br>class=&#x201d;board&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:22:\"removePrependedNumbers\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:90:\"if set, any 2-digit prepended numbers (&#x201c;eg _23&#x201d;) in the filename is removed.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:20:\"altText<br>titleText\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:212:\"For icons (image made with &quot;iconCObject&quot; must have their own properties)<br>&nbsp;<br>If no titltext is specified, it will use the alttext insteadIf no alttext is specified, it will use an empty alttext\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:11:\"longdescURL\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:189:\"For icons (image made with &quot;iconCObject&quot; must have their own properties)<br>&nbsp;<br>&quot;longdesc&quot; attribute (URL pointing to document with extensive details about image).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (44,7389802,'ce1240f8c0d72bb639611ea407625c88','','tsref:->parseFunc','a:1:{s:4:\"rows\";a:13:{i:0;a:6:{s:8:\"property\";s:15:\"externalBlocks \";s:8:\"datatype\";s:28:\"list of tagnames/+properties\";s:11:\"description\";s:3527:\"This allows you to pre-split the content passed to parseFunc so that only content outside the blocks with the given tags is parsed.<b><br>Extra properties:</b><br>&nbsp;<b>.[tagname]</b> {<br>&nbsp;&nbsp;&nbsp;<b>callRecursive</b> = [boolean]; If set, the content of the block is directed into parseFunc again. Otherwise the content is just passed through with no other processing than stdWrap (see below)<br>&nbsp;&nbsp;&nbsp;<b>callRecursive.dontWrapSelf </b>= [boolean]; If set, the tags of the block is <i>not</i> wrapped around the content returned from parseFunc.<br>&nbsp;&nbsp;&nbsp;<b>callRecursive.alternativeWrap</b> = Alternative wrapping instead of the original tags.<br>&nbsp;&nbsp;&nbsp;<b>callRecursive.tagStdWrap</b> = -&gt;stdWrap processing of the block-tags.<br>&nbsp;&nbsp;&nbsp;<b>stdWrap </b>= -&gt;stdWrap processing of the whole block (regardless of whether callRecursive was set.)<br>&nbsp;&nbsp;&nbsp;<b>stripNLprev </b>= [boolean]; Strips off last linebreak of the previous outside block<br>&nbsp;&nbsp;&nbsp;<b>stripNLnext </b>= [boolean]; Strips off first linebreak of the next outside block<br>&nbsp;&nbsp;&nbsp;<b>stripNL </b>= [boolean]: Does both of the above.<br>&nbsp;<br>&nbsp;&nbsp;&nbsp;&nbsp;= [boolean]; If set, then the content is expected to be a table and every table-cell is traversed.<br>&nbsp;&nbsp;&nbsp;&nbsp;# Below, default is all cells and 1,2,3... overrides for specific cols.<br>&nbsp;&nbsp;&nbsp;&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>callRecursive </b>= [boolean]; The content is parsed through current parseFunc<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>stdWrap </b>= -&gt;stdWrap processing of the content in the cell<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>tagStdWrap</b> = -&gt; The &lt;TD&gt; tag is processed by -&gt;stdWrap<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;<b>HTMLtableCells.addChr10BetweenParagraphs </b>= [boolean]; If set, then all &lt;/P&gt;&lt;P&gt; appearances will have a chr(10) inserted between them<br>}<br>&nbsp;<b><br>Example:</b><br>This example is used to split regular bodytext content so that tables and blockquotes in the bodytext are processed correctly. The blockquotes are passed into parseFunc again (recursively) and further their top/bottom margins are set to 0 (so no apparent linebreaks are seen)<br>The tables are also displayed with a number of properties of the cells overridden.<br>tt_content.text.20.parseFunc.externalBlocks {<br>&nbsp;&nbsp;&nbsp;blockquote.callRecursive=1<br>&nbsp;&nbsp;&nbsp;blockquote.callRecursive.tagStdWrap.HTMLparser = 1<br>&nbsp;&nbsp;&nbsp;blockquote.callRecursive.tagStdWrap.HTMLparser {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;tags.blockquote.fixAttrib.style.list = margin-bottom:0;margin-top:0;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;tags.blockquote.fixAttrib.style.always=1<br>&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;blockquote.stripNLprev=1<br>&nbsp;&nbsp;&nbsp;blockquote.stripNLnext=1<br>&nbsp;<br>&nbsp;&nbsp;&nbsp;table.stripNL=1<br>&nbsp;&nbsp;&nbsp;table.stdWrap.HTMLparser = 1<br>&nbsp;&nbsp;&nbsp;table.stdWrap.HTMLparser {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;tags.table.overrideAttribs = border=0 cellpadding=2 cellspacing=1 style=&quot;margin-top:10px; margin-bottom:10px;&quot;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;tags.tr.allowedAttribs=0<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;tags.td.overrideAttribs = valign=top bgcolor=&quot;#eeeeee&quot; style=&quot;font-family : Verdana, Geneva, Arial, Helvetica, sans-serif;font-size : 10px;&quot;<br>&nbsp;&nbsp;&nbsp;}<br>}\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:9:\"constants\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:346:\"The toplevel-defined constants will be substituted in the text. The constant-name is wrapped in &quot;###&quot;.<br>&nbsp;<b><br>Example:</b><br>constants.EMAIL = <i>email@email.com</i><br>(NOTE: This is toplevel TypoScript!)<br>All cases of the string ###EMAIL### will be substituted in the text. The constants are defined as a toplevel object. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:5:\"short\";s:8:\"datatype\";s:23:\"<i>array of strings</i>\";s:11:\"description\";s:346:\"Like constants above, but local.<br>&nbsp;<b><br>Example:</b><br>This substitutes all occurencies of &#x201c;T3&#x201d; with &#x201c;TYPO3 CMS&#x201d; and &#x201c;T3web&#x201d; with a link to typo3.com.<br>short {<br>&nbsp;&nbsp;&nbsp;T3 = TYPO3 CMS<br>&nbsp;&nbsp;&nbsp;T3web = &lt;a href=&#x201d;http://typo3.com&#x201d;&gt;typo3&lt;/a&gt;<br>}\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:16:\"plainTextStdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:52:\"This is stdWrap properties for all non-tag content. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:8:\"userFunc\";s:8:\"datatype\";s:13:\"function name\";s:11:\"description\";s:185:\"This passes the non-tag content to a function of your own choice. Similar to eg. .postUserFunc in stdWrap.<br>Remember the function name must possibly be prepended &#x201c;user_&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:17:\"nonTypoTagStdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:250:\"Like .plainTextStdWrap. Difference: <br>.plainTextStdWrap works an ALL non-tag pieces in the text. .nonTypoTagStdWrap is post processing of all text (including tags) between special TypoTags (unless .breakoutTypoTagContent is not set for the TypoTag)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:18:\"nonTypoTagUserFunc\";s:8:\"datatype\";s:13:\"function name\";s:11:\"description\";s:276:\"Like .userFunc. Differences is (like nonTypoTagStdWrap) that this is post processing of all content pieces around TypoTags while .userFunc processes all non-tag content. (Notice: .breakoutTypoTagContent must be set for the TypoTag if it&#039;s excluded from nonTypoTagContent)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:5:\"sword\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:203:\"Marks up any words from the GET-method send array sword_list[] in the text. The word MUST be at least two characters long!<br>&nbsp;<b>NOTE:</b> works only with $GLOBALS[&quot;TSFE&quot;]-&gt;no_cache==1\";s:7:\"default\";s:48:\"&lt;font color=&quot;red&quot;&gt;|&lt;/font&gt;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:9:\"makelinks\";s:8:\"datatype\";s:24:\"boolean / -&gt;makelinks\";s:11:\"description\";s:115:\"Convert webadresses prefixed with &quot;http://&quot; and mail-adresses prefixed with &quot;mailto:&quot; to links.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:4:\"tags\";s:8:\"datatype\";s:16:\"<i>-&gt;tags</i>\";s:11:\"description\";s:80:\"Here you can define <b>custom tags</b> that will parse the content to something.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:9:\"allowTags\";s:8:\"datatype\";s:15:\"list of strings\";s:11:\"description\";s:125:\"List of tags, which are allowed to exist in code! <br>Highest priority: If a tag is found in allowTags, denyTags is ignored!!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:8:\"denyTags\";s:8:\"datatype\";s:15:\"list of strings\";s:11:\"description\";s:388:\"List of tags, which may NOT exist in code! (use &quot;*&quot; for all.)<br>Lowest priority: If a tag is NOT found in allowTags, denyTags is checked. If denyTags is not &quot;*&quot; and the tag is not found in the list, the tag may exist!<br>&nbsp;<b><br>Example:</b><br>This allows &lt;B&gt;, &lt;I&gt;, &lt;A&gt; and &lt;IMG&gt; -tags to exist<br>.allowTags = b,i,a,img<br>.denyTags = *\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:2:\"if\";s:8:\"datatype\";s:7:\"-&gt;if\";s:11:\"description\";s:85:\"if &quot;if&quot; returns false the input value is not parsed, but returned directly.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (45,7389802,'87d6057bd7b0beb18019a72c170a32fa','','tsref:->makelinks','a:1:{s:4:\"rows\";a:8:{i:0;a:6:{s:8:\"property\";s:14:\"http.extTarget\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:22:\"The target of the link\";s:7:\"default\";s:4:\"_top\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:9:\"http.wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:20:\"wrap around the link\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:19:\"http.ATagBeforeWrap\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:83:\"If set, the link is first wrapped with <i>http.wrap</i> and then the &lt;A&gt;-tag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:9:\"http.keep\";s:8:\"datatype\";s:66:\"list: <i>&quot;scheme&quot;,&quot;path&quot;,&quot;query&quot;</i>\";s:11:\"description\";s:381:\"As default the link-text will be the full domain-name of the link. <br>&nbsp;<br>&nbsp;<b>Examples</b>:<br>http://www.webaddress.rld/test/doc.php?id=3 <br>&quot;&quot;: www.webaddress.rld<br>&quot;scheme&quot;: http://www.webaddress.rld<br>&quot;scheme,path&quot;: http://www.webaddress.rld/test/doc.php<br>&quot;scheme,path,query&quot;: http://www.webaddress.rld/test/doc.php?id=3\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:15:\"http.ATagParams\";s:8:\"datatype\";s:38:\"&nbsp;<i>&lt;A&gt;-params</i> /stdWrap\";s:11:\"description\";s:81:\"Additional parameters<br>&nbsp;<b><br>Example:</b><br>class=&#x201d;board&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:11:\"mailto.wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:20:\"wrap around the link\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:21:\"mailto.ATagBeforeWrap\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:85:\"If set, the link is first wrapped with mailto<i>.wrap</i> and then the &lt;A&gt;-tag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:17:\"mailto.ATagParams\";s:8:\"datatype\";s:38:\"&nbsp;<i>&lt;A&gt;-params</i> /stdWrap\";s:11:\"description\";s:81:\"Additional parameters<br>&nbsp;<b><br>Example:</b><br>class=&#x201d;board&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (46,7389802,'a465bb5ed515df37786b15b6049198e8','','tsref:->tags','a:1:{s:4:\"rows\";a:1:{i:0;a:6:{s:8:\"property\";s:15:\"<i>Array...</i>\";s:8:\"datatype\";s:58:\"cObject <i>+stripNL</i><i><br>+ breakoutTypoTagContent</i>\";s:11:\"description\";s:1226:\"Every entry in the <i>Array...</i> corresponds to a tag, that will be parsed. The elements MUST be in lowercase. <br>Every entry must be set to a content-object.<br>&quot;current&quot; is set to the content of the tag, eg &lt;TAG&gt;content&lt;/TAG&gt;: here &quot;current&quot; is set to &quot;content&quot;.<b><br>Parameters:</b><br>Parameters of the tag is set in $cObj-&gt;parameters (key is lowercased):<br>&lt;TAG COLOR=&quot;red&quot;&gt;content&lt;/TAG&gt;<br>=&gt; $cObj-&gt;parameters[color] = red <b><br>Special added properties to the content-object:</b><br>$cObj-&gt;parameters[allParams]: this is automatically set to the whole parameter-string of the tag, eg &#039; color=&quot;red&quot;&#039;<br>[cObject].stripNL: is a boolean option, which tells <i>parseFunc</i> that NewLines before and after content of the tag should be stripped.<br>[cObject].breakoutTypoTagContent: is a boolean option, which tells parseFunc that this block of content is breaking up the nonTypoTag content and that the content after this must be re-wrapped.<br>&nbsp;<b><br>Examples:</b><br>tags.bold = TEXT<br>tags.bold {<br>&nbsp;&nbsp;&nbsp;current = 1<br>&nbsp;&nbsp;&nbsp;wrap = &lt;B&gt; | &lt;/B&gt;<br>}<br>tags.bold.stripNL = 1\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (47,7389802,'eb48183e6181b87770ebe8ae2364c70d','','page:->HTMLparser; tsref:->HTMLparser','a:1:{s:4:\"rows\";a:10:{i:0;a:6:{s:8:\"property\";s:9:\"allowTags\";s:8:\"datatype\";s:12:\"list of tags\";s:11:\"description\";s:20:\"Default allowed tags\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:14:\"tags.[tagname]\";s:8:\"datatype\";s:28:\"boolean/-&gt;HTMLparser_tags\";s:11:\"description\";s:212:\"Either set this property to 0 or 1 to allow or deny the tag. If you enter -&gt;HTMLparser_tags properties, those will automatically overrule this option, thus it&#039;s not needed then.<br>[tagname] in lowercase.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:12:\"localNesting\";s:8:\"datatype\";s:42:\"list of tags, must be among preserved tags\";s:11:\"description\";s:100:\"List of tags (among the already set tags), which will be forced to have the nesting-flag set to true\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:13:\"globalNesting\";s:8:\"datatype\";s:6:\"(ibid)\";s:11:\"description\";s:118:\"List of tags (among the already set tags), which will be forced to have the nesting-flag set to &#x201c;global&#x201d;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:15:\"rmTagIfNoAttrib\";s:8:\"datatype\";s:6:\"(ibid)\";s:11:\"description\";s:103:\"List of tags (among the already set tags), which will be forced to have the rmTagIfNoAttrib set to true\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:8:\"noAttrib\";s:8:\"datatype\";s:6:\"(ibid)\";s:11:\"description\";s:154:\"List of tags (among the already set tags), which will be forced to have the allowedAttribs value set to zero (which means, all attributes will be removed.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:10:\"removeTags\";s:8:\"datatype\";s:6:\"(ibid)\";s:11:\"description\";s:95:\"List of tags (among the already set tags), which will be configured so they are surely removed.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:18:\"keepNonMatchedTags\";s:8:\"datatype\";s:33:\"boolean / &#x201c;protect&#x201d;\";s:11:\"description\";s:328:\"If set (true=1), then all tags are kept regardless of tags present as keys in $tags-array. <br>If &quot;protect&quot;, then the preserved tags have their &lt;&gt; converted to &amp;lt; and &amp;gt;<br>Default is to REMOVE all tags, which are not specifically assigned to be allowed! So you might probably want to set this value!\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:16:\"htmlSpecialChars\";s:8:\"datatype\";s:14:\"-1 / 0 / 1 / 2\";s:11:\"description\";s:538:\"This regards all content which is NOT tags: <br>&#x201c;0&#x201d; means &#x201c;disabled&#x201d; - nothing is done<br>&#x201c;1&#x201d; means the content outside tags is htmlspecialchar()&#039;ed (PHP-function which converts &amp;&#x201d;&lt;&gt; to &amp;...;)<br>&#x201c;2&#x201d; is the same as &#x201c;1&#x201d; but entities like &#x201c;&amp;amp;&#x201d; or &#x201c;&amp;#234&#x201d; are untouched.<br>&#x201c;-1&#x201d; does the opposite of &#x201c;1&#x201d; - converts &amp;lt; to &lt;, &amp;gt; to &gt;, &amp;quot; to &#x201c; etc.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:14:\"xhtml_cleaning\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:167:\"Cleans up the content for XHTML compliance. Still slightly experimental and supports only some clean up operations (like convertion tags and attributes to lower case).\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (48,7389802,'0adbed858831d26590fb2051a38b06d1','','page:->HTMLparser_tags; tsref:->HTMLparser_tags','a:1:{s:4:\"rows\";a:19:{i:0;a:6:{s:8:\"property\";s:15:\"overrideAttribs\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:60:\"If set, this string is preset as the attributes of the tag. \";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:14:\"allowedAttribs\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:156:\"&#039;0&#039; (zero) = no attributes allowed, &#039;[commalist of attributes]&#039; = only allowed attributes. If blank/not set, all attributes are allowed.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:31:\"&nbsp;fixAttrib.[attribute].set\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:40:\"Force the attribute value to this value.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:33:\"&nbsp;fixAttrib.[attribute].unset\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:37:\"&nbsp;If set, the attribute is unset.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:35:\"&nbsp;fixAttrib.[attribute].default\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:100:\"If no attribute exists by this name, this value is set as default value (if this value is not blank)\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:34:\"&nbsp;fixAttrib.[attribute].always\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:95:\"If set, the attribute is always processed. Normally an attribute is processed only if it exists\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:144:\"&nbsp;fixAttrib.[attribute].trim<br>&nbsp;fixAttrib.[attribute].intval<br>&nbsp;fixAttrib.[attribute].upper<br>&nbsp;fixAttrib.[attribute].lower\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:87:\"If any of these keys are set, the value is passed through the respective PHP-functions.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:33:\"&nbsp;fixAttrib.[attribute].range\";s:8:\"datatype\";s:12:\"[low],[high]\";s:11:\"description\";s:23:\"Setting integer range. \";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:32:\"&nbsp;fixAttrib.[attribute].list\";s:8:\"datatype\";s:23:\"list of values, trimmed\";s:11:\"description\";s:84:\"Attribute value must be in this list. If not, the value is set to the first element.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:41:\"&nbsp;fixAttrib.[attribute].removeIfFalse\";s:8:\"datatype\";s:36:\"boolean/&#x201d;blank&#x201d; string\";s:11:\"description\";s:203:\"If set, then the attribute is removed if it is &quot;false&quot;. If this value is set to &quot;blank&quot; then the value must be a blank string (that means a &quot;zero&quot; value will not be removed)\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:42:\"&nbsp;fixAttrib.[attribute].removeIfEquals\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:70:\"If the attribute value matches the value set here, then it is removed.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:45:\"&nbsp;fixAttrib.[attribute].casesensitiveComp\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:113:\"If set, the comparison in .removeIfEquals and .list will be case-sensitive. At this point, it&#039;s insensitive.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:46:\"&nbsp;fixAttrib.[attribute].prefixLocalAnchors\";s:8:\"datatype\";s:7:\"integer\";s:11:\"description\";s:473:\"If the first char is a &#x201c;#&#x201d; character (anchor of fx. &lt;a&gt; tags) this will prefix either a relative or absolute path.<br>If the value is &#x201c;1&#x201d; you will get the absolute path (t3lib_div::getIndpEnv(&#039;TYPO3_REQUEST_URL&#039;))<br>If the value is &#x201c;2&#x201d; you will get the relative path (stripping of t3lib_div::getIndpEnv(&#039;TYPO3_SITE_URL&#039;))<br>&nbsp;<b><br>Example:</b><br>&nbsp;<br>...fixAttrib.href.prefixLocalAnchors = 1\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:45:\"&nbsp;fixAttrib.[attribute].prefixRelPathWith\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:314:\"If the value of the attribute seems to be a relative URL (no scheme like &#x201c;http&#x201d; and no &#x201c;/&#x201d; as first char) then that value of this property will be prefixed the attribute.<br>&nbsp;<b><br>Example:</b><br>&nbsp;<br>...fixAttrib.src.prefixRelPathWith = http://192.168.230.3/typo3/32/dummy/\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:36:\"&nbsp;fixAttrib.[attribute].userFunc\";s:8:\"datatype\";s:18:\"function reference\";s:11:\"description\";s:145:\"User function for processing of the attribute.<br>&nbsp;<b><br>Example:</b><br>&nbsp;<br>...fixAttrib.href.userFunc = tx_realurl-&gt;test_urlProc\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:7:\"protect\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:62:\"If set, the tag &lt;&gt; is converted to &amp;lt; and &amp;gt;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:5:\"remap\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:47:\"If set, the tagname is remapped to this tagname\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:15:\"rmTagIfNoAttrib\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:69:\"If set, then the tag is removed if no attributes happend to be there.\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:7:\"nesting\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:624:\"If set true, then this tag must have starting and ending tags in the correct order. Any tags not in this order will be discarded. Thus &#039;&lt;/B&gt;&lt;B&gt;&lt;I&gt;&lt;/B&gt;&lt;/I&gt;&lt;/B&gt;&#039; will be converted to &#039;&lt;B&gt;&lt;I&gt;&lt;/B&gt;&lt;/I&gt;&#039;. <br>Is the value &quot;global&quot; then true nesting in relation to other tags marked for &quot;global&quot; nesting control is preserved. This means that if &lt;B&gt; and &lt;I&gt; are set for global nesting then this string &#039;&lt;/B&gt;&lt;B&gt;&lt;I&gt;&lt;/B&gt;&lt;/I&gt;&lt;/B&gt;&#039; is converted to &#039;&lt;B&gt;&lt;/B&gt;&#039;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (49,7389802,'2bcf41f3a27a1da90a09ebdf3a40f315','','tsref:(TLO)','a:1:{s:4:\"rows\";a:10:{i:0;a:6:{s:8:\"property\";s:5:\"types\";s:8:\"datatype\";s:8:\"readonly\";s:11:\"description\";s:59:\"Types (internal)<br>type=99 reserved for plaintext display \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:9:\"resources\";s:8:\"datatype\";s:8:\"readonly\";s:11:\"description\";s:28:\"Resources in list (internal)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:9:\"sitetitle\";s:8:\"datatype\";s:8:\"readonly\";s:11:\"description\";s:20:\"SiteTitle (internal)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:6:\"config\";s:8:\"datatype\";s:11:\"-&gt;CONFIG\";s:11:\"description\";s:135:\"Global configuration. <br>These values are stored with cached pages which means they are also accessible when retrieving a cached page.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:9:\"constants\";s:8:\"datatype\";s:14:\"-&gt;CONSTANTS\";s:11:\"description\";s:183:\"Site-specific constants, eg. a general email-adresse. These constants may be substituted in the text throughout the pages. The substitution is done by parseFunc. (Option: constants=1)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:6:\"FEData\";s:8:\"datatype\";s:19:\"<i>-&gt;FE_DATA</i>\";s:11:\"description\";s:105:\"Here you can configure how data submitted from the front-end should be processed, which script and so on.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:11:\"includeLibs\";s:8:\"datatype\";s:23:\"<i>Array of strings</i>\";s:11:\"description\";s:160:\"With this you can include php-files with function libraries for use in your includescript in TYPO3. <br>Please see the PAGE-object, which has the same property.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:101:\"<i>Other reserved TLO&#039;s:</i><br>&nbsp;<br>plugin<br>tt_*<br>temp<br>styles<br>lib<br>_GIFBUILDER\";s:8:\"datatype\";s:13:\"<i>&nbsp;</i>\";s:11:\"description\";s:872:\"These toplevel object names are reserved. That means you can risk static_templates to use them:<br>&#x201c;plugin&#x201d; is used for rendering of special content like boards, ecommerce solutions, guestbooks and so on. Normally set from static_templates. <i>Please see separate description below!</i><br>&#x201c;tt_*&#x201d;, eg tt_content (from &#x201c;content (default)&#x201d;) is used to render content from tables.<br>&#x201c;temp&#x201d; and &#x201c;styles&#x201d; are used for conde-libraries you can copy during parse-time, but they are not saved with the template in cache. <b>&quot;temp&quot; / &quot;styles&quot; are unset</b> before the template is cached! Therefore use these names to store temporary data.<br>&#x201c;lib&#x201d; can be used for a &#x201c;library&#x201d; of code, you can reference in TypoScript (unlike &#x201c;styles&#x201d; which is unset)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:3:\"...\";s:8:\"datatype\";s:4:\"PAGE\";s:11:\"description\";s:469:\"Start a new page<br>&nbsp;<b><br>Example:</b><br>page = PAGE<br>page.typeNum = 1<br>&nbsp;<b><br>Guidelines:</b><br>Good, general PAGE object names to use are such as:<br>&nbsp;<i>page </i>for the main page with content<br>&nbsp;<i>frameset, frameset2 </i>for framesets.<br>&nbsp;<i>top, left, menu, right, bottom, border </i>for top and menu frames etc.<br>This is just recommandations. Especially the name &#039;page&#039; for the content bearing page is very common.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:3:\"...\";s:8:\"datatype\";s:17:\"<i>(whatever)</i>\";s:11:\"description\";s:368:\"If a toplevel-object is not a PAGE-object it could be used as a temporary repository for setup. In this case you should use the &quot;temp&quot; or &quot;styles&quot; objects.<br>&quot;tt_...&quot; is normally used to define the setup of content-records. Eg. &quot;tt_content&quot; would be used for the tt_content-table as default. See the &quot;CONTENT&quot;-cObject\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (50,7389802,'270df189982a4482d7b570baa95cba05','','tsref:plugin','a:1:{s:4:\"rows\";a:5:{i:0;a:6:{s:8:\"property\";s:15:\"<i>userFunc</i>\";s:8:\"datatype\";s:13:\"<i>&nbsp;</i>\";s:11:\"description\";s:67:\"<i>Property setting up the USER / USER_INT object of the plugin</i>\";s:7:\"default\";s:13:\"<i>&nbsp;</i>\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:18:\"<i>includeLibs</i>\";s:8:\"datatype\";s:13:\"<i>&nbsp;</i>\";s:11:\"description\";s:67:\"<i>Property setting up the USER / USER_INT object of the plugin</i>\";s:7:\"default\";s:13:\"<i>&nbsp;</i>\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:18:\"_CSS_DEFAULT_STYLE\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:331:\"Use this to have some default CSS styles inserted in the header section of the document. Most likely this will provide a default acceptable display from the plugin, but should ideally be cleared and moved to an external stylesheet.<br>This value is for all plugins read by the pagegen script when making the header of the document.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:28:\"_DEFAULT_PI_VARS.[piVar-key]\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:206:\"Allows you to set default values of the piVars array which most plugins are using (and should use) for data exchange with themselves. <br>This works only if the plugin calls $this-&gt;pi_setPiVarDefaults().\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:34:\"_LOCAL_LANG.[lang-key].[label-key]\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:68:\"Can be used to override the default locallang labels for the plugin.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (51,7389802,'f98a9e5726c62fb08803c31cac589abb','','tsref:config/->CONFIG','a:1:{s:4:\"rows\";a:109:{i:0;a:6:{s:8:\"property\";s:8:\"linkVars\";s:8:\"datatype\";s:4:\"list\";s:11:\"description\";s:1097:\"HTTP_GET_VARS, which should be passed on with links in TYPO3. This is compiled into a string stored in $GLOBALS[&quot;TSFE&quot;]-&gt;linkVars<br>&nbsp;<br>The values are rawurlencoded in PHP.<br>&nbsp;<br>You can specify a range of valid values by appending a () after each value. If this range does not match, the variable won&#039;t be appended to links. This is very important to prevent that the cache system gets flooded with forged values.<br>&nbsp;<br>The range may containing one of these values:<br>&nbsp;<b>[a]-[b]</b> - A range of allowed integer values<br>&nbsp;<b>int</b> - Only integer values are allowed<br>&nbsp;<b>[a]|[b]|[c]</b> - A list of allowed strings (whitespaces will be remove','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (52,7389802,'61a507730523bd733941819140f8811b','','tsref:constants','a:1:{s:4:\"rows\";a:1:{i:0;a:6:{s:8:\"property\";s:15:\"<i>Array...</i>\";s:8:\"datatype\";s:13:\"<i>string</i>\";s:11:\"description\";s:232:\"Constants. <br>&nbsp;<b><br>Examples:</b><br>.EMAIL = <i>email@email.com</i><br>Now if parseFunc anywhere is configured with constants=1 then all cases of the string ###EMAIL### will be substituted in the text.<br>see -&gt;parseFunc\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (53,7389802,'7032add39bae490a73f4ce839844c001','','tsref:(page)','a:1:{s:4:\"rows\";a:27:{i:0;a:6:{s:8:\"property\";s:7:\"typeNum\";s:8:\"datatype\";s:17:\"<i>typeNumber</i>\";s:11:\"description\";s:221:\"This decides the the typeId of the page. The value defaults to 0 for the first <br>found PAGE object, but it MUST be set and be unique as soon you use more than one such object (watch this if you use frames on your page)!\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:10:\"1,2,3,4...\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:42:\"Wraps the content of the the cObject array\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:63:\"Wraps the content of the the cObject array with stdWrap options\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:14:\"bodyTagCObject\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:78:\"This is default bodytag overridden by &#x201c;.bodyTag&#x201d; if that is set.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:7:\"bodyTag\";s:8:\"datatype\";s:11:\"&lt;tag&gt;\";s:11:\"description\";s:93:\"Bodytag on the page<br>&nbsp;<b><br>Example:</b><br>&lt;body bgcolor=&quot;{$bgCol}&quot;&gt;\";s:7:\"default\";s:40:\"&lt;body bgcolor=&quot;#FFFFFF&quot;&gt;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:7:\"headTag\";s:8:\"datatype\";s:11:\"&lt;tag&gt;\";s:11:\"description\";s:35:\"Head-tag if alternatives are wanted\";s:7:\"default\";s:12:\"&lt;head&gt;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:14:\"bodyTagMargins\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:373:\"margins in the bodytag. <br>&nbsp;<b><br>Property:</b><br>.useCSS = 1 (boolean) - will set a &#x201c;BODY {margin: ...}&#x201d; line in the in-document style declaration - for XHTML compliance.<br>&nbsp;<b><br>Example:</b><br>value <i>4</i><br>adds <i>leftmargin=&quot;4&quot; topmargin=&quot;4&quot; marginwidth=&quot;4&quot; marginheight=&quot;4&quot;</i> to the bodyTag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:10:\"bodyTagAdd\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:48:\"This content is added to the end of the bodyTag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:5:\"bgImg\";s:8:\"datatype\";s:11:\"imgResource\";s:11:\"description\";s:74:\"Background image on the page. This is automatically added to the body-tag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:8:\"frameSet\";s:8:\"datatype\";s:13:\"-&gt;FRAMESET\";s:11:\"description\";s:76:\"if any properties is set to this property, the page is made into a frameset.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:4:\"meta\";s:8:\"datatype\";s:9:\"-&gt;META\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:12:\"shortcutIcon\";s:8:\"datatype\";s:8:\"resource\";s:11:\"description\";s:339:\"Favicon of the page. Create a reference to an icon here!<br>Browsers that support favicons display them in the browser&#039;s address bar, next to the site&#039;s name in lists of bookmarks, and next to the page&#039;s title in a Tabbed Document Interface.<br>&nbsp;<b><br>Note:</b><br>This must be a valid &quot;.ico&quot;-file (iconfile)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:10:\"headerData\";s:8:\"datatype\";s:11:\"-&gt;CARRAY\";s:11:\"description\";s:149:\"Inserts content in the header-section. Could be JavaScripts, meta-tags, other stylesheet references. <br>Is inserted after all the style-definitions.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:6:\"config\";s:8:\"datatype\";s:11:\"-&gt;CONFIG\";s:11:\"description\";s:108:\"configuration for the page. Any entries override the same entries in the toplevel-object &quot;config&quot;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:17:\"includeJS.[array]\";s:8:\"datatype\";s:8:\"resource\";s:11:\"description\";s:537:\"Inserts one or more (Java)Scripts in &lt;script&gt; tags.<br>&nbsp;<br>The file definition must be a valid &quot;resource&quot; datatype, otherwise nothing is inserted.<br>&nbsp;<br>Each file has <i>optional properties</i>:<br>&nbsp;<b>.style</b> - setting the MIME type of the script (default: text/javascript)<br>&nbsp;<b><br>Example:</b><br>includeJS {<br>&nbsp;&nbsp;&nbsp;file1 = fileadmin/helloworld.js<br>&nbsp;&nbsp;&nbsp;file1.type = application/x-javascript<br>&nbsp;&nbsp;&nbsp;file2 = javascript_uploaded_to_template*.js<br>}\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:11:\"includeLibs\";s:8:\"datatype\";s:23:\"<i>array of strings</i>\";s:11:\"description\";s:902:\"With this you may include php-files. This does the same as &quot;includeLibrary&quot; in -&gt;CONFIG but this can include more than one file. These files are included <i>after</i> the file of includeLibrary.<br>&nbsp;<b><br>NOTE:</b><br>The toplevel object &quot;includeLibs&quot; and the scripts defined with this property is added to each other. Script-keys (that is the &quot;array of strings&quot;-value, like below &quot;ts_address&quot;) from this property of the page overrides any scripts-keys from the toplevel &quot;includeLibs&quot; property!<br>The script-filenames are of the datatype &quot;resource&quot;.<br>&nbsp;<b><br>Example:</b><br>includeLibs.ts_address = lib_filename.php<br>includeLibs.ts_shop = lib_filename.php<br>&nbsp;<br>Please do not use the prefix shown above (&quot;ts_&quot;) as this will probably be used by the standard TYPO3 libraries that will appear in the future. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:40:\"<b>&nbsp;</b><b><br>CSS Stylesheets:</b>\";s:8:\"datatype\";N;s:11:\"description\";N;s:7:\"default\";N;s:12:\"column_count\";i:1;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:10:\"stylesheet\";s:8:\"datatype\";s:8:\"resource\";s:11:\"description\";s:139:\"Inserts a stylesheet in the &lt;HEAD&gt;-section of the page;<i><br>&lt;link rel=&quot;stylesheet&quot; href=&quot;[resource]&quot;&gt;</i>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:19;a:6:{s:8:\"property\";s:18:\"includeCSS.[array]\";s:8:\"datatype\";s:8:\"resource\";s:11:\"description\";s:962:\"Inserts a stylesheet (just like the .stylesheet property) by allows to setting up more than a single stylesheet, because you can enter files in an array.<br>&nbsp;<br>The file definition must be a valid &quot;resource&quot; datatype, otherwise nothing is inserted.<br>&nbsp;<br>Each file has <i>optional properties</i>:<br>&nbsp;<b>.media</b> - setting the media attribute of the &lt;style&gt; tag.<br>&nbsp;<b>.title</b> - setting the title of the &lt;style&gt; tag.<br>&nbsp;<b>.alternate</b> - If set (boolean) then the rel-attribute will be &quot;alternate stylesheet&quot;<br>&nbsp;<b>.import</b> - If set (boolean) then the @import way of including a stylesheet is used instead of &lt;link&gt;<br>&nbsp;<b><br>Example:</b><br>includeCSS {<br>&nbsp;&nbsp;&nbsp;file1 = fileadmin/mystylesheet1.css<br>&nbsp;&nbsp;&nbsp;file2 = stylesheet_uploaded_to_template*.css<br>&nbsp;&nbsp;&nbsp;file2.title = High contrast<br>&nbsp;&nbsp;&nbsp;file2.media = print<br>}\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:20;a:6:{s:8:\"property\";s:15:\"CSS_inlineStyle\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:95:\"This value is just passed on as inline css (in-document css encapsulated in &lt;style&gt;-tags)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:21;a:6:{s:8:\"property\";s:20:\"insertClassesFromRTE\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:470:\"If set, the classes for the Rich Text Editor configured in Page TSconfig is inserted in as the first thing in the Style-section right after the setting of the stylesheet.<br>&nbsp;<br>&nbsp;<b>.add_mainStyleOverrideDefs</b> = [* / list of tags ] - will add all the &#x201c;RTE.default. mainStyleOverride_add&#x201d; - tags configured as well.<br>&nbsp;<i><br>Might be deprecated soon. Most likely the RTE should be configured by the stylesheet instead. Stay tuned...</i>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:22;a:6:{s:8:\"property\";s:15:\"noLinkUnderline\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:111:\"Disables link-underlining. Uses in-document stylesheet.<br>&nbsp;<i><br>Deprecated. Use stylesheet instead.</i>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:23;a:6:{s:8:\"property\";s:5:\"hover\";s:8:\"datatype\";s:10:\"HTML-color\";s:11:\"description\";s:147:\"The color of a link when the mouse moves over it! (only MSIE). Uses in-document stylesheet.<br>&nbsp;<i><br>Deprecated. Use stylesheet instead.</i>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:24;a:6:{s:8:\"property\";s:10:\"hoverStyle\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:189:\"Additional style information to the hover-color.<br>&nbsp;<b><br>Example:</b><br>page.hoverStyle = font: bold; text-decoration: none;<br>&nbsp;<i><br>Deprecated. Use stylesheet instead.</i>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:25;a:6:{s:8:\"property\";s:15:\"smallFormFields\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:396:\"Renders formfields like textarea, input and select-boxes small with &quot;verdana size 1&quot; font. <br>Uses in-document stylesheet.<br>&nbsp;<b><br>Tip:</b><br>Use this together with the config-option &quot;compensateFieldWidth&quot; set to &quot;0.6&quot; for netscape-browsers in order to render the small form fields in the same width!<br>&nbsp;<i><br>Deprecated. Use stylesheet instead.</i>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:26;a:6:{s:8:\"property\";s:16:\"adminPanelStyles\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:44:\"Will include CSS styles for the Admin Panel.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (56,7389802,'dfb38a4b542a66ea9ca4c51cc1e8c454','','tsref:(page).frameSet/->FRAMESET','a:1:{s:4:\"rows\";a:4:{i:0;a:6:{s:8:\"property\";s:10:\"1,2,3,4...\";s:8:\"datatype\";s:8:\"frameObj\";s:11:\"description\";s:45:\"Configuration of frames and nested framesets.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:4:\"cols\";s:8:\"datatype\";s:26:\"&lt;frameset&gt;-data:cols\";s:11:\"description\";s:4:\"Cols\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:4:\"rows\";s:8:\"datatype\";s:26:\"&lt;frameset&gt;-data:rows\";s:11:\"description\";s:4:\"Rows\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:6:\"params\";s:8:\"datatype\";s:23:\"&lt;frameset&gt;-params\";s:11:\"description\";s:93:\"<b>Example:</b><br>border=&quot;0&quot; framespacing=&quot;0&quot; frameborder=&quot;NO&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (57,7389802,'88d7c4b86d5cebdad2b438b0538b48d3','','tsref:(page).frameSet.(number)/->FRAMESET.(number)','a:1:{s:4:\"rows\";a:5:{i:0;a:6:{s:8:\"property\";s:3:\"obj\";s:8:\"datatype\";s:38:\"<i>pointer to toplevel object-name</i>\";s:11:\"description\";s:130:\"toplevel object-name of a PAGE / FRAMESET<br>&nbsp;<b><br>Example:</b><br>&quot;left&quot;, &quot;page&quot;, &quot;frameset&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:7:\"options\";s:8:\"datatype\";s:21:\"<i>url-parameters</i>\";s:11:\"description\";s:199:\"<b>Example:</b><br>print=1&amp;othervar=anotherthing<br>would add &#039;<i>&amp;print=1&amp;othervar=anotherthing</i>&#039; to the &quot;.src&quot;-content (if not &quot;.src&quot; is set manually!!)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:6:\"params\";s:8:\"datatype\";s:20:\"&lt;frame&gt;-params\";s:11:\"description\";s:81:\"<b>Example:</b><br>scrolling=&quot;AUTO&quot; noresize frameborder=&quot;NO&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:4:\"name\";s:8:\"datatype\";s:23:\"&lt;frame&gt;-data:name\";s:11:\"description\";s:133:\"Manually set name of frame<br>&nbsp;<br>&nbsp;<b>NOTE:</b> Is set automatically and should not be overridden under normal conditions!\";s:7:\"default\";s:25:\"value of &quot;.obj&quot;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:3:\"src\";s:8:\"datatype\";s:22:\"&lt;frame&gt;-data:src\";s:11:\"description\";s:140:\"Manually set the src of the frame<br>&nbsp;<br>&nbsp;<b>NOTE:</b> Is set automatically and should not be overridden under normal conditions!\";s:7:\"default\";s:33:\"could be index.php?$id&amp;$type \";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (58,7389802,'c123846d335b06bc44b284ee8c7a8ea0','','tsref:->META','a:1:{s:4:\"rows\";a:1:{i:0;a:6:{s:8:\"property\";s:15:\"<i>Array...</i>\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:495:\"Metatags<br>If value is empty (after trimming) the metatag is not generated.<br>If the &quot;key&quot; (eg. &quot;REFRESH&quot; or &quot;DESCRIPTION&quot;) is &quot;REFRESH&quot; (caseinsensitive), then the &quot;http-equiv&quot;-attribute is used in the metatag instead of &quot;name&quot;.<br>&nbsp;<b><br>Examples:</b><br>.REFRESH = <i>[sec]; [url, leave blank for same page]</i><br>.DESCRIPTION = This is the description of the content in this document<br>.KEYWORDS = This is the keywords...\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (59,7389802,'e8c6be7a5c4bf207221e5b5d5a6441a4','','tsref:->CARRAY','a:1:{s:4:\"rows\";a:4:{i:0;a:6:{s:8:\"property\";s:10:\"1,2,3,4...\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:182:\"This is a numerical &quot;array&quot; of content-objects (cObjects). The order by which you specific the objects is not important as the array will be sorted before it&#039;s parsed!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:29:\"<b>Occational properties:</b>\";s:8:\"datatype\";N;s:11:\"description\";N;s:7:\"default\";N;s:12:\"column_count\";i:1;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:23:\"(stdWrap properties...)\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:474:\"&nbsp;<b>NOTE:</b> This applies ONLY if &quot;CARRAY /stdWrap&quot; is set to be data type<br>If you specify any non-integer properties to a CARRAY, stdWrap will be invoked with all properties of the CARRAY.<br>&nbsp;<b><br>Example:</b><br>This will return &#039;&lt;B&gt;This will be rendered before &quot;10&quot;testing&lt;/B&gt;&#039;<br>10 = TEXT<br>10.value = testing<br>5 = HTML<br>5.value = This will be rendered before &quot;10&quot;<br>wrap = &lt;B&gt; |&lt;/B&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:10:\"(TDParams)\";s:8:\"datatype\";s:17:\"&lt;TD&gt;-params\";s:11:\"description\";s:217:\"&nbsp;<b>NOTE:</b> This applies ONLY if &quot;CARRAY +TDParams&quot; is set to be data type<br>This property is used only in some cases where CARRAY is used. Please look out for a note about that in the various cases.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (60,7389802,'90abbeca518fabd3ec8944e62fd200b7','','tsref:(cObject).HTML','a:1:{s:4:\"rows\";a:1:{i:0;a:6:{s:8:\"property\";s:5:\"value\";s:8:\"datatype\";s:13:\"HTML /stdWrap\";s:11:\"description\";s:14:\"Raw HTML-code.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (61,7389802,'03fe46bb8628f60fec5859bf75fa5f75','','tsref:(cObject).TEXT','a:1:{s:4:\"rows\";a:2:{i:0;a:6:{s:8:\"property\";s:5:\"value\";s:8:\"datatype\";s:5:\"value\";s:11:\"description\";s:34:\"text, wrap with stdWrap properties\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:30:\"<i>(stdWrap properties...)</i>\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (62,7389802,'571d18366e15bc9b1ce2cd4ef7da8ff5','','tsref:(cObject).COA/(cObject).COA_INT/(cObject).COBJ_ARRAY','a:1:{s:4:\"rows\";a:5:{i:0;a:6:{s:8:\"property\";s:10:\"1,2,3,4...\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:2:\"if\";s:8:\"datatype\";s:7:\"-&gt;if\";s:11:\"description\";s:55:\"if &quot;if&quot; returns false the COA is NOT rendered\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:11:\"includeLibs\";s:8:\"datatype\";s:29:\"&nbsp;<i>list of </i>resource\";s:11:\"description\";s:380:\"<b>(This property is used only if the object is COA_INT!, See introduction.)</b><br>This is a comma-separated list of resources that are included as PHP-scripts (with include_once() function) if this script is included.<br>This is possible to do because any include-files will be known before the scripts are included. That&#039;s not the case with the regular PHP_SCRIPT cObject.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (63,7389802,'3918801c03952a7fde07c4afd3faa80a','','tsref:(cObject).FILE','a:1:{s:4:\"rows\";a:5:{i:0;a:6:{s:8:\"property\";s:4:\"file\";s:8:\"datatype\";s:8:\"resource\";s:11:\"description\";s:217:\"If the resource is <b>jpg,gif,jpeg,png </b>the image is inserted as an image-tag. Al other formats is read and inserted into the HTML-code.<br>The maximum filesize of documents to be read is set to 1024 kb internally!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:8:\"linkWrap\";s:8:\"datatype\";s:8:\"linkWrap\";s:11:\"description\";s:26:\"(before &quot;.wrap&quot;)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:20:\"altText<br>titleText\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:165:\"<b>For &lt;img&gt; output only!</b><br>&nbsp;<br>If no titltext is specified, it will use the alttext insteadIf no alttext is specified, it will use an empty alttext\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:11:\"longdescURL\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:149:\"<b>For &lt;img&gt; output only!</b><b><br>&nbsp;</b><br>&quot;longdesc&quot; attribute (URL pointing to document with extensive details about image).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (64,7389802,'45effe326c7d351875eb76178430f63f','','tsref:(cObject).IMAGE','a:1:{s:4:\"rows\";a:10:{i:0;a:6:{s:8:\"property\";s:4:\"file\";s:8:\"datatype\";s:11:\"imgResource\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"params\";s:8:\"datatype\";s:18:\"&lt;IMG&gt;-params\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:6:\"border\";s:8:\"datatype\";s:7:\"integer\";s:11:\"description\";s:63:\"Value of the &#x201c;border&#x201d; attribute of the image tag.\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:43:\"altText<br>titleText<br>&nbsp;<br>(alttext)\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:273:\"If no titltext is specified, it will use the alttext insteadIf no alttext is specified, it will use an empty alttext<br>&nbsp;<br>(&quot;alttext&quot; is the old spelling of this attribute. It will be used only if &quot;altText&quot; does not specify a value or properties)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:11:\"longdescURL\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:93:\"&quot;longdesc&quot; attribute (URL pointing to document with extensive details about image).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:8:\"linkWrap\";s:8:\"datatype\";s:8:\"linkWrap\";s:11:\"description\";s:26:\"(before &quot;.wrap&quot;)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:13:\"imageLinkWrap\";s:8:\"datatype\";s:30:\"boolean/<br>-&gt;imageLinkWrap\";s:11:\"description\";s:122:\"&nbsp;<b>NOTE:</b> ONLY active if linkWrap is NOT set and file is NOT GIFBUILDER (as it works with the original imagefile)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:2:\"if\";s:8:\"datatype\";s:7:\"-&gt;if\";s:11:\"description\";s:55:\"if &quot;if&quot; returns false the image is not shown!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (65,7389802,'a2140130f94da8de59d956354896d9bf','','tsref:(cObject).IMG_RESOURCE','a:1:{s:4:\"rows\";a:2:{i:0;a:6:{s:8:\"property\";s:4:\"file\";s:8:\"datatype\";s:11:\"imgResource\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (66,7389802,'698298181be68b56fd0361e9da1a114f','','tsref:(cObject).CLEARGIF','a:1:{s:4:\"rows\";a:3:{i:0;a:6:{s:8:\"property\";s:6:\"height\";s:8:\"datatype\";s:45:\"&nbsp;<i>&lt;img&gt;-data:height</i> /stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:5:\"width\";s:8:\"datatype\";s:44:\"&nbsp;<i>&lt;img&gt;-data:width</i> /stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:18:\"&nbsp;| &lt;BR&gt;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (67,7389802,'0f2fee21aca2929d02f2ede163104d2b','','tsref:(cObject).CONTENT','a:1:{s:4:\"rows\";a:6:{i:0;a:6:{s:8:\"property\";s:6:\"select\";s:8:\"datatype\";s:11:\"-&gt;select\";s:11:\"description\";s:30:\"The SQL-statement is set here!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:5:\"table\";s:8:\"datatype\";s:16:\"<i>tableName</i>\";s:11:\"description\";s:320:\"The table, the content should come from. <br>In standard-configurations this will be &quot;tt_content&quot;<br>&nbsp;<b>NOTE:</b> Only tables allowed are &#x201c;pages&#x201d; or tables prefixed with one of these: &#x201c;tt_&#x201d;, &#x201c;tx_&#x201d;, &#x201c;ttx_&#x201d;, &#x201c;fe_&#x201d;, &#x201c;user_&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:9:\"renderObj\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:16:\"&lt; [tablename]\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:5:\"slide\";s:8:\"datatype\";s:7:\"integer\";s:11:\"description\";s:1001:\"If set and no content element is found by the select command, then the rootLine will be traversed back until some content is found.<br>&nbsp;<br>Possible values are &#x201c;-1&#x201d; (slide back up to the siteroot), &#x201c;1&#x201d; (only the current level) and &#x201c;2&#x201d; (up from one level back).<br>&nbsp;<br>Use -1 in combination with collect.<br>&nbsp;<br>&nbsp;<b>.collect (integer)</b>: If set, all content elements found on current and parent pages will be collected. Otherwise, the sliding would stop after the first hit. Set this value to the amount of levels to collect on, or use &#x201c;-1&#x201d; to collect up to the siteroot.<br>&nbsp;<b>.collectFuzzy (boolean)</b>: Only useful in collect mode. If no content elements have been found for the specified depth in collect mode, traverse further until at least one match has occurred.<br>&nbsp;<b>.collectReverse (boolean)</b>: Change order of elements in collect mode. If set, elements of the current page will be on the bottom.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:31:\"Wrap the whole content-story...\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (68,7389802,'fda9a1413332096fac6ed5a6f0f8ce90','','tsref:(cObject).RECORDS','a:1:{s:4:\"rows\";a:5:{i:0;a:6:{s:8:\"property\";s:6:\"source\";s:8:\"datatype\";s:34:\"&nbsp;<i>records-list </i>/stdWrap\";s:11:\"description\";s:126:\"List of record-id&#039;s, optionally with appended table-names.<br>&nbsp;<b><br>Example:</b><br>tt_content_34, 45, tt_links_56\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"tables\";s:8:\"datatype\";s:21:\"<i>list of tables</i>\";s:11:\"description\";s:456:\"List of accepted tables. If any items in the &quot;.source&quot;-list is not prepended with a tablename, the first table in this list is assumed to be the table for such records.<br>Also tablenames configured in .conf is allowed.<br>&nbsp;<b><br>Example:</b><br>tables = tt_content, tt_address, tt_links<br>conf.tx_myexttable = TEXT<br>conf.tx_myexttable.value = Hello world<br>&nbsp;<br>This adds the tables tt_content, tt_address, tt_links, tx_myexttable\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:23:\"conf.[<i>tablename</i>]\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:62:\"Config-array which renders records from table <i>tablename</i>\";s:7:\"default\";s:131:\"If this is NOT defined, the rendering of the records is done with the toplevel-object [tablename] - just like the cObject, CONTENT!\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:12:\"dontCheckPid\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:141:\"Normally a record cannot be selected, if it&#039;s parent page (pid) is not accessible for the website user. This option disables that check.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (69,7389802,'58494541fb84e7f9f04a7222619963cf','','tsref:(cObject).HMENU','a:1:{s:4:\"rows\";a:16:{i:0;a:6:{s:8:\"property\";s:16:\"(1 / 2 / 3 /...)\";s:8:\"datatype\";s:7:\"menuObj\";s:11:\"description\";s:260:\"<b>Required!</b><br>Defines which menuObj that should render the menuitems on the various levels. <br>1 is the first level, 2 is the second level, 3 is the third level, 4 is ....<br>&nbsp;<b><br>Example:</b><br>temp.sidemenu = HMENU<br>temp.sidemenu.1 = GMENU \";s:7:\"default\";s:15:\"&nbsp;(no menu)\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:10:\"entryLevel\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:358:\"Defines at which level in the rootLine, the menu should start. <br>Default is &quot;0&quot; which gives us a menu of the very first pages on the site.<br>If the value is &lt; 0, entryLevel is chosen from &quot;behind&quot; in the rootLine. Thus &quot;-1&quot; is a menu with items from the outermost level, &quot;-2&quot; is the level before the outermost...\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:7:\"special\";s:8:\"datatype\";s:163:\"<i>&quot;directory&quot; / &quot;list&quot; / &quot;updated&quot; / &quot;browse&quot; / &quot;rootline&quot; / &quot;keywords&quot; / &#x201c;language&#x201d;</i>\";s:11:\"description\";s:26:\"(See separate table below)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:13:\"special.value\";s:8:\"datatype\";s:45:\"&nbsp;<i>list of page-uid&#039;s</i> /stdWrap\";s:11:\"description\";s:9:\"See above\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:8:\"minItems\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:364:\"The minimum items in the menu. If the number of pages does not reach this level, a dummy-page with the title &quot;...&quot; and uid=[currentpage_id] is inserted.<br>&nbsp;<br>&nbsp;<b>Notice:</b> Affects all sub menus as well. To set the value for each menu level individually, set the properties in the menu objects (see &#x201c;Common properties&#x201d; table).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:8:\"maxItems\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:165:\"The maximum items in the menu. More items will be ignored.<br>&nbsp;<br>&nbsp;<b>Notice:</b> Affects all sub menus as well. (See &#x201c;minItems&#x201d; for notice)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:5:\"begin\";s:8:\"datatype\";s:9:\"int +calc\";s:11:\"description\";s:290:\"The first item in the menu. <br>&nbsp;<b><br>Example:</b><br>This results in a menu, where the first two items are skipped starting with item number 3:<br>&nbsp;&nbsp;&nbsp;begin = 3 <br>&nbsp;<br>&nbsp;<b>Notice:</b> Affects all sub menus as well. (See &#x201c;minItems&#x201d; for notice)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:14:\"excludeUidList\";s:8:\"datatype\";s:11:\"list of int\";s:11:\"description\";s:372:\"This is a list of page uid&#039;s to exclude when the select statement is done. Comma-separated. You may add &#x201c;current&#x201d; to the list to exclude the current page.<br>&nbsp;<b><br>Example:</b><br>The pages with these uid-number will NOT be within the menu!! Additionally the current page is always excluded too.<br>&nbsp;&nbsp;&nbsp;excludeUidList = 34,2,current\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:15:\"excludeDoktypes\";s:8:\"datatype\";s:16:\"list of integers\";s:11:\"description\";s:193:\"Enter the list of page document types (doktype) to exclude from menus. By default pages that are &#x201c;not in menu&#x201d; (5) are excluded and those marked for backend user access only (6). \";s:7:\"default\";s:3:\"5,6\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:16:\"includeNotInMenu\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:272:\"If set, pages with type &#x201c;Not in menu&#x201d; will be included in menus.<br>The number &#x201c;5&#x201d; will simply be removed from the current dok-type list (which is by default &#x201c;5,6&#x201d; but can be changed by &#x201c;excludeDoktypes&#x201d;, see above).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:19:\"alwaysActivePIDlist\";s:8:\"datatype\";s:16:\"list of integers\";s:11:\"description\";s:145:\"This is a list of page UID numbers that will always be regarded as active menu items and thereby automatically opened regardless of the rootline.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:11:\"protectLvar\";s:8:\"datatype\";s:17:\"boolean / keyword\";s:11:\"description\";s:1242:\"If set, then for each page in the menu it will be checked if an Alternative Page Language record for the language defined in &quot;config.sys_language_uid&quot; (typically defined via &amp;L) exists for the page. If that is not the case and the pages &#x201c;Localization settings&#x201d; have the &#x201c;Hide page if no translation for current language exists&#x201d; flag set, then the menu item will link to a non accessible page that will yield an error page to the user. Setting this option will prevent that situation by simply adding &#x201c;&amp;L=0&#x201d; for such pages, meaning that they will switch to the default language rather than keeping the current language.<br>The check is only carried out if a translation is requested (&quot;config.sys_language_uid&quot; is not zero).<br>&nbsp;<b><br>Keyword: &#x201c;all&#x201d;</b><br>When set to &#x201c;all&#x201d; the same check is carried out but it will not look if &#x201c;Hide page if no translation for current language exists&#x201d; is set - it always reverts to default language if no translation is found.<br>&nbsp;<br>For these options to make sense, they should only be used when &#x201c;config.sys_language_mode&#x201d; is not set to &#x201c;content_fallback&#x201d;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:14:\"addQueryString\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:113:\"<i>see typolink.addQueryString</i><br>&nbsp;<br>&nbsp;<b>Notice: </b>This works only for <i>special=language</i>.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:2:\"if\";s:8:\"datatype\";s:7:\"-&gt;if\";s:11:\"description\";s:58:\"If &quot;if&quot; returns false, the menu is not generated\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (70,7389802,'cd830eb4ea8346e958afc812a860ed75','','tsref:(cObject).HMENU.special','a:1:{s:4:\"rows\";a:9:{i:0;a:6:{s:8:\"property\";s:9:\"directory\";s:8:\"datatype\";s:304:\"This will generate a menu of all pages with pid = 35 and pid = 56.<br>&nbsp;&nbsp;&nbsp;20 = HMENU<br>&nbsp;&nbsp;&nbsp;20.special = directory<br>&nbsp;&nbsp;&nbsp;20.special.value = 35, 56<br>&nbsp;<br>If .value is not set, the default pid is the current page.<br>&nbsp;<br>Support for Mount Pages: Yes.\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:4:\"list\";s:8:\"datatype\";s:308:\"This will generate a menu with the two pages (uid=35 and uid=36) listed:<br>&nbsp;&nbsp;&nbsp;20 = HMENU<br>&nbsp;&nbsp;&nbsp;20.special = list<br>&nbsp;&nbsp;&nbsp;20.special.value = 35, 56<br>&nbsp;<br>If .value is not set, the default uid is the .entryLevel uid.<br>&nbsp;<br>Support for Mount Pages: Yes.\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:7:\"updated\";s:8:\"datatype\";s:2753:\"This will generate a menu of the most recently updated pages from the branches in the tree starting with the uid&#039;s (uid=35 and uid=36) listed. Furthermore the field &quot;tstamp&quot; is used (default is SYS_LASTCHANGED) and the treedepth is 2 levels. Also there will be shown a maximum of 8 pages and they must have been updated within the last three days (3600*24*3):<br>&nbsp;&nbsp;&nbsp;20 = HMENU<br>&nbsp;&nbsp;&nbsp;20.special = updated<br>&nbsp;&nbsp;&nbsp;20.special.value = 35, 56<br>&nbsp;&nbsp;&nbsp;20.special {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;mode = tstamp<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;depth = 2<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;maxAge = 3600*24*3<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;limit = 8<br>&nbsp;&nbsp;&nbsp;}<br>&nbsp;<br>&nbsp;<b>Ordering </b>is by default done in reverse order (desc) with the field specified by &#x201c;mode&#x201d; , but setting &#x201c;alternativeSortingField&#x201d; for the menu object (eg GMENU, see later) will override that.<br>Properties &quot;mode&quot;, &quot;depth&quot;, &quot;maxAge&quot; and &quot;limit&quot; is only used with special=&quot;updated&quot;.<br>&nbsp;<b>mode:</b> Which field in the pages-table to use. Default is &quot;SYS_LASTCHANGED&quot; (which is updated when a page is generated to the youngest tstamp of the records on the page), &quot;manual&quot; or &#x201c;lastUpdated&#x201d; will use the field &quot;lastUpdated&quot; (set manually in the page-record) and &quot;tstamp&quot; will use the &quot;tstamp&quot;-field of the pagerecord, which is set automatically when the record is changed. &quot;crdate&quot; will use &quot;crdate&quot;-field of the pagerecord. &#x201c;starttime&#x201d; will use the starttime field.<br>Fields with zero value is not selected anyway.<br>&nbsp;<b>depth:</b> By default (if the value is not an integer) the depth is 20 levels. The range is 1-20. A depth of 1 means only the start id, depth of 2 means start-id + first level. NOTE: depth is relative to beginAtLevel.<br>&nbsp;<b>beginAtLevel</b>: Integer. Determines starting level for the pagetrees generated based on .value and .depth. Zero is default and includes the start id. 1=starts with the first row of subpages, 2=starts with the second row of subpages. Depth is relative to this starting point.<br>&nbsp;<b>maxAge:</b> Seconds+calc. Pages with update-dates older than currenttime minus this number of seconds will not be shown in the menu no matter what. Default is &quot;not used&quot;. You may use +-*/ for calculations.<br>&nbsp;<b>limit:</b> Max number of items in the menu. Default is 10, max is 100.<br>&nbsp;<b>excludeNoSearchPages:</b> Boolean. If set, pages marked &quot;No search&quot; is not included into special-menus.<br>&nbsp;<br>Support for Mount Pages: Yes.\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:8:\"rootline\";s:8:\"datatype\";s:888:\"Creates a menu with pages from the &quot;rootline&quot; (see earlier in this reference)<br>&nbsp;<b>.range</b> = [begin-level] | [end-level] (same way as you reference the .entryLevel for HMENU)<br>&nbsp;<b>.target_[0-x]</b> targets<br>&nbsp;<br>This...<br>&nbsp;<br>page.2 = HMENU<br>page.2.special = rootline<br>page.2.special.range = 1|-2<br>page.2.special.targets.3 = page<br>page.2.1 = TMENU<br>page.2.1.target = _top<br>page.2.1.wrap = &lt;HR&gt; | &lt;HR&gt;<br>page.2.1.NO {<br>&nbsp;&nbsp;&nbsp;linkWrap = | &gt; <br>}<br>&nbsp;<br>... creates a menu like this:<b><br>Page level 1 &gt; Page level 2 &gt; Page level 3 &gt; Page level 4 &gt;</b><br>(The menu starts at level 1 and does NOT link to the current page (-2 is the level before). Further all pages on level 3 will have &quot;page&quot; as target and all other &quot;_top&quot;)<br>&nbsp;<br>Support for Mount Pages: Yes.\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:6:\"browse\";s:8:\"datatype\";s:3477:\"This kind of menu is built of items given by a list from the property &quot;.item&quot;. Each element in the list (sep. by &quot;|&quot;) is either a reserved itemname (see list) with a predefined function or a userdefined name which you can assign a link to any page. Note that the current page cannot be the root-page of a site.<br>&nbsp;<br>Support for Mount Pages: No!<br>&nbsp;<b>.items </b>( &quot;|&quot; separated list of &quot;itemnames&quot;)<br>&nbsp;<b>.[itemnames].target </b>(target) - optional/alternative target of the item<br>&nbsp;<b>.[itemnames].uid </b>(uid of page) - optional/alternative page-uid to link to<br>&nbsp;<b>.[itemnames].fields.[fieldname]</b> (string) - override field &quot;fieldname&quot; in pagerecord.<br>&nbsp;<b>.items.prevnextToSection (boolean)</b> - if set, the &quot;prev&quot; and &quot;next&quot; navigation will jump to the next section when it reaches the end of pages in the current section<br>&nbsp;<b>.value</b> (page-uid) - default is current page id. Seldomly you might want to override this value with another page-uid which will then act as the basepoint for the menu and the predefined items.<br>&nbsp;<b>Ordering </b>is by default done in reverse order (desc) with the field specified by &#x201c;mode&#x201d; , but setting &#x201c;alternativeSortingField&#x201d; for the menu object (eg GMENU, see later) will override that.<i><br>&nbsp;</i><i><br>Reserved itemnames:</i><br>&nbsp;<b>next</b> / <b>prev</b> : links to next page / previous page. Next and previous pages are from the same &quot;pid&quot; as the current page id (or &quot;value&quot;) - that is the next item in a menu with the current page. Also referred to as current level. <br>If &quot;.prevnextToSection&quot; is set then next/prev will link to the first page of next section / last page of previous section.<br>&nbsp;<b>nextsection</b> / <b>prevsection</b> : links to next section / previous section. A section is defined as the subpages of a page on the same level as the parent (pid) page of the current page. Will not work if parent page of current page is the root page of the site.<br>&nbsp;<b>nextsection_last</b> | <b>prevsection_last</b>: Where nextsection/prevsection links to the first page in a section, these links to the last pages. If there is only one page in the section that will be both first and last. Will not work if parent page of current page is the root page of the site.<br>&nbsp;<b>first</b> / <b>last </b>: First / Last page on current level. If there is only one page on the current level that page will be both first and last.<br>&nbsp;<b>up</b> : Links to the parent (pid) page of the current page. (up 1 level) Will always be available<br>&nbsp;<b>index </b>: Links to the parent of the parent page of the current page (up 2 levels). May not be available if that page is out of the rootline.<br>&nbsp;<b><br>&nbsp;Examples:</b><br>If id=20 is current page then:<br>21= prev and first, 19 = next, 18 = last, 17 = up, 1=index, 10 = nextsection, 11 = nextsection_last<br>prevsection and prevsection_last is not present because id=3 has no subpages! <br>&nbsp;<b><br>TypoScript (only &quot;browse&quot;-part, needs also TMENU/GMENU):</b><br>xxx = HMENU<br>xxx.special = browse<br>xxx.special {<br>&nbsp;&nbsp;&nbsp;items = index|up|next|prev <br>&nbsp;&nbsp;&nbsp;items.prevnextToSection = 1<br>&nbsp;&nbsp;&nbsp;index.target = _blank<br>&nbsp;&nbsp;&nbsp;index.fields.title = INDEX<br>&nbsp;&nbsp;&nbsp;index.uid = 8<br>}\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:8:\"keywords\";s:8:\"datatype\";s:1741:\"Makes a menu of pages with one or more keywords also found on the current page.<br>&nbsp;<b>.value </b>= page for which keywords to find similar pages.<br>&nbsp;<b>.mode:</b> Which field in the pages-table to use for sorting. Default is &quot;SYS_LASTCHANGED&quot; (which is updated when a page is generated to the youngest tstamp of the records on the page), &quot;manual&quot; or &#x201c;lastUpdated&#x201d; will use the field &quot;lastUpdated&quot; (set manually in the page-record) and &quot;tstamp&quot; will use the &quot;tstamp&quot;-field of the pagerecord, which is set automatically when the record is changed. &quot;crdate&quot; will use &quot;crdate&quot;-field of the pagerecord. &#x201c;starttime&#x201d; will use the starttime field.<br>&nbsp;<b>.entryLevel </b>= where in the rootline the search begins. Standard rootline syntax (-x to x)<br>&nbsp;<b>.depth, .limit, .excludeNoSearchPages, .beginAtLevel </b>(like &quot;updated&quot; menu)<b><br>.setKeywords (/stdWrap) = lets you define the keywords manually by defining them as a commaseparated list. If this property is defined, it overrides the default, which is the keywords of the current page.</b><br>&nbsp;<b>.keywordsField</b> = defines the field in the pages-table in which to search for the keywords. Default is the fieldname &#x201c;keyword&#x201d;. No check is done to see if the field you enter here exists, so enter an existing field, OK?!<br>&nbsp;<b>.keywordsField.sourceField</b> = defines the field from the current page from which to take the keywords being matched. The default is &#x201c;keyword&#x201d;. (Notice that &#x201c;.keywordsField&#x201d; is only setting the page-record field to <i>search in</i> !)<br>&nbsp;<br>Support for Mount Pages: Yes.\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:8:\"language\";s:8:\"datatype\";s:2678:\"Creates a language selector menu. Typically this is made as a menu with flags for each language a page is translated to and when the user clicks any element the same page id is hit but with a change to the &#x201c;&amp;L&#x201d; parameter in the URL.<br>&nbsp;<br>The &#x201c;language&#x201d; type will create menu items based on the current page record but with the language record for each language overlaid if available. The items all link to the current page id and only &#x201c;&amp;L&#x201d; is changed.<br>&nbsp;<b><br>Item states:</b><br>When &#x201c;TSFE-&gt;sys_language_uid&#x201d; matches the sys_language uid for an element the state is set to &#x201c;ACT&#x201d;, otherwise &#x201c;NO&#x201d;. However, if a page is not available due to the pages &#x201c;Localization settings&#x201d; (which can disable translations) or if no Alternative Page Language record was found (can be disabled with &#x201c;.normalWhenNoLanguage&#x201d;, see below) the state is set to &#x201c;USERDEF1&#x201d; for non-active items and &#x201c;USERDEF2&#x201d; for active items. So in total there are four states to create designs for. It is recommended to disable the link on menu items rendered with &#x201c;USERDEF1&#x201d; and &#x201c;USERDEF2&#x201d; in this case since they are disabled exactly because a page in that language does not exist and might even issue an error if tried accessed (depending on site configuration).<br>&nbsp;<br>&nbsp;<b>.value</b> = comma list of sys_language uids to construct the menu with. The number of elements in this list determines the number of menu items.<br>&nbsp;<b>.normalWhenNoLanguage</b> = boolean, which if set will render the button for a language as a non-disabled button even if no translation is found for the language.<br>&nbsp;<b><br>Example:</b><br>Creates a language menu with flags (notice that some lines break):<br>&nbsp;<br>&nbsp;<br>&nbsp;<br>&nbsp;<br>lib.langMenu = HMENU<br>lib.langMenu.special = language<br>lib.langMenu.special.value = 0,1,2<br>lib.langMenu.1 = GMENU<br>lib.langMenu.1.NO {<br>&nbsp;&nbsp;&nbsp;XY = [5.w]+4, [5.h]+4<br>&nbsp;&nbsp;&nbsp;backColor = white<br>&nbsp;&nbsp;&nbsp;5 = IMAGE<br>&nbsp;&nbsp;&nbsp;5.file = media/flags/flag_uk.gif || media/flags/flag_fr.gif || media/flags/flag_es.gif <br>&nbsp;&nbsp;&nbsp;5.offset = 2,2<br>}<br>&nbsp;<br>lib.langMenu.1.ACT &lt; lib.langMenu.1.NO<br>lib.langMenu.1.ACT=1<br>lib.langMenu.1.ACT.backColor = black<br>&nbsp;<br>lib.langMenu.1.USERDEF1 &lt; lib.langMenu.1.NO<br>lib.langMenu.1.USERDEF1=1<br>lib.langMenu.1.USERDEF1.5.file = media/flags/flag_uk_d.gif || media/flags/flag_fr_d.gif || media/flags/flag_es_d.gif <br>lib.langMenu.1.USERDEF1.noLink = 1\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:11:\"userdefined\";s:8:\"datatype\";s:2231:\"Lets you write your own little PHP-script that generates the array of menuitems.<br>&nbsp;<b>.file</b> [resource] = filename of the php-file to include. (Just like cObject PHP_SCRIPT)<br>&nbsp;<b>.[any other key]</b> = your own variables to your script. They are all accessible in the array $conf in your script<br>&nbsp;<b><br>Howto:</b><br>You must populate an array called $menuItemsArray with page-records of the menuitems you want to be in the menu.<br>It goes like this:<br>&nbsp;<br>$menuItemsArray[] = pageRow1;<br>$menuItemsArray[] = pageRow2;<br>$menuItemsArray[] = pageRow3;<br>...<br>&nbsp;<br>A &#x201c;pageRow&#x201d; is a record from the table &#x201c;pages&#x201d; with all fields selected (SELECT * FROM...)<br>If you create fake page rows, make sure to add at least &#x201c;title&#x201d; and &#x201c;uid&#x201d; field values.<br>&nbsp;<b><br>Notice:</b><br>If you work with mount-points you can set the MP param which should be set for the page by setting the internal field &#x201c;_MP_PARAM&#x201d; in the page-record (xxx-xxx).<br>&nbsp;<b><br>Overriding URLs:</b><br>You can also use the internal field &quot;_OVERRIDE_HREF&quot; to set a custom href-value (eg. &quot;http://www.typo3.org&quot;) which will in any case be used rather than a link to the page that the page otherwise might represent. If you use &quot;_OVERRIDE_HREF&quot; then &quot;_OVERRIDE_TARGET&quot; can be used to override the target value as well (See example below).<br>&nbsp;<b><br>Other reserved keys:</b><br>&#x201c;_ADD_GETVARS&#x201d; can be used to add get parameters to the URL, eg. &#x201c;&amp;L=xxx&#x201d;.<br>&#x201c;_SAFE&#x201d; can be used to protect the element to make sure it is not filtered out for any reason.<br>&nbsp;<b><br>Creating submenus:</b><br>You can create submenus for the next level easily by just adding an array of menu items in the internal field &quot;_SUB_MENU&quot; (See example below).<br>&nbsp;<b><br>Presetting element state</b><br>If you would like to preset an element to be recognized as a SPC, IFSUB, ACT, CUR or USR mode item, you can do so by specifying one of these values in the key &#x201c;ITEM_STATE&#x201d; of the page record. This setting will override the natural state-evaluation.\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:12:\"userfunction\";s:8:\"datatype\";s:184:\"Calls a user function/method in class which should (as with &#x201c;userdefined&#x201d; above) return an array with page records for the menu.<br>&nbsp;<b>.userFunc</b> = function-name\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";N;s:12:\"column_count\";i:3;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (71,7389802,'51fb0254a54165c4bb95358367ae0aec','','tsref:(cObject).CTABLE','a:1:{s:4:\"rows\";a:9:{i:0;a:6:{s:8:\"property\";s:6:\"offset\";s:8:\"datatype\";s:3:\"x,y\";s:11:\"description\";s:29:\"Offset from upper left corner\";s:7:\"default\";s:11:\"0,0 = intet\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:2:\"tm\";s:8:\"datatype\";s:21:\"-&gt;CARRAY +TDParams\";s:11:\"description\";s:7:\"topMenu\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:2:\"lm\";s:8:\"datatype\";s:21:\"-&gt;CARRAY +TDParams\";s:11:\"description\";s:8:\"leftMenu\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:2:\"rm\";s:8:\"datatype\";s:21:\"-&gt;CARRAY +TDParams\";s:11:\"description\";s:9:\"rightMenu\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:2:\"bm\";s:8:\"datatype\";s:21:\"-&gt;CARRAY +TDParams\";s:11:\"description\";s:10:\"bottomMenu\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:1:\"c\";s:8:\"datatype\";s:21:\"-&gt;CARRAY +TDParams\";s:11:\"description\";s:13:\"content-cell \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:8:\"cMargins\";s:8:\"datatype\";s:7:\"margins\";s:11:\"description\";s:46:\"Distance around the content-cell &quot;c&quot;\";s:7:\"default\";s:7:\"0,0,0,0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:6:\"cWidth\";s:8:\"datatype\";s:6:\"pixels\";s:11:\"description\";s:39:\"Width of the content-cell &quot;c&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:11:\"tableParams\";s:8:\"datatype\";s:20:\"&lt;TABLE&gt;-params\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:36:\"border=0 cellspacing=0 cellpadding=0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (72,7389802,'d15fe4c25a73d14a1233602ae5c00aeb','','tsref:(cObject).OTABLE','a:1:{s:4:\"rows\";a:3:{i:0;a:6:{s:8:\"property\";s:6:\"offset\";s:8:\"datatype\";s:3:\"x,y\";s:11:\"description\";s:334:\"Offset from upper left corner<br>&nbsp;<b><br>Note:</b><br>Actually the datatype is &#x201c;x,y,r,b,w,h&#x201d;:<br>x,y is offset from upperleft corner<br>r,b is offset (margin) to right and bottom<br>w is the required width of the content field<br>h is the required height of the content field<br>&nbsp;<br>All measures is in pixels.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:10:\"1,2,3,4...\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:11:\"tableParams\";s:8:\"datatype\";s:20:\"&lt;TABLE&gt;-params\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:36:\"border=0 cellspacing=0 cellpadding=0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (73,7389802,'ac73d41309997ef5db797e0ac823c30f','','tsref:(cObject).COLUMNS','a:1:{s:4:\"rows\";a:12:{i:0;a:6:{s:8:\"property\";s:11:\"tableParams\";s:8:\"datatype\";s:20:\"&lt;TABLE&gt;-params\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:36:\"border=0 cellspacing=0 cellpadding=0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:8:\"TDparams\";s:8:\"datatype\";s:17:\"&lt;TD&gt;-params\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:10:\"valign=top\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:4:\"rows\";s:8:\"datatype\";s:17:\"int (Range: 2-20)\";s:11:\"description\";s:34:\"The number of rows in the columns.\";s:7:\"default\";s:1:\"2\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:10:\"totalWidth\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:35:\"The total-width of the columns+gaps\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:8:\"gapWidth\";s:8:\"datatype\";s:28:\"int /stdWrap<br>+optionSplit\";s:11:\"description\";s:48:\"Width of the gap between columns. <br>0 = no gap\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:8:\"gapBgCol\";s:8:\"datatype\";s:35:\"HTML-color /stdWrap<br>+optionSplit\";s:11:\"description\";s:39:\"background-color for the gap-tablecells\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:16:\"gapLineThickness\";s:8:\"datatype\";s:28:\"int /stdWrap<br>+optionSplit\";s:11:\"description\";s:72:\"lineThickness of the dividerline in the gap between cells<br>0 = no line\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:10:\"gapLineCol\";s:8:\"datatype\";s:35:\"HTML-color /stdWrap<br>+optionSplit\";s:11:\"description\";s:10:\"Line color\";s:7:\"default\";s:5:\"black\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:29:\"[column-number]<br>1,2,3,4...\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:44:\"This is the content-object for each column!!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:5:\"after\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:50:\"This is a cObject placed after the columns-table!!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:2:\"if\";s:8:\"datatype\";s:7:\"-&gt;if\";s:11:\"description\";s:61:\"if &quot;if&quot; returns false the columns are not rendered!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (74,7389802,'735744d82b01f1ae8ee357b35a445a18','','tsref:(cObject).HRULER','a:1:{s:4:\"rows\";a:6:{i:0;a:6:{s:8:\"property\";s:13:\"lineThickness\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:11:\"Range: 1-50\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:9:\"lineColor\";s:8:\"datatype\";s:10:\"HTML-color\";s:11:\"description\";s:23:\"The color of the ruler.\";s:7:\"default\";s:5:\"black\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:9:\"spaceLeft\";s:8:\"datatype\";s:6:\"pixels\";s:11:\"description\";s:35:\"space before the line (to the left)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:10:\"spaceRight\";s:8:\"datatype\";s:6:\"pixels\";s:11:\"description\";s:35:\"space after the line (to the right)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:10:\"tableWidth\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:63:\"Width of the ruler (&#x201c;width&#x201d; attribute in a table)\";s:7:\"default\";s:3:\"99%\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (75,7389802,'99c88493b5fbff11087e84f8b0caf05e','','tsref:(cObject).IMGTEXT','a:1:{s:4:\"rows\";a:36:{i:0;a:6:{s:8:\"property\";s:4:\"text\";s:8:\"datatype\";s:20:\"-&gt;CARRAY /stdWrap\";s:11:\"description\";s:82:\"Use this to import / generate the content, that should flow around the imageblock.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:7:\"textPos\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:352:\"Textposition:<br>bit[0-2]: 000 = centre, 001 = right, 010 = left<br>bit[3-5]: 000 = over, 001 = under, 010 text<br>&nbsp;<br>0 - Above: Centre<br>1 - Above: Right<br>2 - Above: Left<br>8 - Below: Centre<br>9 - Below: Right<br>10 - Below: Left<br>17 - In Text: Right<br>18 - In Text: Left<br>25 - In Text: Right (no wrap)<br>26 - In Text: Left (no wrap)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:10:\"textMargin\";s:8:\"datatype\";s:15:\"pixels /stdWrap\";s:11:\"description\";s:40:\"margin between the image and the content\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:20:\"textMargin_outOfText\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:337:\"If set, the textMargin space will still be inserted even if the image is placed above or below the text. <br>This flag is only for a kind of backwards compatibility because this &quot;feature&quot; was recently considered a bug and thus corrected. So if anyone has depended on this way things are done, you can compensate with this flag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:7:\"imgList\";s:8:\"datatype\";s:40:\"&nbsp;<i>list of imagefiles</i> /stdWrap\";s:11:\"description\";s:176:\"list of images from &quot;.imgPath&quot;<br>&nbsp;<b><br>Example:</b><br>This imports the list of images from tt_content&#039;s image-field<br>&quot;imgList.field = image&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:7:\"imgPath\";s:8:\"datatype\";s:13:\"path /stdWrap\";s:11:\"description\";s:76:\"Path to the images<br>&nbsp;<b><br>Example:</b><br>&quot;uploads/pics/&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:6:\"imgMax\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:20:\"max number of images\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:8:\"imgStart\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:45:\"start with image-number &quot;.imgStart&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:9:\"imgObjNum\";s:8:\"datatype\";s:35:\"&nbsp;<i>imgObjNum</i> +optionSplit\";s:11:\"description\";s:410:\"Here you define, which IMAGE-cObjects from the array &quot;1,2,3,4...&quot; in this object that should render the images.<br>&quot;current&quot; is set to the image-filename.<br>&nbsp;<b><br>Example:</b><br>&quot;imgObjNum = 1 |*||*| 2&quot;:<br>This would render the first two images with &quot;1. ...&quot; and the last image with &quot;2. ...&quot;, provided that the &quot;.imgList&quot; contains 3 images.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:7:\"1,2,3,4\";s:8:\"datatype\";s:20:\"-&gt;IMAGE (cObject)\";s:11:\"description\";s:323:\"Rendering of the images<br>The register &quot;IMAGE_NUM&quot; is set with the number of image being rendered for each rendering of a image-object. Starting with zero.<br>The image-object should not be of type GIFBUILDER!<br>&nbsp;<b><br>Important:</b><br>&quot;file.import.current = 1&quot; fetches the name of the images! \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:7:\"caption\";s:8:\"datatype\";s:20:\"-&gt;CARRAY /stdWrap\";s:11:\"description\";s:7:\"Caption\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:12:\"captionAlign\";s:8:\"datatype\";s:14:\"align /stdWrap\";s:11:\"description\";s:17:\"Caption alignment\";s:7:\"default\";s:30:\"default = &quot;.textPos&quot;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:12:\"captionSplit\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:379:\"If this is set, the caption text is split by the character (or string) from &quot;.token&quot; , and every item is displayed under an image each in the image block.<br>.token = (string /stdWrap) Character to split the caption elements (default is chr(10))<br>.cObject = cObject, used to fetch the caption for the split<br>.stdWrap = stdWrap properties used to render the caption.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:20:\"altText<br>titleText\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:215:\"Default altText/titleText if no alternatives are provided by the -&gt;IMAGE cObjects <br>&nbsp;<br>If no titltext is specified, it will use the alttext insteadIf no alttext is specified, it will use an empty alttext\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:11:\"longdescURL\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:186:\"Default longdescURL if no alternatives are provided by the -&gt;IMAGE cObjects <br>&nbsp;<br>&quot;longdesc&quot; attribute (URL pointing to document with extensive details about image).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:6:\"border\";s:8:\"datatype\";s:17:\"boolean /stdWrap \";s:11:\"description\";s:48:\"If true, a border i generated around the images.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:9:\"borderCol\";s:8:\"datatype\";s:32:\"&nbsp;<i>HTML-color</i> /stdWrap\";s:11:\"description\";s:50:\"Color of the border, if &quot;.border&quot; is set\";s:7:\"default\";s:5:\"black\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:11:\"borderThick\";s:8:\"datatype\";s:15:\"pixels /stdWrap\";s:11:\"description\";s:39:\"Width of the border around the pictures\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:4:\"cols\";s:8:\"datatype\";s:13:\"int /stdWrap \";s:11:\"description\";s:7:\"Columns\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:19;a:6:{s:8:\"property\";s:4:\"rows\";s:8:\"datatype\";s:13:\"int /stdWrap \";s:11:\"description\";s:44:\"Rows (higher priority thab &quot;cols&quot;)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:20;a:6:{s:8:\"property\";s:6:\"noRows\";s:8:\"datatype\";s:16:\"boolean /stdWrap\";s:11:\"description\";s:145:\"If set, the rows are not divided by a table-rows. Thus images are more nicely shown if the height differs a lot (normally the width is the same!)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:21;a:6:{s:8:\"property\";s:6:\"noCols\";s:8:\"datatype\";s:16:\"boolean /stdWrap\";s:11:\"description\";s:208:\"If set, the columns are not made in the table. The images are all put in one row separated by a clear giffile to space them apart.<br>If noRows is set, noCols will be unset. They cannot be set simultaneously.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:22;a:6:{s:8:\"property\";s:8:\"colSpace\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:21:\"space between columns\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:23;a:6:{s:8:\"property\";s:8:\"rowSpace\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:18:\"space between rows\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:24;a:6:{s:8:\"property\";s:15:\"spaceBelowAbove\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:101:\"Pixelsspace between content an images when position of image is above or belox text (but not in text)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:25;a:6:{s:8:\"property\";s:12:\"tableStdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:85:\"This passes the final &lt;table&gt; code for the image block to the stdWrap function.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:26;a:6:{s:8:\"property\";s:4:\"maxW\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:195:\"max width of the image-table. <br>This will scale images not in the right size! Takes the number of columns into account!<br>&nbsp;<br>&nbsp;<b>NOTE: </b>Works ONLY if IMAGE-obj is NOT GIFBUILDER\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:27;a:6:{s:8:\"property\";s:10:\"maxWInText\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:269:\"max width of the image-table, if the text is wrapped around the image-table (on the left or right side).<br>This will scale images not in the right size! Takes the number of columns into account!<br>&nbsp;<br>&nbsp;<b>NOTE: </b>Works ONLY if IMAGE-obj is NOT GIFBUILDER\";s:7:\"default\";s:11:\"50% of maxW\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:28;a:6:{s:8:\"property\";s:6:\"equalH\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:655:\"If this value is greater than zero, it will secure that images in a row has the same height. The width will be calculated.<br>If the total width of the images raise above the &quot;maxW&quot;-value of the table the height for each image will be scaled down equally so that the images still have the same height but is within the limits of the totalWidth.<br>Please note that this value will override the properties &quot;width&quot;, &quot;maxH&quot;, &quot;maxW&quot;, &quot;minW&quot;, &quot;minH&quot; of the IMAGE-objects generating the images. Furthermore it will override the &quot;noRows&quot;-property and generate a table with no columns instead!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:29;a:6:{s:8:\"property\";s:12:\"colRelations\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:629:\"This value defines the width-relations of the images in the columns of IMGTEXT. The syntax is &quot;[int] : [int] : [int] : ...&quot; for each column. If there are more imagecolumns than figures in this value, it&#039;s ignored. If the relation between two of these figures exceeds 10, this function is ignore. <br>It works only fully if all images are downscaled by their maxW-definition.<br>&nbsp;<b><br>Example:</b><br>If 6 images are placed in three columns and their width&#039;s are high enough to be forcibly scaled, this value will scale the images in the to be eg. 100, 200 and 300 pixels from left to right<br>1 : 2 : 3\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:30;a:6:{s:8:\"property\";s:17:\"image_compression\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:996:\"Image Compression:<br>0= Default<br>1= Dont change! (removes all parameters for the image_object!!)<br>(adds gif-extension and color-reduction command)<br>10= GIF/256<br>11= GIF/128<br>12= GIF/64<br>13= GIF/32<br>14= GIF/16<br>15= GIF/8<br>(adds jpg-extension and quality command)<br>20= IM: -quality 100<br>21= IM: -quality 90 &lt;=&gt; Photoshop 60 &nbsp;&nbsp;&nbsp;&nbsp;(JPG/Very High)<br>22= IM: -quality 80 &nbsp;&nbsp;&nbsp;(JPG/High)<br>23= IM: -quality 70<br>24= IM: -quality 60 &lt;=&gt; Photoshop 30 &nbsp;&nbsp;(JPG/Medium)<br>25= IM: -quality 50<br>26= IM: -quality 40 (JPG/Low)<br>27= IM: -quality 30 &lt;=&gt; Photoshop 10 <br>28= IM: -quality 20 &nbsp;&nbsp;(JPG/Very Low)<br>&nbsp;<br>The default ImageMagick quality seems to be 75. This equals Photoshop quality 45. Images compressed with ImageMagick with the same visual quality as a Photoshop-compressed image seems to be largely 50% greater in size!!<br>&nbsp;<br>&nbsp;<b>NOTE: </b>Works ONLY if IMAGE-obj is NOT GIFBUILDER\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:31;a:6:{s:8:\"property\";s:13:\"image_effects\";s:8:\"datatype\";s:12:\"int /stdWrap\";s:11:\"description\";s:548:\"Adds these commands to the parameteres for the scaling. This function has no effect if &quot;image_compression&quot; above is set to 1!!<br>&nbsp;<br>1 =&gt; &quot;-rotate 90&quot;,<br>2 =&gt; &quot;-rotate 270&quot;,<br>3 =&gt; &quot;-rotate 180&quot;,<br>10 =&gt; &quot;-colorspace GRAY&quot;,<br>11 =&gt; &quot;-sharpen 70&quot;,<br>20 =&gt; &quot;-normalize&quot;,<br>23 =&gt; &quot;-contrast&quot;,<br>25 =&gt; &quot;-gamma 1.3&quot;,<br>26 =&gt; &quot;-gamma 0.8&quot;<br>&nbsp;<br>&nbsp;<b>NOTE: </b>Works ONLY if IMAGE-obj is NOT GIFBUILDER\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:32;a:6:{s:8:\"property\";s:12:\"image_frames\";s:8:\"datatype\";s:35:\"Array <br>+ .key /stdWrap<br>&nbsp;\";s:11:\"description\";s:1175:\"<b>Frames:</b><br>.key points to the frame used.<br>&nbsp;<br>&quot;.image_frames.x&quot; is imgResource-mask (&quot;.m&quot;)properties which will override to the [imgResource].m properties of the imageObjects. This is used to mask the images into a frame. See how it&#039;s done in the default configuration and IMGTEXT in the static_template-table. <br>&nbsp;<b><br>Example:</b><br>1 {<br>&nbsp;&nbsp;&nbsp;mask = media/uploads/darkroom1_mask.jpg<br>&nbsp;&nbsp;&nbsp;bgImg = GIFBUILDER<br>&nbsp;&nbsp;&nbsp;bgImg {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;XY = 100,100<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;backColor = {$bgCol}<br>&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;bottomImg = GIFBUILDER<br>&nbsp;&nbsp;&nbsp;bottomImg {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;XY = 100,100<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;backColor = black<br>&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;bottomImg_mask = media/uploads/darkroom1_bottom.jpg<br>}<br>&nbsp;<br>&nbsp;<b>NOTE: </b>This cancels the jpg-quality settings sent as ordinary &quot;.params&quot; to the imgResource. In addition the output of this operation will always be jpg or gif!<br>&nbsp;<b>NOTE: </b>Works ONLY if IMAGE-obj is NOT GIFBUILDER\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:33;a:6:{s:8:\"property\";s:9:\"editIcons\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:23:\"(See stdWrap.editIcons)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:34;a:6:{s:8:\"property\";s:23:\"noStretchAndMarginCells\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:279:\"If set (1), the cells used to add left and right margins plus stretch out the table will not be added. You will loose the ability to set margins for the object if entered &#x201c;in text&#x201d;. So it&#039;s not recommended, but it has been requested by some people for reasons.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:35;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (76,7389802,'7831f428e4ab5909c05af00f0f34071e','','tsref:(cObject).CASE','a:1:{s:4:\"rows\";a:6:{i:0;a:6:{s:8:\"property\";s:10:\"setCurrent\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:36:\"Sets the &quot;current&quot;-value. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:3:\"key\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:12:\"This is the \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:7:\"default\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:15:\"<i>Array...</i>\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:2:\"if\";s:8:\"datatype\";s:7:\"-&gt;if\";s:11:\"description\";s:51:\"if &quot;if&quot; returns false nothing is returned\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (77,7389802,'11559da8560e5a25a5d94dc2ababdc78','','tsref:(cObject).LOAD_REGISTER','a:1:{s:4:\"rows\";a:1:{i:0;a:6:{s:8:\"property\";s:38:\"<i>Array... </i><i><br>[fieldname]</i>\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:347:\"<b>Example:</b><br>(This sets &quot;contentWidth&quot;, &quot;label&quot; and &quot;head&quot;)<br>&nbsp;<br>page.27 = LOAD_REGISTER<br>page.27 {<br>&nbsp;&nbsp;&nbsp;contentWidth = 500 <br>&nbsp;<br>&nbsp;&nbsp;&nbsp;label.field = header<br>&nbsp;<br>&nbsp;&nbsp;&nbsp;head = some text<br>&nbsp;&nbsp;&nbsp;head.wrap = &lt;B&gt; | &lt;/B&gt;<br>}\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (78,7389802,'d6df84910bc06faaa29868176f91b324','','tsref:(cObject).FORM','a:1:{s:4:\"rows\";a:38:{i:0;a:6:{s:8:\"property\";s:4:\"data\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:102:\"This is the data that sets up the form. See above.<br>&quot;||&quot; can be used instead of linebreaks\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:9:\"dataArray\";s:8:\"datatype\";s:31:\"<i>[array of form elements]</i>\";s:11:\"description\";s:2835:\"This is an alternative way to define the form-fields. Instead of using the syntax with vertical separator bars suggested by the .data property, you can define the elements in regular TypoScript style arrays.<br>.dataArray is <i>added </i>to the input in .data if any.<br>Every entry in the dataArray is numeric and has three main properties, <i>label</i>, <i>type</i>, <i>value</i> and <i>required</i>. &#039;label&#039; and &#039;value&#039; has stdWrap properties. <br>There is an alternative property to .value, which is .valueArray. This is also an array in the same style with numeric entries which has properties <i>label</i>, <i>value</i> and <i>selected</i>. &#039;label&#039; has stdWrap properties.<br>&nbsp;<b><br>Example:</b><br>&nbsp;&nbsp;&nbsp;dataArray {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.label = Name:<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.type = name=input<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.value = [Enter name]<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.required = 1<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.label = Eyecolor<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.type = eyecolor=select<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.valueArray {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.label = Blue<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.value = 1<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.label = Red<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.value = 2<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.selected = 1<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;40.type = submit=submit<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;40.value = Submit<br>&nbsp;&nbsp;&nbsp;}<br>&nbsp;<br>&nbsp;<br>This is the same as this line in the .data property:<br>&nbsp;<br>Name: | *name=input | [Enter name]<br>Eyecolor: | eyecolor=select | Blue=1, *Red=2<br>| submit=submit | Submit<br>&nbsp;<br>&nbsp;<b>Why do it this way?</b> <br>Inserting an email-field after the name field would be like this:<br>&nbsp;&nbsp;&nbsp;dataArray {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;15.label = Email:<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;15.type = input<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;15.value = <br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;15.specialEval = EMAIL<br>&nbsp;&nbsp;&nbsp;}<br>&nbsp;<br>&nbsp;Or translating the form to danish (setting config.language to &#039;dk&#039;):<br>&nbsp;<br>&nbsp;&nbsp;&nbsp;dataArray {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.label.lang.dk = Navn:<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.value.lang.dk = [Indtast dit navn]<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.label.lang.dk = &#xd8;jenfarve<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.valueArray {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.label.lang.dk = Bl&#xe5;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.label.lang.dk = R&#xf8;d<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;40.value.lang.dk = Send<br>&nbsp;&nbsp;&nbsp;}<br>&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:9:\"radioWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:33:\"Wraps the labels for radiobuttons\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:4:\"type\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:742:\"Type (action=&quot;&quot; of the form):<br>&nbsp;<br>&nbsp;<b>Integer:</b> this is regarded to be a page in TYPO3<br>&nbsp;<b>String:</b> this is regarded to be a normal URL (eg. &quot;formmail.php&quot; or &quot;fe_tce_db.php&quot;)<br>&nbsp;<b>Empty:</b> the current page is chosen.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> If type is integer/empty the form will be submitted to a page in TYPO3 and if this page has a value for target/no_cache, then this will be used instead of the default target/no_cache below.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> If the redirect-value is set, the redirect-target overrides the target set by the action-url<br>&nbsp;<br>&nbsp;<b>NOTE:</b> May be overridden by the property override feature of the formdata (see above)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:6:\"target\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:28:\"Default target of the form. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:6:\"method\";s:8:\"datatype\";s:11:\"form-method\";s:11:\"description\";s:23:\"<b>Example: </b><br>GET\";s:7:\"default\";s:4:\"POST\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:8:\"no_cache\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:23:\"Default no_cache-option\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:13:\"noValueInsert\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:434:\"By default values that are submitted to the same page (and thereby same form, eg. at searchforms) are re-inserted in the form instead of any default-data that might be set up. <br>This, however, applies ONLY if the &quot;no_cache=1&quot; is set! (a page being cached may not include user-specific defaults in the fields of course...) <br>If you set this flag, &quot;noValueInsert&quot;, the content will always be the default content.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:20:\"compensateFieldWidth\";s:8:\"datatype\";s:6:\"double\";s:11:\"description\";s:85:\"Overriding option to the config-value of the same name. See &quot;CONFIG&quot; above.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:12:\"locationData\";s:8:\"datatype\";s:16:\"boolean / string\";s:11:\"description\";s:1479:\"If this value is true, then a hidden-field called &quot;locationData&quot; is added to the form. This field wil be loaded with a value like this:<br>[page id]:[current record table]:[current record id]<br>For example, if a formfield is inserted on page with uid = &quot;100&quot;, as a page-content item from the table &quot;tt_content&quot; with id &quot;120&quot;, then the value would be &quot;100:tt_content:120&quot;.<br>The value is use by eg. the cObject SEARCHRESULT. If the value $GLOBALS[&quot;HTTP_POST_VARS&quot;][&quot;locationData&quot;] is detected here, the search is done as if it was performed on this page! This is very usefull if you want a search functionality implemented on a page with the &quot;stype&quot; field set to &quot;L1&quot; which means that the search is carried out from the first level in the rootline. <br>Suppose you want the search to submit to a dedicated searchpage where ever. This page will then know - because of locationData - that the search was submittet from another place on the website.<br>If &quot;locationData&quot; is not only true but also set to &quot;HTTP_POST_VARS&quot; then the value will insert the content of $GLOBALS[&quot;HTTP_POST_VARS&quot;][&quot;locationData&quot;] instead of the true location data of the page. This should be done with search-fields as this will carry the initial searching start point with.<br>&nbsp;<b>NOTE:</b> May be overridden by the property override feature of the formdata (see above)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:8:\"redirect\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:372:\"URL to redirect to (generates the hidden field &quot;redirect&quot;)<br>&nbsp;<br>&nbsp;<b>Integer:</b> this is regarded to be a page in TYPO3<br>&nbsp;<b>String:</b> this is regarded to be a normal url<br>&nbsp;<b>Empty;</b> the current page is chosen.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> If this value is set the target of this overriddes the target of the &quot;type&quot;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:9:\"recipient\";s:8:\"datatype\";s:38:\"&nbsp;<i>(list of) string</i> /stdWrap\";s:11:\"description\";s:89:\"Email recipient of the formmail content (generates the hiddenfield &quot;recipient&quot;)\";s:7:\"default\";s:8:\"No email\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:8:\"goodMess\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:184:\"Message for the formevaluation function in case of correctly filled form.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> May be overridden by the property override feature of the formdata (see above)\";s:7:\"default\";s:10:\"No message\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:7:\"badMess\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:238:\"Prefixed Message for the formevaluation in case of missing required fields. <br>This message is shown above the list of fields.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> May be overridden by the property override feature of the formdata (see above)\";s:7:\"default\";s:10:\"No message\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:9:\"emailMess\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:180:\"Message if a field evaluated to be an email adresse did not validate.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> May be overridden by the property override feature of the formdata (see above)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:5:\"image\";s:8:\"datatype\";s:20:\"-&gt;IMAGE (cObject)\";s:11:\"description\";s:171:\"If this is a valid image the submitbutton is rendered as this image!!<br>&nbsp;<br>&nbsp;<b>NOTE:</b> CurrentValue is set to the caption-label before generating the image.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:6:\"layout\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:471:\"This defines how the label and the field are placed towards each other.<br>&nbsp;<b><br>Example:</b><br>This substitutes the &quot;###FIELD###&quot; with the field data and the &quot;###LABEL###&#039; with labeldata. <br>&nbsp;<br>&lt;tr&gt;&lt;td&gt;###FIELD###&lt;/td&gt;&lt;td&gt; ###LABEL###&lt;/td&gt;&lt;/tr&gt;<br>&nbsp;<br>You can also use the marker ###COMMENT### which is ALSO the label value inserted, but wrapped in .commentWrap stdWrap-properties (see below)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:9:\"fieldWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:23:\"Field: Wraps the fields\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:9:\"labelWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:23:\"Labels: Wraps the label\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:19;a:6:{s:8:\"property\";s:11:\"commentWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:52:\"Comments: Wrap for comments IF you use ###COMMENT###\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:20;a:6:{s:8:\"property\";s:3:\"REQ\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:58:\"Defines if required-fields should be checked and marked up\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:21;a:6:{s:8:\"property\";s:13:\"REQ.fieldWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:47:\"Field: Wraps the fields, but for reuired fields\";s:7:\"default\";s:34:\"the &quot;fieldWrap&quot;-property\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:22;a:6:{s:8:\"property\";s:13:\"REQ.labelWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:47:\"Labels: Wraps the label, but for reuired fields\";s:7:\"default\";s:34:\"the &quot;labelWrap&quot;-property\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:23;a:6:{s:8:\"property\";s:10:\"REQ.layout\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:60:\"The same as &quot;layout&quot; above, but for reuired fields\";s:7:\"default\";s:31:\"the &quot;layout&quot;-property\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:24;a:6:{s:8:\"property\";s:14:\"COMMENT.layout\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:32:\"Alternative layout for comments.\";s:7:\"default\";s:31:\"the &quot;layout&quot;-property\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:25;a:6:{s:8:\"property\";s:12:\"CHECK.layout\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:33:\"Alternative layout for checkboxes\";s:7:\"default\";s:31:\"the &quot;layout&quot;-property\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:26;a:6:{s:8:\"property\";s:12:\"RADIO.layout\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:35:\"Alternative layout for radiobuttons\";s:7:\"default\";s:31:\"the &quot;layout&quot;-property\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:27;a:6:{s:8:\"property\";s:12:\"LABEL.layout\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:34:\"Alternative layout for label types\";s:7:\"default\";s:31:\"the &quot;layout&quot;-property\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:28;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:46:\"Wraps the hole form (before formtags is added)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:29;a:6:{s:8:\"property\";s:12:\"hiddenFields\";s:8:\"datatype\";s:18:\"[array of cObject]\";s:11:\"description\";s:221:\"Used to set hiddenFields from TS.<br>&nbsp;<b><br>Example:</b><br>hiddenFields.pid = TEXT<br>hiddenFields.pid.value = 2<br>&nbsp;<br>This makes a hidden-field with the name &#x201c;pid&#x201d; and value &#x201c;2&#x201d;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:30;a:6:{s:8:\"property\";s:6:\"params\";s:8:\"datatype\";s:27:\"form-element tag parameters\";s:11:\"description\";s:288:\"Extra parameters to form elements<br>&nbsp;<b><br>Example:</b><br>params = style=&#x201d;width:200px;&#x201d;<br>params.textarea = style=&#x201d;width:300px;&#x201d;<br>params.check = <br>&nbsp;<br>This sets the default to 200 px width, but excludes check-boxes and sets textareas to 300.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:31;a:6:{s:8:\"property\";s:13:\"wrapFieldName\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:222:\"This wraps the fieldnames before they are applied to the form-field tags.<br>&nbsp;<b><br>Example:</b><br>If value is then the fieldname &quot;email&quot; would be wrapped to this value: <i>tx_myextension[input][email]</i>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:32;a:6:{s:8:\"property\";s:10:\"noWrapAttr\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:281:\"If this value is true then all wrap attributes of textarea elements are suppressed. This is needed for XHTML-compliancy.<br>&nbsp;<br>The wrap attributes can also be disabled on a per-field basis by using the special keyword &quot;disabled&quot; as the value of the wrap attribute.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:33;a:6:{s:8:\"property\";s:15:\"arrayReturnMode\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:217:\"If set, the &lt;form&gt; tags and the form content will be returned in an array as separate elements including other pratical values. This mode is for use in extensions where the array return value can be more useful.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:34;a:6:{s:8:\"property\";s:13:\"accessibility\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:461:\"If set, then the form will be compliant with accessibility guidelines (XHTML compliant). This includes:<br>&nbsp;<br>label string will be wrapped in &lt;label for=&quot;formname[fieldname-hash]&quot;&gt; ... &lt;/label&gt;<br>All form elements will have an id-attribute carrying the formname with the md5-hashed fieldname appended<br>&nbsp;<br>&nbsp;<b>Notice: </b>In TYPO3 4.0 and later, CSS Styled Content is configured to produce accessible forms by default.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:35;a:6:{s:8:\"property\";s:8:\"formName\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:125:\"An alternative name for this form. Default will be a unique (random) hash.<br>&nbsp;<br>&lt;form name=&#x201d;...&#x201d;&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:36;a:6:{s:8:\"property\";s:11:\"fieldPrefix\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:255:\"Alternative prefix for the name of the fields in this form. Otherwise, all fields are prefixed with the form name (either a unique hash or the name set in the &#x201c;formName&#x201d; property). If set to &#x201c;0&#x201d;, there will be no prefix at all.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:37;a:6:{s:8:\"property\";s:17:\"dontMd5FieldNames\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:272:\"The IDs generated for all elements in a form are md5 hashes from the fieldname. Setting this to true will disable this behaviour and use a cleaned fieldname, prefixed with the form name as the ID, instead.<br>This can be useful to style specifically named fields with CSS.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (79,7389802,'06d8abccf0aba6b413150e9c100f4bd4','','tsref:(cObject).SEARCHRESULT','a:1:{s:4:\"rows\";a:15:{i:0;a:6:{s:8:\"property\";s:11:\"allowedCols\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:120:\"List (separated by &quot;:&quot;) of allowed table-cols.<br>&nbsp;<b><br>Example:</b><br>pages.title:tt_content.bodytext\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"layout\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:368:\"This defines how the search content is shown.<br>&nbsp;<b><br>Example:</b><br>This substitutes the following fields:<br>###RANGELOW###:The low result range, eg. &quot;1&quot;<br>###RANGEHIGH###:The high result range, eg. &quot;10&quot;<br>###TOTAL###:The total results<br>###RESULT###:The result itself<br>&nbsp;###NEXT###:The next-button<br>###PREV###:The prev-button\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:4:\"next\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:126:\"This cObject will be wrapped by a link to the next searchresult. This is the code substituting the &quot;###NEXT###&quot;-mark\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:4:\"prev\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:126:\"This cObject will be wrapped by a link to the prev searchresult. This is the code substituting the &quot;###PREV###&quot;-mark\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:6:\"target\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:27:\"target til next/prev links!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:5:\"range\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:32:\"The number of results at a time!\";s:7:\"default\";s:2:\"20\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:9:\"renderObj\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:325:\"the cObject to render the searchresults<br>$cObj-&gt;data array is set to the resulting record from the search.<br>Please note, that in all fields are named [tablename]_[fieldnam]. Thus the pagetitle is in the field &quot;pages_title&quot;.<br>Apart from this, these fields from the pages-table are also present:<br>&nbsp;uid\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:10:\"renderWrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:9:\"resultObj\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:56:\"the cObject prepended in the search results returns rows\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:11:\"noResultObj\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:50:\"the cObject used if the search results in no rows.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:9:\"noOrderBy\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:87:\"If this is set, the result is NOT sorted after lastUpdated, tstamp for the pages-table.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:25:\"Wrap the whole content...\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:25:\"Wrap the whole content...\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:22:\"addExtUrlsAndShortCuts\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:408:\"If set, then the doktypes 3 and 4 (External URLS and Shortcuts) are added to the doktypes being searched.<br>However at this point in time, no pages will be select if they do not have at least one tt_content record on them! That is because the pages and tt_content (or other) table is joined. So there must at least one occurance of a tt_content element on a External URL / Shortcut page for them to show up.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:25:\"languageField.[2nd table]\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:202:\"Setting a field name to filter language on. This works like the &#x201c;languageField&#x201d; setting in -&gt;select<br>&nbsp;<b><br>Example:</b><br>&nbsp;<br>languageField.tt_content = sys_language_uid\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (80,7389802,'4a24c3602d0800d7feacc6759c6dbf21','','tsref:(cObject).USER/(cObject).USER_INT','a:1:{s:4:\"rows\";a:2:{i:0;a:6:{s:8:\"property\";s:15:\"<i>userFunc</i>\";s:8:\"datatype\";s:13:\"function-name\";s:11:\"description\";s:862:\"The name of the function. If you specify the name with a &#039;-&gt;&#039; in, it&#039;s intepreted as a call to a method in a class.<br>&nbsp;Two parameters are sent: A content variable (which is empty in this case, but not when used from stdWrap function .postUserFunc and .preUserFunc) and the second parameter is an array with the properties of this cObject if any.<br>&nbsp;<b><br>Example:</b><br>This TypoScript will display all content element headers of a page in reversed order. Please take a look in media/scripts/example_callfunction.php!!<br>(Also demonstrated on the testsite, page <br>&nbsp;<br>page = PAGE<br>page.typeNum=0<br>includeLibs.something = media/scripts/example_callfunction.php<br>&nbsp;<br>page.30 = USER<br>page.30 {<br>&nbsp;&nbsp;&nbsp;userFunc = user_various-&gt;listContentRecordsOnPage<br>&nbsp;&nbsp;&nbsp;reverseOrder = 1<br>}\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:11:\"includeLibs\";s:8:\"datatype\";s:29:\"&nbsp;<i>list of </i>resource\";s:11:\"description\";s:372:\"<b>(This property applies only if the object is created as USER_INT)</b><br>This is a comma-separated list of resources that are included as PHP-scripts (with include_once() function) if this script is included.<br>This is possible to do because any include-files will be known before the scripts are included. That&#039;s not the case with the regular PHP_SCRIPT cObject.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (84,7389802,'36fc199c62fb88c0a813769e3de9adc9','','tsref:(cObject).TEMPLATE','a:1:{s:4:\"rows\";a:9:{i:0;a:6:{s:8:\"property\";s:8:\"template\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:84:\"&nbsp;This must be loaded with the template-code. If not the object returns nothing.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:8:\"subparts\";s:8:\"datatype\";s:30:\"<i>Array... of </i><br>cObject\";s:11:\"description\";s:1253:\"This is an array of subpart-markers (case-sensitive).<br>A subpart is defined by two markers in the template. The markers must be wrapped by &quot;###&quot; on both sides. You may insert the subpart-markers inside HTML-comment-tags!! <br>&nbsp;<b><br>Example: </b><br>subparts {<br>&nbsp;&nbsp;&nbsp;HELLO = TEXT<br>&nbsp;&nbsp;&nbsp;HELLO.value = En subpart er blevet erstattet!!<br>}<br>&nbsp;<br>In the templates:<br>&lt;!-- start of subpart: ###HELLO### --&gt;<br>This is the HTML.code, that will be loaded in the register and replaced with the result...<br>&lt;!-- end ###HELLO### --&gt;<br>&nbsp;<b><br>NOTE:</b><br>Before the content-objects of each subpart is generated, all subparts in the array are extracted and loaded into the register so that you can load them from there later on. <br>The register-key for each subparts code is &quot;SUBPART_[theSubpartkey]&quot;.<br>In addition the current-value is loaded with the content of each subpart just before the cObject for the subpart is parsed. That makes it quite easy to load the subpart of the cObject (eg: &quot;.current=1&quot;)<br>Eg. this subpart above has the register-key &quot;SUBPART_HELLO&quot;.<i><br>This is valid ONLY if the property .nonCachedSubst is not set! (see below)</i>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:13:\"relPathPrefix\";s:8:\"datatype\";s:26:\"<i>string / properties</i>\";s:11:\"description\";s:757:\"Finds all relative references (eg. to images or stylesheets) and prefixes this value.<br>If you specify properties (uppercase) these will match HTML tags and specify alternative paths for them. See example below.<br>If the property is named &quot;style&quot; it will set alternative path for the &quot;url()&quot; wrapper that may be in &lt;style&gt; sections.<br>&nbsp;<b><br>Example:</b><br>page.10 = TEMPLATEpage.10 { template = FILE template.file = fileadmin/template.html relPathPrefix = fileadmin/<br>&nbsp;&nbsp;&nbsp;relPathPrefix.IMG = fileadmin/img/}<br>&nbsp;<br>Inthis example all relative paths found are prefixed &quot;fileadmin/&quot; unless it was the src attribute of an img tag in which case the path prefixed is &quot;fileadmin/img/&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:5:\"marks\";s:8:\"datatype\";s:30:\"<i>Array... of </i><br>cObject\";s:11:\"description\";s:451:\"This is an array of marks-markers (case-sensitive).<br>A mark is defined by one markers in the template. The marker must be wrapped by &quot;###&quot; on both sides. Opposite to subparts, you may NOT insert the subpart-markers inside HTML-comment-tags! (They will not be removed).<br>Marks are substituted bya str_replace-function. The subparts loaded in the register is available also to the cObjects of markers (only if .nonCachedSubst is not set!).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:5:\"wraps\";s:8:\"datatype\";s:30:\"<i>Array... of </i><br>cObject\";s:11:\"description\";s:487:\"This is an array of wraps-markers (case-sensitive).<br>This is shown best by an example:<b><br>Example: </b><br>subparts {<br>&nbsp;&nbsp;&nbsp;MYLINK = TEXT<br>&nbsp;&nbsp;&nbsp;MYLINK.value = &lt;A href=&#x201d;#&#x201d;&gt; | &lt;/A&gt;<br>}<br>&nbsp;<br>In the template:<br>This is &lt;!--###MYLINK###--&gt;a link to my&lt;!--###MYLINK###--&gt; page!<br>&nbsp;<br>In this example the MYLINK subpart will be substituted by the wrap which is the content returned by the MYLINK cObject.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:13:\"workOnSubpart\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:204:\"This is an optional definition of a subpart, that we decide to work on. In other words; if you define this value that subpart is extracted from the template and is the basis for this whole templateobject.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:10:\"markerWrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:235:\"This is the wrap the markers is wrapped with. The default value is ### | ### resulting in the markers to be presented as ###[marker_key]###.<br>Any whitespace around the wrap-items is stripped before they are set around the marker_key.\";s:7:\"default\";s:9:\"### | ###\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:20:\"substMarksSeparately\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:431:\"If set, then marks are substituted in the content AFTER the substitution of subparts and wraps. <br>Normally marks are not substituted inside of subparts and wraps when you are using the default cached mode of the TEMPLATE cObject. That is a problem if you have marks inside of subparts! But setting this flag will make the marker-substitution a non-cached, subsequent process.<br>Another solution is to turn of caching, see below.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:14:\"nonCachedSubst\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:711:\"If set, then the substitution mode of this cObject is totally different. Normally the raw template is read and divided into the sections denoted by the marks, subparts and wraps keys. The good thing is high speed, because this &#x201c;pre-parsed&#x201d; template is cached. The bad thing is that templates that depends on incremental substition (where the order of substition is important) will not work so well.<br>By setting this flag, markers are first substituted by str_replace in the template - one by one. Then the subparts are substituted one by one. And finally the wraps one by one.<br>Obviously you loose the ability to refer to other parts in the template with the register-keys as described above. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (85,7389802,'9e45ae1e8e57e33a34339154486c9e40','','tsref:(cObject).MULTIMEDIA','a:1:{s:4:\"rows\";a:3:{i:0;a:6:{s:8:\"property\";s:4:\"file\";s:8:\"datatype\";s:17:\"resource /stdWrap\";s:11:\"description\";s:213:\"The multimedia file. Types are:<br>txt, html, htm:Inserted directly<br>class:Java-applet<br>swf:Flash animation<br>swa, dcr:ShockWave Animation<br>wav,au:Sound<br>avi,mov,asf,mpg,wmv:Movies (AVI, QuickTime, MPEG4)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"params\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:351:\"This is parameters for the multimedia-objects. Use this to enter stuff like with and height:<br>&nbsp;<b><br>Example:</b><br>width=200<br>height=300<br>&nbsp;<br>... will generate a tag like &#039;&lt;embed .... width=&quot;200&quot; height=&quot;300&quot;&gt;&#039;<br>height=<br>&nbsp;<br>An empty string will remove the parameter from the embed-tag\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (86,7389802,'71d45380d5c1507c11c46fa7b95d72fa','','tsref:(cObject).EDITPANEL','a:1:{s:4:\"rows\";a:11:{i:0;a:6:{s:8:\"property\";s:5:\"label\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:123:\"Title for the panel. You can insert the record title with %s<br>&nbsp;<b><br>Example:</b><br>Section: &lt;B&gt;%s&lt;/B&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:5:\"allow\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:356:\"Define which functions are accessible. Further this list may be reduced, if the BE_USER does not have permission to perform the action<br>Values should be listed separated by comma. This is the options you can choose between:<br>toolbar,edit,new,delete,move,hide <br>(toolbar is a general list of icons regarding the page, so use this for pagerecords only)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:18:\"newRecordFromTable\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:98:\"Will display a panel for creation of new element (in the top of list) on the page from that table.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:14:\"newRecordInPid\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:70:\"Define a page ID where new records (except new pages) will be created.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:4:\"line\";s:8:\"datatype\";s:13:\"boolean / int\";s:11:\"description\";s:120:\"If set, a black line will appear after the panel. This value will indicate the distance from the black line to the panel\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:18:\"edit.displayRecord\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:67:\"If set, then the record edited is displayed above the editing form.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:14:\"onlyCurrentPid\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:99:\"If set, only records with a pid matching the current id (TSFE-&gt;id) will be shown with the panel.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:9:\"innerWrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:20:\"Wraps the edit panel\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:9:\"outerWrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:67:\"Wraps the whole edit panel including the black line (if configured)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:13:\"previewBorder\";s:8:\"datatype\";s:14:\"boolean / int \";s:11:\"description\";s:157:\"If set, the hidden/starttime/endtime/fe_user elements which are previewed will have a border around.<br>The integer value denotes the thickness of the border\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:73:\"previewBorder.innerWrap<br>previewBorder.outerWrap<br>previewBorder.color\";s:8:\"datatype\";s:17:\"wrap / HTML color\";s:11:\"description\";s:271:\"&nbsp;<b>innerWrap</b> wraps the content elements (including the icons) inside the preview border (an HTML table). <br>&nbsp;<br>&nbsp;<b>outerWrap</b> wraps the whole content element including the border. <br>&nbsp;<br>&nbsp;<b>color</b> denotes the color of the border.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (87,7389802,'92d229100f79be75aed1bd3d7f60373d','','tsref:_GIFBUILDER.charRangeMap','a:1:{s:4:\"rows\";a:4:{i:0;a:6:{s:8:\"property\";s:8:\"[array] \";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:1003:\"Basename of font file to match for this configuration. Notice that only the <i>filename</i> of the font file is used - the path is stripped off. This is done to make matching easier and avoid problems when font files might move to other locations in extensions etc.<br>&nbsp;<br>So if you use the font file &#x201c;EXT:myext/fonts/arial.ttf&#x201d; or &#x201c;t3lib/fonts/arial.ttf&#x201d; both of them will match with this configuration.<br>&nbsp;<b><br>The key:</b><br>The value of the array key will be the key used when forcing the configuration into &#x201c;splitRendering&#x201d; configuration of the individual GIFBUILDER objects. In the example below the key is &#x201c;123&#x201d;. <br>Notice; If the key is already found in the local GIFBUILDER configuration the content of that key is respected and not overridden. Thus you can make local configurations which override the global setting.<br>&nbsp;<b><br>Example:</b><br>_GIFBUILDER.charRangeMap {<br>&nbsp;&nbsp;&nbsp;123 = arial.ttf<br>....\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:21:\"[array].charMapConfig\";s:8:\"datatype\";s:43:\"TEXT / splitRendering.[array] configuration\";s:11:\"description\";s:879:\"splitRendering configuration to set. See GIFBUILDER TEXT object for details.<br>&nbsp;<b><br>Example:</b><br>&nbsp;<br>_GIFBUILDER.charRangeMap {<br>&nbsp;&nbsp;&nbsp;123 = arial.ttf<br>&nbsp;&nbsp;&nbsp;123 {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>charMapConfig {</b><b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontFile = t3lib/fonts/vera.ttf</b><b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;value = -65</b><b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontSize = 45</b><b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontSizeMultiplicator = 2.3<br>&nbsp;&nbsp;&nbsp;}<br>}<br>&nbsp;<br>This example configuration shows that GIFBUILDER TEXT objects with font faces matching &#x201c;arial.ttf&#x201d; will have a splitConfiguration that uses &#x201c;t3lib/fonts/vera.ttf&#x201d; for all characters that fall below/equal to 65 in Unicode value.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:29:\"[array].fontSizeMultiplicator\";s:8:\"datatype\";s:6:\"double\";s:11:\"description\";s:193:\"If set, this will take the font size of the TEXT GIFBUILDER object and multiply with this amount (xx.xx) and override the &#x201c;fontSize&#x201d; property inside &#x201c;charMapConfig&#x201d;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:29:\"[array].pixelSpaceFontSizeRef\";s:8:\"datatype\";s:6:\"double\";s:11:\"description\";s:1377:\"If set, this will multiply the four [x/y]Space[Before/After] properties of split rendering with the relationship between the fontsize and this value. <br>In other words; Since pixel space may vary depending on the font size used you can simply specify by this value at what fontsize the pixel space settings are optimized and for other fontsizes this will automatically be adjusted according to this font size.<br>&nbsp;<b><br>Example:</b><br>_GIFBUILDER.charRangeMap {<br>&nbsp;&nbsp;&nbsp;123 = arial.ttf<br>&nbsp;&nbsp;&nbsp;123 {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;charMapConfig {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontFile = t3lib/fonts/vera.ttf<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;value = 48-57<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;color = green<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>xSpaceBefore = 3</b><b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;xSpaceAfter = 3</b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>pixelSpaceFontSizeRef = 24</b><br>&nbsp;&nbsp;&nbsp;}<br>}<br>&nbsp;<br>In this example xSpaceBefore and xSpaceAfter will be &#x201c;3&#x201d; when the font size is 24. If this configuration is used on a GIFBUILDER TEXT object where the font size is only 16 the spacing values will be corrected by &#x201c;16/24&#x201d;, effectively reducing the pixelspace to &#x201c;2&#x201d; in that case.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (88,7389802,'07c97ef7ff913a09f3d43365e693254c','','tsref:->GIFBUILDER','a:1:{s:4:\"rows\";a:12:{i:0;a:6:{s:8:\"property\";s:10:\"1,2,3,4...\";s:8:\"datatype\";s:32:\"GifBuilderObj<br>+ .if (-&gt;if)\";s:11:\"description\";s:205:\".if (-&gt;if) is a property of all gifbuilder-objects. If the property is present and NOT set, the object is NOT rendered! This corresponds to the functionallity of &quot;.if&quot; of the stdWrap-function.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:2:\"XY\";s:8:\"datatype\";s:9:\"x,y +calc\";s:11:\"description\";s:22:\"Size of the gif-file. \";s:7:\"default\";s:6:\"100,20\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:6:\"format\";s:8:\"datatype\";s:33:\"&quot;gif&quot; / &quot;jpg&quot;\";s:11:\"description\";s:61:\"Output type. <br>&quot;jpg&quot;/&quot;jpeg&quot; = jpg-image\";s:7:\"default\";s:3:\"gif\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:12:\"reduceColors\";s:8:\"datatype\";s:14:\"posint (1-255)\";s:11:\"description\";s:41:\"Reduce the number of colors (if gif-file)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:21:\"transparentBackground\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:287:\"Set this flag to render the background transparent. TYPO3 makes the color found at position 0,0 of the image (upper left corner) transparent.<br>If you render text you should leave the niceText option OFF as the result with probably be more precise without the niceText antialiasing hack\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:16:\"transparentColor\";s:8:\"datatype\";s:31:\"&nbsp;<i>HTMLColor</i> /stdWrap\";s:11:\"description\";s:451:\"Specify a color that should be transparent<br>&nbsp;<b><br>Example-values:</b><br>#ffffcc <br>red <br>255,255,127<br>&nbsp;<b><br>Option:</b><br>transparentColor.closest = 1<br>This will allow for the closest color to be matched instead. You may need this if you image is not garanteed &quot;clean&quot;.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> You may experience that this doesn&#039;t work if you use reduceColors-option or render text with niceText-option.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:7:\"quality\";s:8:\"datatype\";s:15:\"posint (10-100)\";s:11:\"description\";s:51:\"JPG-quality (if &#x201c;.format&#x201d; = jpg/jpeg)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:9:\"backColor\";s:8:\"datatype\";s:25:\"GraphicColor <br>/stdWrap\";s:11:\"description\";s:28:\"Background color for the gif\";s:7:\"default\";s:5:\"white\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:6:\"offset\";s:8:\"datatype\";s:9:\"x,y +calc\";s:11:\"description\";s:30:\"Offset all objects on the gif.\";s:7:\"default\";s:3:\"0,0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:8:\"workArea\";s:8:\"datatype\";s:14:\"x,y,w,h + calc\";s:11:\"description\";s:253:\"Define the workarea on the giffile. All the GifBuilderObj&#039;s will see this as the dimensions of the gif-file regarding alignment, overlaying of images an so on. Only will TEXT-objects exceeding the boundaries of the workarea print outside this area.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:8:\"maxWidth\";s:8:\"datatype\";s:6:\"pixels\";s:11:\"description\";s:25:\"Maximal width of gif-file\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:9:\"maxHeight\";s:8:\"datatype\";s:6:\"pixels\";s:11:\"description\";s:26:\"Maximal heigth of gif-file\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (89,7389802,'39e45fa8cdd66301847431223c33177b','','tsref:->GIFBUILDER.(GBObj).TEXT','a:1:{s:4:\"rows\";a:22:{i:0;a:6:{s:8:\"property\";s:4:\"text\";s:8:\"datatype\";s:7:\"stdWrap\";s:11:\"description\";s:220:\"This is text text-string on the gif-file. The item is rendered only if this string is not empty.<br>The cObj-&gt;data-array is loaded with the page-record, if for example the GIFBUILDER-object is used by GMENU or IMGMENU\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:13:\"textMaxLength\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:115:\"The maximum length of the text. This is just a natural break that prevents incidental rendering of very long texts!\";s:7:\"default\";s:3:\"100\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:8:\"maxWidth\";s:8:\"datatype\";s:6:\"pixels\";s:11:\"description\";s:261:\"Sets the maximum width in pixels, the text must be. Reduces the fontSize if the text does not fit within this width.<br>&nbsp;<br>Does not support setting alternative fontSizes in splitRendering options.<br>&nbsp;<br>(By Rene Fritz &lt;r.fritz@colorcube.de&gt;)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:14:\"doNotStripHTML\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:104:\"If set, HTML-tags in the string inserted are NOT removed. Any other way HTML-code is removed by default!\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:8:\"fontSize\";s:8:\"datatype\";s:6:\"posint\";s:11:\"description\";s:9:\"Font size\";s:7:\"default\";s:2:\"12\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:9:\"fontColor\";s:8:\"datatype\";s:21:\"GraphicColor /stdWrap\";s:11:\"description\";s:10:\"Font color\";s:7:\"default\";s:5:\"black\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:8:\"fontFile\";s:8:\"datatype\";s:8:\"resource\";s:11:\"description\";s:42:\"Font face (truetype font you can upload!!)\";s:7:\"default\";s:20:\"Nimbus (Arial-clone)\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:5:\"angle\";s:8:\"datatype\";s:6:\"degree\";s:11:\"description\";s:116:\"Rotation degrees of the text. <br>&nbsp;<br>&nbsp;<b>NOTE:</b> Angle is not available if spacing/wordSpacing is set.\";s:7:\"default\";s:22:\"0<br>Range: -90 til 90\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:5:\"align\";s:8:\"datatype\";s:5:\"align\";s:11:\"description\";s:21:\"Alignment of the text\";s:7:\"default\";s:4:\"left\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:6:\"offset\";s:8:\"datatype\";s:9:\"x,y +calc\";s:11:\"description\";s:18:\"Offset of the text\";s:7:\"default\";s:3:\"0,0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:9:\"antiAlias\";s:8:\"datatype\";s:7:\"Boolean\";s:11:\"description\";s:151:\"FreeType antialiasing. Notice, the default mode is &quot;on&quot;!<br>&nbsp;<br>&nbsp;<b>Note:</b> This option is not available if .niceText is enabled\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:10:\"iterations\";s:8:\"datatype\";s:6:\"posint\";s:11:\"description\";s:191:\"How many times the text should be &quot;printed&quot; onto it self. This will add the effect of bold text.<br>&nbsp;<br>&nbsp;<b>Note:</b> This option is not available if .niceText is enabled\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:7:\"spacing\";s:8:\"datatype\";s:6:\"posint\";s:11:\"description\";s:53:\"Pixel-distance between letters. This may render ugly!\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:11:\"wordSpacing\";s:8:\"datatype\";s:6:\"posint\";s:11:\"description\";s:29:\"Pixel-distance between words.\";s:7:\"default\";s:24:\"= &quot;.spacing&quot;*2\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:4:\"hide\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:163:\"If this is true, the text is NOT printed. <br>This feature may be used if you need a shadow-object to base a shadow on the text, but do not want the text to print.\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:16:\"hideButCreateMap\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:230:\"If this option is set, the text will not be rendered. Shadows and emboss will, though, so don&#039;t apply these!! But this feature is also meant only to enable a text to generate the imageMap coordinates without rendering itself.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:6:\"emboss\";s:8:\"datatype\";s:24:\"GifBuilderObj-&gt;EMBOSS\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:6:\"shadow\";s:8:\"datatype\";s:24:\"GifBuilderObj-&gt;SHADOW\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:7:\"outline\";s:8:\"datatype\";s:25:\"GifBuilderObj-&gt;OUTLINE\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:19;a:6:{s:8:\"property\";s:6:\"imgMap\";s:8:\"datatype\";s:111:\"-&gt;IMGMAP<br>&nbsp;<br>-&gt;stdWrap properties for &quot;altText&quot; and &quot;titleText&quot; in this case\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:20;a:6:{s:8:\"property\";s:8:\"niceText\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:1004:\"This is a very popular feature that helps to render small letters much nicer than the freetype library can normally do. But it also loads the system very much!<br>The principle of this function is to create a black/white giffile in twice or more times the size of the actual gif-file and then print the text onto this is a scaled dimension. Afterwards ImageMagick (IM) scales down the mask and masks the font color down on the original gif-file through the temporary mask. <br>The fact that the font is actually rendered in the double size and scaled down adds a more homogenous shape to the lettes. Some fonts are more critical than others though. If you do not need the quality, then don&#039;t use the function.<br>&nbsp;<b><br>Some properties:</b><br>.before = IM-params before scale<br>.after = IM-params after scale<br>.sharpen = sharpen-value for the mask (after scaling), integer 0-99 (this enables you to make the text crisper if it&#039;s too blurred!)<br>.scaleFactor = scaling-factor, int 2-5\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:21;a:6:{s:8:\"property\";s:80:\"splitRendering.compX<br>splitRendering.compY<br>splitRendering.[array]<br>&nbsp;\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:2838:\"Split the rendering of a string into separate processes with individual configurations. By this method a certain range of characters can be rendered with another font face or size. This is very useful if you want to use separate fonts for strings where you have latin characters combined with eg. Japanese and there is a separate font file for each.<br>You can also render keywords in another font/size/color.<br>&nbsp;<b><br>Properties:</b><br>splitRendering.compX = <i>Additional pixelspace between parts, x direction</i><br>splitRendering.compY = <i>Additional pixelspace between parts, y direction</i><br>splitRendering.[array] = [charRange, highlightWord]<br>splitRendering.[array] {<br>&nbsp;&nbsp;&nbsp;fontFile = <i>Alternative font file for this rendering</i><br>&nbsp;&nbsp;&nbsp;fontSize = <i>Alternative font size for this rendering</i><br>&nbsp;&nbsp;&nbsp;color = <i>Alternative color for this rendering, works ONLY without &#x201c;niceText&#x201d;</i><br>&nbsp;&nbsp;&nbsp;xSpaceBefore = <i>x-Space before this part</i><br>&nbsp;&nbsp;&nbsp;xSpaceAfter = <i>x-Space after this part</i><br>&nbsp;&nbsp;&nbsp;ySpaceBefore =<i> y-Space before this part</i><br>&nbsp;&nbsp;&nbsp;ySpaceAfter = <br>}<br>&nbsp;<b><br>Keyword: charRange</b><br>splitRendering.[array].value = Commaseparated list of character ranges (eg. &#x201c;100-200&#x201d;) given as Unicode character numbers. The list accepts optional starting and ending points, eg. &#x201c; - 200&#x201d; or &#x201c; 200 -&#x201d; and single values, eg. &#x201c;65, 66, 67&#x201d;<br>&nbsp;<b><br>Keyword: highlightWord</b><br>splitRendering.[array].value = Word to highlight, makes a case sensitive search for this.<br>&nbsp;<b><br>Limitations:</b><br>The pixelcompensation values are not corrected for scale factor used with niceText. Basically this means that when niceText is used, these values will have only the half effect.<br>When word spacing is used the &#x201c;highlightWord&#x201d; mode doesn&#039;t work.<br>The color override works only without &#x201c;niceText&#x201d;.<br>&nbsp;<b><br>Example:</b><br>&nbsp;&nbsp;&nbsp;10.splitRendering.compX <b>=</b> 2<br>&nbsp;&nbsp;&nbsp;10.splitRendering.compY <b>=</b> -2<br>&nbsp;&nbsp;&nbsp;10.splitRendering.10 <b>=</b> charRange<br>&nbsp;&nbsp;&nbsp;10.splitRendering.10 <b>{</b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;value <b>=</b> 200-380 , 65, 66<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontSize <b>=</b> 50<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontFile <b>=</b> t3lib/fonts/nimbus.ttf<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;xSpaceBefore <b>=</b> 30<br>&nbsp;&nbsp;&nbsp;<b>}</b><br>&nbsp;&nbsp;&nbsp;10.splitRendering.20 <b>=</b> highlightWord<br>&nbsp;&nbsp;&nbsp;10.splitRendering.20 <b>{</b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;value <b>=</b> TheWord<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;color <b>=</b> red<br>&nbsp;&nbsp;&nbsp;<b>}</b>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (90,7389802,'5be8c3b8974e916b9d679e1daf164057','','tsref:->GIFBUILDER.(GBObj).SHADOW','a:1:{s:4:\"rows\";a:6:{i:0;a:6:{s:8:\"property\";s:10:\"textObjNum\";s:8:\"datatype\";s:7:\"pos-int\";s:11:\"description\";s:309:\"Must point to the TEXT-object if these shadow-properties are not properties to a TEXT-object directly (&quot;stand-alone-shadow&quot;). Then the shadow needs to know which TEXT-object it should be a shadow of!<br>If - on the other hand - the shadow is a property to a text-object, this property is not needed.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"offset\";s:8:\"datatype\";s:3:\"x,y\";s:11:\"description\";s:13:\"Shadow offset\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:5:\"color\";s:8:\"datatype\";s:12:\"GraphicColor\";s:11:\"description\";s:12:\"Shadow color\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:4:\"blur\";s:8:\"datatype\";s:13:\"posint (1-99)\";s:11:\"description\";s:389:\"Blurring of the shadow. Above 40 only values of 40,50,60,70,80,90 means something.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> Unfortunately the blurring capabilities of ImageMagick is not very mature in the version 4.2.9. This is addressed in the later version 5.2.0 where a gaussian blur-function is added. BUT as we do cannot use the latest ImageMagick development yet, this is not utilized so far.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:7:\"opacity\";s:8:\"datatype\";s:14:\"posint (1-100)\";s:11:\"description\";s:97:\"Opacity (transparency^-1) <br>100% opacity = 0% transparency). Only active with a value for blur.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:9:\"intensity\";s:8:\"datatype\";s:13:\"posint(0-100)\";s:11:\"description\";s:148:\"How &quot;massive&quot; the shadow is. This value can - if it has a high value combined with a blurred shadow - create a kind of soft-edged outline.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (91,7389802,'effa3a1bd581c80efddf4f9370a21358','','tsref:->GIFBUILDER.(GBObj).EMBOSS','a:1:{s:4:\"rows\";a:7:{i:0;a:6:{s:8:\"property\";s:10:\"textObjNum\";s:8:\"datatype\";s:7:\"pos-int\";s:11:\"description\";s:309:\"Must point to the TEXT-object if these shadow-properties are not properties to a TEXT-object directly (&quot;stand-alone-shadow&quot;). Then the shadow needs to know which TEXT-object it should be a shadow of!<br>If - on the other hand - the shadow is a property to a text-object, this property is not needed.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"offset\";s:8:\"datatype\";s:3:\"x,y\";s:11:\"description\";s:20:\"Offset of the emboss\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:9:\"highColor\";s:8:\"datatype\";s:12:\"GraphicColor\";s:11:\"description\";s:18:\"Upper border-color\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:8:\"lowColor\";s:8:\"datatype\";s:12:\"GraphicColor\";s:11:\"description\";s:18:\"lower border-color\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:4:\"blur\";s:8:\"datatype\";s:13:\"posint (1-99)\";s:11:\"description\";s:82:\"Blurring of the shadow. Above 40 only values of 40,50,60,70,80,90 means something.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:7:\"opacity\";s:8:\"datatype\";s:14:\"posint (1-100)\";s:11:\"description\";s:97:\"Opacity (transparency^-1) <br>100% opacity = 0% transparency). Only active with a value for blur.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:9:\"intensity\";s:8:\"datatype\";s:13:\"posint(0-100)\";s:11:\"description\";s:148:\"How &quot;massive&quot; the emboss is. This value can - if it has a high value combined with a blurred shadow - create a kind of soft-edged outline.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (92,7389802,'51a763b66d4511f3e4e5216bc300f7db','','tsref:->GIFBUILDER.(GBObj).OUTLINE','a:1:{s:4:\"rows\";a:3:{i:0;a:6:{s:8:\"property\";s:10:\"textObjNum\";s:8:\"datatype\";s:7:\"pos-int\";s:11:\"description\";s:309:\"Must point to the TEXT-object if these shadow-properties are not properties to a TEXT-object directly (&quot;stand-alone-shadow&quot;). Then the shadow needs to know which TEXT-object it should be a shadow of!<br>If - on the other hand - the shadow is a property to a text-object, this property is not needed.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:9:\"thickness\";s:8:\"datatype\";s:3:\"x,y\";s:11:\"description\";s:38:\"Thickness in each direction, range 1-2\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:5:\"color\";s:8:\"datatype\";s:12:\"GraphicColor\";s:11:\"description\";s:13:\"Outline color\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (93,7389802,'8ff83c2335d4b0a2170dbe5180c449a0','','tsref:->GIFBUILDER.(GBObj).BOX','a:1:{s:4:\"rows\";a:3:{i:0;a:6:{s:8:\"property\";s:10:\"dimensions\";s:8:\"datatype\";s:13:\"x,y,w,h +calc\";s:11:\"description\";s:164:\"Dimensions of a filled box. <br>x,y &nbsp;&nbsp;&nbsp;is the offset. <br>w,h &nbsp;&nbsp;&nbsp;is the dimensions. Dimensions of 1 will result in 1-pixel wide lines!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:5:\"color\";s:8:\"datatype\";s:12:\"GraphicColor\";s:11:\"description\";s:10:\"fill-color\";s:7:\"default\";s:5:\"black\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:5:\"align\";s:8:\"datatype\";s:7:\"VHalign\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (94,7389802,'2cb1f22d527579954a79b8a61bde9821','','tsref:->GIFBUILDER.(GBObj).IMAGE','a:1:{s:4:\"rows\";a:5:{i:0;a:6:{s:8:\"property\";s:4:\"file\";s:8:\"datatype\";s:11:\"imgResource\";s:11:\"description\";s:13:\"The imagefile\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"offset\";s:8:\"datatype\";s:9:\"x,y +calc\";s:11:\"description\";s:7:\"Offset \";s:7:\"default\";s:3:\"0,0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:4:\"tile\";s:8:\"datatype\";s:3:\"x,y\";s:11:\"description\";s:93:\"tile x,y times. <br>Maximum times is 20 each direction. If you need more, use a larger image.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:5:\"align\";s:8:\"datatype\";s:7:\"VHalign\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:4:\"mask\";s:8:\"datatype\";s:11:\"imgResource\";s:11:\"description\";s:38:\"Optional mask-image for the imagefile.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
