INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (100,7389802,'c647a6f7686f2a20dfc6fcea63b02c18','','tsref:->IMGMAP','a:1:{s:4:\"rows\";a:6:{i:0;a:6:{s:8:\"property\";s:3:\"url\";s:8:\"datatype\";s:3:\"url\";s:11:\"description\";s:11:\"url to link\";s:7:\"default\";s:47:\"For IMGMENU menu objects provided automatically\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"target\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:15:\"target for link\";s:7:\"default\";s:47:\"For IMGMENU menu objects provided automatically\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:6:\"noBlur\";s:8:\"datatype\";s:7:\"Boolean\";s:11:\"description\";s:192:\"Normally graphical links are &quot;blurred&quot; if the browser is MSIE. This removes the ugly box around a link. <br>If this property is set, the link is NOT blurred with &quot;onFocus&quot;.\";s:7:\"default\";s:47:\"For IMGMENU menu objects provided automatically\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:7:\"explode\";s:8:\"datatype\";s:3:\"x,y\";s:11:\"description\";s:146:\"This &quot;explodes&quot; or &quot;implodes&quot; the image-map. Useful to let the hot area cover a little more than just the letters of the text.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:7:\"altText\";s:8:\"datatype\";s:6:\"atring\";s:11:\"description\";s:121:\"Value of the alt-attribute.<br>&nbsp;<br>(Used from TEXT Gifbuilding objects, this has stdWrap properties. Otherwise not)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:9:\"titleText\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:123:\"Value of the title attribute.<br>&nbsp;<br>(Used from TEXT Gifbuilding objects, this has stdWrap properties. Otherwise not)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (101,7389802,'5c576ef57f313263731885ee7eb93387','','tsref:(cObject).HMENU.(mObj)','a:1:{s:4:\"rows\";a:14:{i:0;a:6:{s:8:\"property\";s:12:\"sectionIndex\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:11:\"(see below)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:23:\"alternativeSortingField\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:517:\"Normally the menuitems are sorted by the fields &quot;sorting&quot; in the pages- and tt_content-table. Here you can enter a list of fields that is used in the SQL- &quot;ORDER BY&quot; statement instead.<br>&nbsp;<b><br>Examples (for &quot;pages&quot; table):</b><br>alternativeSortingField = title desc<br>(This will render the menu in reversed alphabetical order)<br>&nbsp;<br>&nbsp;<b>LIMITATIONS:</b> <br>This property works with normal menus, sectionsIndex menus and special-menus of type &quot;directory&quot;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:8:\"minItems\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:212:\"The minimum items in the menu. If the number of pages does not reach this level, a dummy-page with the title &quot;...&quot; and uid=[currentpage_id] is inserted.<br>&nbsp;<br>Takes precedence over HMENU.minItems\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:8:\"maxItems\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:108:\"The maximum items in the menu. More items will be ignored.<br>&nbsp;<br>Takes precedence over HMENU.maxItems\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:5:\"begin\";s:8:\"datatype\";s:9:\"int +calc\";s:11:\"description\";s:230:\"The first item in the menu. <br>&nbsp;<b><br>Example:</b><br>This results in a menu, where the first two items are skipped starting with item number 3:<br>&nbsp;&nbsp;&nbsp;begin = 3 <br>&nbsp;<br>Takes precedence over HMENU.begin\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:8:\"JSWindow\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:441:\"If set, the links of the menu-items will open by JavaScript in a pop-up window. <br>&nbsp;<b><br>.newWindow boolean, that lets every menuitem open in its own window opposite to opening in the same window for each click.</b><br>&nbsp;<br>&nbsp;<b>.params</b> is the list of parameters sent to the JavaScript open-window function, eg: <br>width=200,height=300,status=0,menubar=0<br>&nbsp;<br>&nbsp;<b>NOTE:</b> Does not work with JSMENU&#039;s\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:13:\"imgNamePrefix\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:76:\"prefix for the imagenames. This prefix is appended with the uid of the page.\";s:7:\"default\";s:15:\"&quot;img&quot;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:16:\"imgNameNotRandom\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:373:\"If set, the image names of menuitems is not randomly assigned. Usefull switch if you&#039;re manipulating these images with some external JavaScript<br>&nbsp;<br>&nbsp;<b>NOTE:</b> Don&#039;t set this if you&#039;re working with a menu with sectionIndex! In that case you need special unique names of items based on something else than the uid of the parent page of course!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:13:\"debugItemConf\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:160:\"Outputs (by the debug()-function) the configuration arrays for each menuitem. Useful to debug optionSplit things and such...<br>Applies to GMENU, TMENU, IMGMENU\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:10:\"overrideId\";s:8:\"datatype\";s:17:\"integer (page-id)\";s:11:\"description\";s:393:\"If set, then all links in the menu will point to this pageid. Instead the real uid of the page is sent by the parameter &quot;&amp;real_uid=[uid]&quot;.<br>This feature is smart, if you have inserted a menu from somewhere else, perhaps a shared menu, but wants the menuitems to call the same page, which then generates a proper output based on the real_uid.<br>Applies to GMENU, TMENU, IMGMENU\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:9:\"addParams\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:174:\"Additional parameter for the menu-links. <br>&nbsp;<b><br>Example:</b><br>&quot;&amp;some_var=some%20value&quot;<br>Must be rawurlencoded.<br>Applies to GMENU, TMENU, IMGMENU\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:25:\"showAccessRestrictedPages\";s:8:\"datatype\";s:48:\"integer (page id) / keyword &#x201c;NONE&#x201d;\";s:11:\"description\";s:1182:\"If set, pages in the menu will include pages with frontend user group access enabled. However the page is of course not accessible and therefore the URL in the menu will be linked to the page with the ID of this value. On that page you could put a login form or other message.<br>If the value is &#x201c;NONE&#x201d; the link will not be changed and the site will perform page-not-found handling when clicked (which can be used to capture the event and act accordingly of course).<br>&nbsp;<b><br>Properties:</b><br>.addParam = Additional parameter for the URL, which can hold two markers; ###RETURN_URL### which will be substituted with the link the page would have had if it had been accessible and ###PAGE_ID### holding the page id of the page coming from (could be used to look up which fe_groups was required for access.<br>&nbsp;<b><br>Example:</b><br>showAccessRestrictedPages = 22<br>showAccessRestrictedPages.addParams = &amp;return_url=###RETURN_URL###&amp;pageId=###PAGE_ID###<br>&nbsp;<br>The example will link access restricted menu items to page id 22 with the return URL in the GET var &#x201c;return_url&#x201d; and the page id in the GET var &#x201c;pageId&#x201d;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:17:\"itemArrayProcFunc\";s:8:\"datatype\";s:13:\"function-name\";s:11:\"description\";s:690:\"The first variable passed to this function is the &#x201c;menuArr&#x201d; array with the menuitems as they are collected based on the type of menu.<br>You&#039;re free to manipulate or add to this array as you like. Just remember to return the array again!<br>&nbsp;<b><br>Note:</b><br>.parentObj property is hardcoded to be a reference to the calling tslib_menu object. Here you&#039;ll find eg. -&gt;id to be the uid of the menu item generating a submenu and such.<br>&nbsp;<b><br>Presetting element state</b><br>You can override element states like SPC, IFSUB, ACT, CUR or USR by setting the key ITEM_STATE in the page records. See cObject HMENU/special=userdefined for more information.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:18:\"submenuObjSuffixes\";s:8:\"datatype\";s:22:\"string<br>+optionSplit\";s:11:\"description\";s:1805:\"Defines a suffix for alternative sub-level menu objects. Useful to create special submenus depending on their parent menu element. See example below.<br>&nbsp;<b><br>Example:</b><br>This example will generate a menu where the menu objects for the second level will differ depending on the number of the first level item for which the submenu is rendered. The second level objects used are &#x201c;2&#x201d; (the default), &#x201c;2a&#x201d; and &#x201c;2b&#x201d; (the alternatives). Which of them is used is defined by &#x201c;1.submenuObjSuffixes&#x201d; which has the configuration &#x201c;a |*| |*| b&#x201d;. This configuration means that the first menu element will use configuration &#x201c;2a&#x201d; and the last will use &#x201c;2b&#x201d; while anything in between will use &#x201c;2&#x201d; (no suffix applied)<br>&nbsp;<br>page.200 = HMENU<br>page.200 {<br>&nbsp;&nbsp;&nbsp;1 = TMENU<br>&nbsp;&nbsp;&nbsp;1.wrap = &lt;div style=&quot;width:200px; border: 1px solid;&quot;&gt;|&lt;/div&gt;<br>&nbsp;&nbsp;&nbsp;1.expAll = 1<br>&nbsp;&nbsp;&nbsp;1.submenuObjSuffixes = a |*| |*| b<br>&nbsp;&nbsp;&nbsp;1.NO.allWrap = &lt;b&gt;|&lt;/b&gt;&lt;br/&gt;<br>&nbsp;<br>&nbsp;&nbsp;&nbsp;2 = TMENU<br>&nbsp;&nbsp;&nbsp;2.NO.allWrap = &lt;div style=&quot;background:red;&quot;&gt;|&lt;/div&gt;<br>&nbsp;<br>&nbsp;&nbsp;&nbsp;2a = TMENU<br>&nbsp;&nbsp;&nbsp;2a.NO.allWrap = &lt;div style=&quot;background:yellow;&quot;&gt;|&lt;/div&gt;<br>&nbsp;<br>&nbsp;&nbsp;&nbsp;2b = TMENU<br>&nbsp;&nbsp;&nbsp;2b.NO.allWrap = &lt;div style=&quot;background:green;&quot;&gt;|&lt;/div&gt;<br>}<br>&nbsp;<br>The result can be seen in the image below (applied on the testsite package):<br>&nbsp;<br>&nbsp;<br>&nbsp;<br>Applies to GMENU, TMENU, GMENU_LAYERS, TMENU_LAYERS and GMENU_FOLDOUT on &gt;= 2nd level in a menu.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (102,7389802,'2c8d1fb6db707d441cd5769786710a19','','tsref:(cObject).HMENU.(mObj_itemStates)','a:1:{s:4:\"rows\";a:10:{i:0;a:6:{s:8:\"property\";s:2:\"NO\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:524:\"The default &#x201c;Normal&#x201d; state rendering of Item. This is required for all menus.<br>If you specify properties for the &#x201c;NO&#x201d; property you do not have to set it &#x201c;1&#x201d;. Otherwise with no properties setting &#x201c;NO=1&#x201d; will render the menu anyways (for TMENU this may make sense).<br>&nbsp;<br>The simplest menu TYPO3 can generate is then:<br>&nbsp;<br>page.20 = HMENU<br>page.20.1 = TMENU<br>page.20.1.NO = 1<br>&nbsp;<br>That will be pure &lt;a&gt; tags wrapped around page titles.\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:16:\"IFSUB<br>IFSUBRO\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:54:\"Enable/Configuration for menu items which has subpages\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:12:\"ACT<br>ACTRO\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:67:\"Enable/Configuration for menu items which are found in the rootLine\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:22:\"ACTIFSUB<br>ACTIFSUBRO\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:84:\"Enable/Configuration for menu items which are found in the rootLine and has subpages\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:12:\"CUR<br>CURRO\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:69:\"Enable/Configuration for a menu item if the item is the current page.\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:22:\"CURIFSUB<br>CURIFSUBRO\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:86:\"Enable/Configuration for a menu item if the item is the current page and has subpages.\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:12:\"USR<br>USRRO\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:96:\"Enable/Configuration for menu items which are access restricted pages that a user has access to.\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:3:\"SPC\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:295:\"Enable/Configuration for &#039;Spacer&#039; pages.<br>Spacers are pages of the doktype &quot;Spacer&quot;. These are not viewable pages but &quot;placeholders&quot; which can be used to divide menuitems.<br>&nbsp;<br>&nbsp;<b>Note:</b> Rollovers doesn&#039;t work with spacers, if you use GMENU!\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:22:\"USERDEF1<br>USERDEF1RO\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:280:\"Userdefined, see .itemArrayProcFunc for details on how to use this.<br>You can set the ITEM_STATE values USERDEF1 and USERDEF2 (+...RO) from a script/userfunction processing the menu item array. See HMENU/special=userdefined or the property .itemArrayProcFunc of the menu objects.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:22:\"USERDEF2<br>USERDEF2RO\";s:8:\"datatype\";s:18:\"Boolean / (config)\";s:11:\"description\";s:11:\"(See above)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (103,7389802,'397a0804fc8ada0e121f0a17e8bf8dd4','','tsref:(cObject).HMENU.(mObj).GMENU','a:1:{s:4:\"rows\";a:21:{i:0;a:6:{s:8:\"property\";s:2:\"RO\";s:8:\"datatype\";s:7:\"Boolean\";s:11:\"description\";s:159:\"RollOver configuration enabled / disabled. <br>If this is true, RO becomes a GIFBUILDER-object defining the layout of the menuitem when the mouse rolls over it\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:6:\"expAll\";s:8:\"datatype\";s:7:\"Boolean\";s:11:\"description\";s:252:\"If this is true, the menu will always show the menu on the level underneath the menuitem. This corresponds to a situation where a user has clicked a menuitem and the menu folds out the next level. This can enable that to happen on all items as default.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:8:\"collapse\";s:8:\"datatype\";s:7:\"Boolean\";s:11:\"description\";s:116:\"If set, &quot;active&quot; menuitems that has expanded the next level on the menu will now collapse that menu again.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:9:\"accessKey\";s:8:\"datatype\";s:7:\"Boolean\";s:11:\"description\";s:44:\"If set access-keys are set on the menu-links\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:6:\"noBlur\";s:8:\"datatype\";s:7:\"Boolean\";s:11:\"description\";s:222:\"Normally graphical links are &quot;blurred&quot; if the browser is MSIE. Blurring removes the ugly box around a clicked link. <br>If this property is set, the link is NOT blurred (browser-default) with &quot;onFocus&quot;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:6:\"target\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:23:\"Target of the menulinks\";s:7:\"default\";s:4:\"self\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:14:\"forceTypeValue\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:151:\"If set, the &amp;type parameter of the link is forced to this value regardless of target. Overrides the global equivalent in &#039;config&#039; if set.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:328:\"Wraps the whole item using stdWrap<br>&nbsp;<b><br>Example:</b><br>2 = TMENU<br>2 {<br>&nbsp;&nbsp;&nbsp;stdWrap.dataWrap = &lt;ul class=&#x201d;{register :<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;parentProperty}&#x201d;&gt; | &lt;/ul&gt;<br>&nbsp;&nbsp;&nbsp;NO {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;...<br>&nbsp;&nbsp;&nbsp;}<br>}\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:43:\"Wraps only if there were items in the menu!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:11:\"applyTotalH\";s:8:\"datatype\";s:20:\"objNumsList (offset)\";s:11:\"description\";s:498:\"This adds the total height of the previously generated menuitems to the offset of the GifBuilderObj&#039;s mentioned in this list.<br>&nbsp;<b><br>Example:</b><br>This is useful it you want to create a menu with individual items but a common background image that extends to the whole area behind the menu. Then you should setup the background image in each GIFBUILDER-object and include the object-number in this list. <br>Look at the implementation in static_template &quot;styles.gmenu.bug&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:11:\"applyTotalW\";s:8:\"datatype\";s:20:\"objNumsList (offset)\";s:11:\"description\";s:129:\"This adds the total width of the previously generated menuitems to the offset of the GifBuilderObj&#039;s mentioned in this list.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:3:\"min\";s:8:\"datatype\";s:13:\"x,y (calcInt)\";s:11:\"description\";s:54:\"Forces the menu as a whole to these minimum dimensions\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:3:\"max\";s:8:\"datatype\";s:13:\"x,y (calcInt)\";s:11:\"description\";s:54:\"Forces the menu as a whole to these maximum dimensions\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:15:\"useLargestItemX\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:81:\"If set, then the width of all menuitems will be equal to the largest of them all.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:15:\"useLargestItemY\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:82:\"If set, then the height of all menuitems will be equal to the largest of them all.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:11:\"distributeX\";s:8:\"datatype\";s:4:\"int+\";s:11:\"description\";s:219:\"If set, the total width of all the menuitems will be equal to this number of pixels by adding/subtracting an equal amount of pixels to each menu items width.<br>Will overrule any setting for &quot;.useLargestItemX&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:11:\"distributeY\";s:8:\"datatype\";s:4:\"int+\";s:11:\"description\";s:221:\"If set, the total height of all the menuitems will be equal to this number of pixels by adding/subtracting an equal amount of pixels to each menu items height.<br>Will overrule any setting for &quot;.useLargestItemY&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:20:\"removeObjectsOfDummy\";s:8:\"datatype\";s:11:\"objNumsList\";s:11:\"description\";s:259:\"If the menu is forced to a certain minimum dimension, this is a list of objects in the gifbuilder-object that is removed for this last item. This is important to do if the menuitems has elements that should only be applied if the item is actually a menuitem!!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:14:\"disableAltText\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:116:\"If set, the alt-parameter of the images are not set. You can do it manually by &#x201c;imgParams&#x201d; (see below)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:19;a:6:{s:8:\"property\";s:9:\"IProcFunc\";s:8:\"datatype\";s:13:\"function-name\";s:11:\"description\";s:339:\"The internal array &#x201c;I&#x201d; is passed to this function and expected returned as well. Subsequent to this function call the menu item is compiled by implode()&#039;ing the array $I[parts] in the passed array. Thus you may modify this if you need to.<br>See example on the testsite and in media/scripts/example_itemArrayProcFunc.php\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:20;a:6:{s:8:\"property\";s:73:\"[Common Item States, see above]<br>+ rollover version for all, except SPC\";s:8:\"datatype\";s:59:\"-&gt;GIFBUILDER<br>+ Additional properties! See table below\";s:11:\"description\";s:572:\"This is the GIFBUILDER-options for each category of menuitem that can be generated.<br>&nbsp;<br>&nbsp;<b>NOTE:</b> For the GMENU series you can also define the RollOver configuration for the item states. This means that you define the GIFBUILDER object for the &#039;Active&#039; state by ACT and the RollOver GIFBUILDER object for the &#039;Active&#039; state by ACTRO.<br>This pattern goes for ALL the states except the SPC state.<br>&nbsp;<b><br>SPECIAL:</b><br>The -&gt;OptionSplit function is run on the whole GIFBUILDER-configuration before the items are generated.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (104,7389802,'1bbf6acf6082debc2080c0087b00e44b','','tsref:(cObject).HMENU.(mObj).GMENU.(itemState)','a:1:{s:4:\"rows\";a:11:{i:0;a:6:{s:8:\"property\";s:6:\"noLink\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:31:\"If set, the item is NOT linked!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:9:\"imgParams\";s:8:\"datatype\";s:6:\"params\";s:11:\"description\";s:34:\"Parameters for the &lt;img&gt;-tag\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:9:\"altTarget\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:67:\"Alternative target which overrides the target defined for the GMENU\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:14:\"altImgResource\";s:8:\"datatype\";s:10:\"imgResouce\";s:11:\"description\";s:109:\"Defines an alternative image to use. If an image returns here, it will override any GIFBUILDER configuration.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:10:\"ATagParams\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:21:\"Additional parameters\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:9:\"ATagTitle\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:68:\"which defines the title attribute of the a-tag. (See TMENUITEM also)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:16:\"additionalParams\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:175:\"Define parameters that are added to the end of the URL. This must be code ready to insert after the last parameter.<br>&nbsp;<br>For details, see typolink-&gt;additionalParams\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:21:\"Wrap of the menu item\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:7:\"allWrap\";s:8:\"datatype\";s:13:\"wrap /stdWrap\";s:11:\"description\";s:20:\"Wraps the whole item\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:16:\"subst_elementUid\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:66:\"If set, &quot;{elementUid}&quot; is substituted with the item uid.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:10:\"allStdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:25:\"stdWrap of the whole item\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (105,7389802,'de8dd1a479d93f1a2b3b6339f8b3bcc6','','tsref:(cObject).HMENU.(mObj).GMENU_LAYERS, (cObject).HMENU.(mObj).TMENU_LAYERS','a:1:{s:4:\"rows\";a:23:{i:0;a:6:{s:8:\"property\";s:10:\"layerStyle\";s:8:\"datatype\";s:22:\"&lt;DIV&gt;-tag params\";s:11:\"description\";s:176:\"Parameters for the &lt;DIV&gt;-layer-tags in the HTML-document. You might probably not need change this.<br>&nbsp;<b><br>Example:</b><br>position:absolute; VISIBILITY: hidden; \";s:7:\"default\";s:39:\"position:absolute; visibility: hidden; \";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:12:\"lockPosition\";s:8:\"datatype\";s:44:\"&quot;x&quot; / &quot;y&quot; / &quot;&quot;\";s:11:\"description\";s:438:\"If this is set to &quot;x&quot; or &quot;y&quot; the menu on the layers is locked and does not follow the mouse-cursor (which it does if this is not set).<br>&quot;x&quot; or &quot;y&quot; defines respectively that the summed width (x) or height (y) is added to the x or y offset of the menu. That means that you should set this value to &quot;x&quot; if you have a horizontal GMENU_LAYERS and to &quot;y&quot; if you have a verical menu.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:15:\"dontFollowMouse\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:422:\"If set and lockPosition is blank (so that the menu layer follows the mouse) then the menu will NOT follow the mouse but still it will appear where the mouse cursor hit the trigger-element. Usefull if you don&#039;t know the exact positions of elements. <br>&nbsp;<br>&nbsp;<b>Warning:</b> You should not set displayActiveOnLoad for menus with this feature enabled (because the absolute position of the layer is not known).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:19:\"lockPosition_adjust\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:153:\"A number which is added to the width/height of the menuitems in order to compensate for eg. hspace or other things between the images in the GMENU_LAYERS\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:20:\"lockPosition_addSelf\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:308:\"Normally the width and height of the items (+lockPosition_adjust) are summed up after the item has been rendered. This is good if the direction of the menulayers is right- og downwards.<br>But if you use directionLeft/directionUp, you might want to add the width of the items before.<br>If so, set this flag.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:10:\"xPosOffset\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:154:\"The offset of the menu from the point where it&#039;s &quot;activated&quot; (if lockPosition is false) / from topleft page corner (if lockPosition is set)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:10:\"yPosOffset\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:34:\"As above, but for the y-dimension.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:9:\"topOffset\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:210:\"The offset of menuitems from top of browser. Should be set rather than defining it in the .layerStyle property. Must be set in order to use directionUp.<br>Used with either lockPosition=x or xPosOffset defined.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:10:\"leftOffset\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:220:\"The offset of menuitems from left border of browser. Should be set rather than defining it in the .layerStyle property. Must be set in order to use directionLeft.<br>Used with either lockPosition=y or yPosOffset defined.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:15:\"blankStrEqFalse\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:308:\"If set, then the properties topOffset,leftOffset, xPosOffset, yPosOffset are considered &#x201c;blank&#x201d; if they are really blank strings - not just &#x201c;zero&#x201d;. You should enable this if you wish to be able to work with zero offsets. This is typically the case if you use relative positioning.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:13:\"directionLeft\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:292:\"Set this, if you want the items to be right-aligned (pop&#039;s out towards the left).<br>Does not work with Opera at this time because I don&#039;t know how to make Opera read the width of each layer.<br>If you set the width of the menu-layers in .layerStyles this might work no matter what.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:11:\"directionUp\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:99:\"Set this, if you want the items to be bottom-aligned (pop&#039;s out upwards instead of downwards).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:13:\"setFixedWidth\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:926:\"For GMENU_LAYERS the width and heights of the element is normally known from the graphical item. For TMENU_LAYERS this cannot be known in the same way. Therefore you can use .setFixedWidth and .setFixedHeight to set these values to a number you find reasonable. Of course this may be blasted by the browsers rendering if the font gets out of proportions etc.<br>Alternatively you may want to use the property &#x201c;relativeToTriggerItem&#x201d; which will position your menu layers relative to the item you roll over. This has some drawbacks though. A middle solution is to use a menu with lockPosition set to blank and dontFollowMouse set to true. Then you need only specify either an x or y coordinate to follow and the item will appear where the mouse hits the element.<br>&nbsp;<b>Notice:</b> Active if value is NOT a blank str. Setting this value to zero means that no width is calculated for the items in GMENU_LAYERS.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:14:\"setFixedHeight\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:56:\"See &#x201c;setFixedWidth&#x201d;. Same, but for height.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:13:\"bordersWithin\";s:8:\"datatype\";s:11:\"l,t,r,b,l,t\";s:11:\"description\";s:172:\"Keep borders of the layer within these limits in pixels. Zero is &#039;not set&#039;<br>(Syntax: List of integers, evaluated clockwise: Left, Top, Right, Bottom, Left, Top)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:19:\"displayActiveOnLoad\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:605:\"If set, the submenu-layer of the active menuitem is opened at page-load. If .freezeMouseover is also set and there is RO defined for the main menu items, the menuitem belonging to the displayed submenu is also shown.<br>&nbsp;<b><br>Properties:</b><br>.onlyOnLoad (boolean)<br>If set, then the display of the active item will happen only when the page is loaded. The display will not be restored on mouseout of other items.<br>&nbsp;<b><br>Warning: If you are cascading GMENU_LAYER objects make sure that all elements before this element (for which you set this attribute) also has this attribute set!</b>\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:15:\"freezeMouseover\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:297:\"If set, any mouseout effect of main menuitems is removed not on roll-out but when another element is rolled over (or the layer is hidden/default layer restored)<br>&nbsp;<b><br>Properties:</b><br>.alwaysKeep (boolean)<br>If set, the freezed element will always stay, even if the submenu is hidden.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:19:\"hideMenuWhenNotOver\";s:8:\"datatype\";s:4:\"int+\";s:11:\"description\";s:267:\"If set (&gt; 1) then the menu will hide it self whenever a user moves the cursor away from the menu. The value of this parameter determines the width (pixels) of the zone around the element until the mousepointer is considered to be far enough away to hide the layer.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:13:\"hideMenuTimer\";s:8:\"datatype\";s:4:\"int+\";s:11:\"description\";s:106:\"This is the number of milliseconds to wait before the submenu will disappear if hideMenuWhenNotOver is set\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:19;a:6:{s:8:\"property\";s:17:\"dontHideOnMouseUp\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:140:\"If set, the menu will not hide it&#039;s layers when the mouse botton is clicked. Usefull if your menuitems loads the pages in another frame\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:20;a:6:{s:8:\"property\";s:13:\"layer_menu_id\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:209:\"If you want to specifically name a menu on a page. Probably you don&#039;t need that!<br>&nbsp;<br>&nbsp;<b>Warning:</b> Don&#039;t use underscore and special characters in this string. Stick to alpha-numeric.\";s:7:\"default\";s:26:\"[random 6 char hashstring]\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:21;a:6:{s:8:\"property\";s:21:\"relativeToTriggerItem\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:1246:\"This allows you to position the menu layers relative to the item that triggers it. However you should be aware of the following facts:<br>This does not work with Netscape 4 - the position of the trigger layer will be calculated to zero and thus the offset for all menu layers will be 0,0 + your values. <br>This feature will wrap the menu item in some &lt;div&gt;-tags right before the whole item is wrapped by the .wrap code (for GMENU_LAYERS) or .allWrap (for TMENU_LAYERS). The bottom line of this is: 1) If your menu is horizontal, always wrap your menu items in a table so linebreaks does not appear because of the &lt;div&gt;-tags and 2) make sure the wrapping of the table cell is done with the .wrap/.allWrap properties respectively.<br>Works only effectively on the first xMENU_LAYER in a cascade. For succeeding xMENU_LAYERS items please use &#x201c;relativeToParentLayer&#x201d;.<i><br>If set, properties xPosOffset, yPosOffset and lockPosition* are not functional (properties directionLeft, directionUp, topOffset and leftOffset are still active)</i><i><br>&nbsp;</i><b><br>Additional Properties:</b><br>&nbsp;<b>.addWidth</b> = Adds the width of the trigger element<br>&nbsp;<b>.addHeight</b> = Adds the height of the trigger element\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:22;a:6:{s:8:\"property\";s:21:\"relativeToParentLayer\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:556:\"If set, then the layer will be positioned relative to the previous layer (parent) in a cascaded series of xMENU_LAYERS. Basically the relative position of the parent layer is just added to the offset of the current menu. <br>&nbsp;<br>&nbsp;<b>Warning:</b> This property makes sense only if there really is a previous GMENU_LAYER to get position from! So you must have a cascaded menu!<br>&nbsp;<b><br>Additional Properties:</b><br>&nbsp;<b>.addWidth</b> = Adds the width of the parent layer<br>&nbsp;<b>.addHeight</b> = Adds the height of the parent layer\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (106,7389802,'cc7479b256282161af55c3137ffe98ec','','tsref:(cObject).HMENU.(mObj).GMENU_FOLDOUT','a:1:{s:4:\"rows\";a:17:{i:0;a:6:{s:8:\"property\";s:17:\"dontLinkIfSubmenu\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:110:\"If set, items that has a submenu is not linked. Items without a submenu are always linked in the regular ways.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:9:\"foldTimer\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:53:\"The timeout in the animation, these are milliseconds.\";s:7:\"default\";s:2:\"40\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:9:\"foldSpeed\";s:8:\"datatype\";s:16:\"int, range 1-100\";s:11:\"description\";s:58:\"How many steps in an animation? Choose 1 for no animation.\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:10:\"stayFolded\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:49:\"Stay open when you click a new toplink? (Level 1)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:12:\"bottomHeight\";s:8:\"datatype\";s:11:\"int, pixels\";s:11:\"description\";s:147:\"Sets the height of the bottom layer. Is important if the bottomlayer contains either content or a background color: Else the layer will be clipped.\";s:7:\"default\";s:3:\"100\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:9:\"menuWidth\";s:8:\"datatype\";s:11:\"int, pixels\";s:11:\"description\";s:174:\"Width of the whole menu main layer. Important to set, especially for the bottomlayer as it is clipped by this value. Always try to set this to the width in pixels of the menu\";s:7:\"default\";s:3:\"170\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:10:\"menuHeight\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:60:\"Height of the whole menulayer. Seems to be not so important.\";s:7:\"default\";s:3:\"400\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:13:\"subMenuOffset\";s:8:\"datatype\";s:3:\"x,y\";s:11:\"description\";s:159:\"Offset of the submenu for each menuitem. This is important because if you don&#039;t set this value the items will appear ontop of their &#x201c;parent&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:10:\"menuOffset\";s:8:\"datatype\";s:3:\"x,y\";s:11:\"description\";s:64:\"Offset of the menu main layer on the page. From upperleft corner\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:13:\"menuBackColor\";s:8:\"datatype\";s:10:\"HTML-color\";s:11:\"description\";s:158:\"Background color behind menu. If not set, transparent (which will not work very well in case .foldSpeed is set to something else than 1. But see for yourself)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:15:\"dontWrapInTable\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:482:\"By default every menuitem on the first level is wrapped in a table:<br>&lt;TABLE cellSpacing=0 cellPadding=0 width=&quot;100%&quot; border=0&gt;&lt;TR&gt;&lt;TD&gt;<br>[menu item HTML here..]<br>&lt;/TD&gt;&lt;/TR&gt;&lt;/TABLE&gt;<br>Doing this ensures that the layers renders equally in the supported browsers. However you might need to disable that which is what you can do by setting this flag.<br>&nbsp;<b>Note:</b> Using &lt;TBODY&gt; in this tables seems to break Netscape 4+\";s:7:\"default\";s:1:\"0\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:13:\"bottomContent\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:62:\"Content for the bottom layer that covers the end of the menu. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:12:\"adjustItemsH\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:211:\"Adjusts the height calculation of the menulayers of the first level (called Top)<br>&nbsp;<b><br>Example:</b><br>-10<br>&nbsp;<br>This value will substract 10 pixels from the height of the layer in calculations.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:15:\"adjustSubItemsH\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:104:\"Adjusts the height calculation of the menulayers of the second level (subitems, called Sub)<br>See above\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:19:\"arrowNO<br>arrowACT\";s:8:\"datatype\";s:11:\"imgResource\";s:11:\"description\";s:407:\"If both arrowNO and arrowACT is defined and valid imgResources then these images are use as &#x201c;traditional arrows&#x201d; that indicates whether an item is expanded (active) or not. <br>NO is normal, ACT is expanded<br>The image is inserted just before the menuitem. If you want to change the position, put the marker ###ARROW_IMAGE### into the wrap of the item and the image will be put there instead.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:14:\"arrowImgParams\";s:8:\"datatype\";s:18:\"&lt;img&gt; params\";s:11:\"description\";s:80:\"Parameters to the arrow-image.<br>&nbsp;<b><br>Example:</b><br>hspace=5 vspace=7\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:19:\"displayActiveOnLoad\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:71:\"If set, then the active menu items will fold out &#x201c;onLoad&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (107,7389802,'bec5811d03462a9bb1c8e461c032d461','','tsref:(cObject).HMENU.(mObj).TMENU','a:1:{s:4:\"rows\";a:10:{i:0;a:6:{s:8:\"property\";s:6:\"expAll\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:252:\"If this is true, the menu will always show the menu on the level underneath the menuitem. This corresponds to a situation where a user has clicked a menuitem and the menu folds out the next level. This can enable that to happen on all items as default.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:8:\"collapse\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:116:\"If set, &quot;active&quot; menuitems that has expanded the next level on the menu will now collapse that menu again.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:9:\"accessKey\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:44:\"If set access-keys are set on the menu-links\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:6:\"noBlur\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:212:\"Normally links are &quot;blurred&quot; if the browser is MSIE. Blurring removes the ugly box around a clicked link. <br>If this property is set, the link is NOT blurred (browser-default) with &quot;onFocus&quot;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:6:\"target\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:23:\"Target of the menulinks\";s:7:\"default\";s:4:\"self\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:14:\"forceTypeValue\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:89:\"If set, the &amp;type parameter of the link is forced to this value regardless of target.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:87:\"Wraps the whole item using stdWrap<br>&nbsp;<br>&nbsp;<b>Example:</b> see GMENU.stdWrap\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:43:\"Wraps only if there were items in the menu!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:9:\"IProcFunc\";s:8:\"datatype\";s:13:\"function-name\";s:11:\"description\";s:339:\"The internal array &#x201c;I&#x201d; is passed to this function and expected returned as well. Subsequent to this function call the menu item is compiled by implode()&#039;ing the array $I[parts] in the passed array. Thus you may modify this if you need to.<br>See example on the testsite and in media/scripts/example_itemArrayProcFunc.php\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:31:\"[Common Item States, see above]\";s:8:\"datatype\";s:14:\"-&gt;TMENUITEM\";s:11:\"description\";s:221:\"This is the TMENUITEM-options for each category of menuitem that can be generated.<br>&nbsp;<b><br>SPECIAL:</b><br>The -&gt;OptionSplit function is run on the whole GIFBUILDER-configuration before the items are generated.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (108,7389802,'6741d93da8500fc90e7237a7a0cf9479','','tsref:(cObject).HMENU.(mObj).TMENUITEM','a:1:{s:4:\"rows\";a:23:{i:0;a:6:{s:8:\"property\";s:7:\"allWrap\";s:8:\"datatype\";s:13:\"wrap /stdWrap\";s:11:\"description\";s:20:\"Wraps the whole item\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:15:\"wrapItemAndSub \";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:56:\"Wraps the whole item and any submenu concatenated to it.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:16:\"subst_elementUid\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:301:\"If set, all appearances of the string &#039;{elementUid}&#039; in the total element html-code (after wrapped in .allWrap} is substituted with the uid number of the menu item. <br>This is useful if you want to insert an identification code in the HTML in order to manipulate properties with JavaScript.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:12:\"RO_chBgColor\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:1411:\"If property RO is set (see below) then you can set this property to a certain set of parameters which will allow you to change the background color of eg. the tablecell when the mouse rolls over you text-link.<br>&nbsp;<b><br>Syntax:</b><br>[over-color] | [out-color] | [id-prefix]<br>&nbsp;<b><br>Example:</b><br>page = PAGE<br>page.typeNum = 0<br>page.10 = HMENU<br>page.10.wrap = &lt;table border=1&gt;|&lt;/table&gt;<br>page.10.1 = TMENU<br>page.10.1.NO {<br>&nbsp;&nbsp;&nbsp;allWrap = &lt;tr&gt;&lt;td valign=top id=&quot;1tmenu{elementUid}&quot; style=&quot;background:#eeeeee;&quot;&gt;|&lt;/td&gt;&lt;/tr&gt;<br>&nbsp;&nbsp;&nbsp;subst_elementUid = 1<br>&nbsp;&nbsp;&nbsp;RO_chBgColor = #cccccc | #eeeeee | 1tmenu<br>&nbsp;&nbsp;&nbsp;RO = 1<br>}<br>&nbsp;<br>This example will start out with the table cells in #eeeeee and change them to #cccccc (and back) when rolled over. The &#x201c;1tmenu&#x201d; string is a unique id for the menu items. You may not need it (unless the same menu items are more than once on a page), but the important thing is that the id of the table cell has the exact same label before the {elementUid} (red marks). The other important thing is that you DO set a default background color for the cell with the style-attribute (blue marking). If you do not, Mozilla browsers will behave a little strange by not capturing the mouseout event the first time it&#039;s triggered. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:6:\"before\";s:8:\"datatype\";s:13:\"HTML /stdWrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:9:\"beforeImg\";s:8:\"datatype\";s:11:\"imgResource\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:18:\"beforeImgTagParams\";s:8:\"datatype\";s:18:\"&lt;img&gt;-params\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:13:\"beforeImgLink\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:68:\"If set, this image is linked with the same &lt;A&gt; tag as the text\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:11:\"beforeROImg\";s:8:\"datatype\";s:11:\"imgResource\";s:11:\"description\";s:99:\"If set, &quot;.beforeImg&quot; and &quot;.beforeROImg&quot; is expected to create a rollOver-pair. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:10:\"beforeWrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:40:\"wrap around the &quot;.before&quot;-code\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:8:\"linkWrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:7:\"stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:26:\"stdWrap to the link-text! \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:14:\"ATagBeforeWrap\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:10:\"ATagParams\";s:8:\"datatype\";s:38:\"&nbsp;<i>&lt;A&gt;-params</i> /stdWrap\";s:11:\"description\";s:81:\"Additional parameters<br>&nbsp;<b><br>Example:</b><br>class=&#x201d;board&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:9:\"ATagTitle\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:280:\"Allows you to specify the &quot;title&quot; attribute of the &lt;a&gt; tag around the menu item.<br>&nbsp;<b><br>Example:</b><br>ATagTitle.field = abstract // description<br>&nbsp;<br>This would use the abstract or description field for the &lt;a title=&quot;&quot;&gt; attribute.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:16:\"additionalParams\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:175:\"Define parameters that are added to the end of the URL. This must be code ready to insert after the last parameter.<br>&nbsp;<br>For details, see typolink-&gt;additionalParams\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:11:\"doNotLinkIt\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:43:\"if set, the linktext are not linked at all!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:13:\"doNotShowLink\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:62:\"if set, the text will not be shown at all (smart with spacers)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:8:\"stdWrap2\";s:8:\"datatype\";s:13:\"wrap /stdWrap\";s:11:\"description\";s:127:\"stdWrap to the total link-text and ATag. (Notice that the plain default value passed to stdWrap function is &#x201c;|&#x201c;.)\";s:7:\"default\";s:8:\"&nbsp;| \";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:19;a:6:{s:8:\"property\";s:2:\"RO\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:41:\"if set, rollOver is enabled for this link\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:20;a:6:{s:8:\"property\";s:8:\"after...\";s:8:\"datatype\";s:7:\"[mixed]\";s:11:\"description\";s:233:\"The series of &#x201c;before...&#x201d; properties are duplicated to &#x201c;after...&#x201d; properties as well. The only difference is that the output generated by the .after.... properties are placed after the link and not before.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:21;a:6:{s:8:\"property\";s:9:\"altTarget\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:70:\"Alternative target overriding the target property of the TMENU if set.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:22;a:6:{s:8:\"property\";s:10:\"allStdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:25:\"stdWrap of the whole item\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (109,7389802,'0ab0ced808e0b089231bd8b12ab07fca','','tsref:(cObject).HMENU.(mObj).IMGMENU','a:1:{s:4:\"rows\";a:10:{i:0;a:6:{s:8:\"property\";s:6:\"target\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:23:\"Target of the menulinks\";s:7:\"default\";s:4:\"self\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:14:\"forceTypeValue\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:89:\"If set, the &amp;type parameter of the link is forced to this value regardless of target.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:6:\"noBlur\";s:8:\"datatype\";s:7:\"Boolean\";s:11:\"description\";s:222:\"Normally graphical links are &quot;blurred&quot; if the browser is MSIE. Blurring removes the ugly box around a clicked link. <br>If this property is set, the link is NOT blurred (browser-default) with &quot;onFocus&quot;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:6:\"params\";s:8:\"datatype\";s:18:\"&lt;img&gt;-params\";s:11:\"description\";s:6:\"&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:4:\"main\";s:8:\"datatype\";s:15:\"-&gt;GIFBUILDER\";s:11:\"description\";s:75:\"Main configuration of the image-map! This defines the &quot;underlay&quot;!\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:9:\"dWorkArea\";s:8:\"datatype\";s:13:\"offset + calc\";s:11:\"description\";s:78:\"Main offset of the GIFBUILDER-items (also called the &quot;distribution&quot;)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:31:\"[Common Item States, see above]\";s:8:\"datatype\";s:30:\"-&gt;IMGMENUITEM<br>+ .distrib\";s:11:\"description\";s:1359:\"This is the TMENUITEM-options for each category of menuitem that can be generated.<br>&nbsp;<b><br>SPECIAL:</b><br>The -&gt;OptionSplit function is run on the whole GIFBUILDER-configuration before the items are generated.<br>&nbsp;<br>&nbsp;<b>.distrib</b> is (x,y,v,h +calc) of the distribution of the menuitems. This provides a way to space each item from the other. The codes &quot;textX&quot; and &quot;textY&quot; can be used for the width (X) and height (Y) dimension of each link.<br>This works by adding a WORKAREA-GifBuilderObj between each of the IMGMENUITEM (&quot;subset&quot; of a GIFBUILDER-object) and this workarea defines where the text should be printed. As such the &quot;x,y&quot; defines the offset <b><i>the next item will have</b></i> (this should be the width of the previous in many cases!) and &quot;v,h&quot; defines the <b><i>dimensions of the current item</b></i>. <br>Consider this example taken from the static_template &quot;template: MM&quot;:<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;NO.distrib = textX+10, 0, textX+10, textY+5<br>In the future TypoScript may provide better ways to position GIFBUILDER-objects on the image-maps!<br>&nbsp;<br>&nbsp;<b>ImgMap</b> is automatically used on the links! (that is the &quot;.imgMap&quot; property of the text-objects in the GIFBUILDER-objects is set automatically, unless is allready set.)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:12:\"imgMapExtras\";s:8:\"datatype\";s:20:\"&lt;area...&gt;-tags\";s:11:\"description\";s:43:\"Extra &lt;area...&gt;tags for the image-map\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:21:\"debugRenumberedObject\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:104:\"if set, the final GIFBUILDER object configuration is output in order for you to debug your configuration\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (110,7389802,'cdccc471d5b78c74bb04998ced50107c','','tsref:(cObject).HMENU.(mObj).IMGMENUITEM','a:1:{s:4:\"rows\";a:1:{i:0;a:6:{s:8:\"property\";s:10:\"1,2,3,4...\";s:8:\"datatype\";s:18:\"-&gt;GifBuilderObj\";s:11:\"description\";s:2298:\"<b>NOTE:</b><br>The way a imagemap is made is this; All IMGMENUITEMS are included in one big Gifbuilderobj (and renumbered!!). Because of this, Gifbuilderobjects on the next level will not be able to access the data of each menuitem.<br>Also the feature of using [##.w] and [##.h] with +calc is currently not supported by IMGMENUITEMs.<br>Therefore all IMAGE-objects on the first level is checked; if &quot;file&quot; or &quot;mask&quot; for any IMAGE-objects are set to &quot;GIFBUILDER&quot;, the Gifbuilder-object is parsed to see if any TEXT-objects are present and if so, the TEXT-object is &quot;checked&quot; - which means, that the stdWrap-function is called at a time where the $cObj-&gt;data-array is set to the actual menuitem.<br>In the example below, the text of each menuitem is rendered by letting the title be rendered on a mask instead of directly on the image. Please observe that the &quot;NO.10&quot;-object is present in order for the image-map coordinates to be generated!!<br>&nbsp;<br>&nbsp;&nbsp;&nbsp;NO.6 = IMAGE<br>&nbsp;&nbsp;&nbsp;NO.6.file = masked_pencolor*.gif<br>&nbsp;&nbsp;&nbsp;NO.6.mask = GIFBUILDER<br>&nbsp;&nbsp;&nbsp;NO.6.mask {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;XY = 500, 200<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;backColor = black<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10 = TEXT<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10 {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;text.field = title<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontFile = fileadmin/fonts/caflisch.ttf<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontSize = 34<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontColor = white<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;angle = 15<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;offset = 48,110<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20 = EFFECT<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.value = blur=80<br>&nbsp;&nbsp;&nbsp;} <br>&nbsp;&nbsp;&nbsp;NO.10 = TEXT<br>&nbsp;&nbsp;&nbsp;NO.10 {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;text.field = title<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontFile = fileadmin/fonts/caflisch.ttf<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fontSize = 34<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;angle = 15<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;offset = 48,110<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;hideButCreateMap = 1<br>&nbsp;&nbsp;&nbsp;}\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (111,7389802,'2cdd9060dcb2633b93d2a58448f1323e','','tsref:(cObject).HMENU.(mObj).JSMENU','a:1:{s:4:\"rows\";a:9:{i:0;a:6:{s:8:\"property\";s:6:\"levels\";s:8:\"datatype\";s:8:\"int, 1-5\";s:11:\"description\";s:25:\"How many levels there are\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:8:\"menuName\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:111:\"JavaScript menu name. <br>If you have more than one JSMENU on the page, you should set this value for each one.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:6:\"target\";s:8:\"datatype\";s:6:\"target\";s:11:\"description\";s:32:\"Decides target of the menu-links\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:14:\"forceTypeValue\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:89:\"If set, the &amp;type parameter of the link is forced to this value regardless of target.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:10:\"1,2,3,4...\";s:8:\"datatype\";s:10:\"JSMENUITEM\";s:11:\"description\";s:13:\"levels-config\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:30:\"wrap around the selector-boxes\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:13:\"wrapAfterTags\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:66:\"wrap around the selector-boxes with wrap and form-tags og JS-code.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:17:\"firstLabelGeneral\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:71:\"General firstlabel. May be overridden by the one set in each JSMENUITEM\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:3:\"SPC\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:46:\"If set, spacer can go into the menu, else not.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (112,7389802,'52b4fd3e8e7b15fb48cd362b5fc056cd','','tsref:(cObject).HMENU.(mObj).JSMENUITEM','a:1:{s:4:\"rows\";a:9:{i:0;a:6:{s:8:\"property\";s:6:\"noLink\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:407:\"Normally the selection of a menu item in the selector box will update the selector on the next level (if there is a next level) and if there are no items for that selector (because there were no subpages), then the link jumps to the page of itself. <br>If this flag is set, however, no menuitems in the selector box will ever link to anything. Only update the content of the next selector box on next level.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:10:\"alwaysLink\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:100:\"If set an item in the menu selector will always link. This takes precedence over &quot;noLink&quot;.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:10:\"showFi rst\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:62:\"if set, the first link will be shown when the menu is updated.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:10:\"showActive\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:53:\"if set, the active level will be selected, if present\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:4:\"wrap\";s:8:\"datatype\";s:4:\"wrap\";s:11:\"description\";s:21:\"wraps the selectorbox\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:5:\"width\";s:8:\"datatype\";s:4:\"int+\";s:11:\"description\";s:61:\"Initial width of the boxes set by a number of _ (underscores)\";s:7:\"default\";s:2:\"14\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:8:\"elements\";s:8:\"datatype\";s:4:\"int+\";s:11:\"description\";s:98:\"Initial number of elements in the menu. This is of course overruled by the actual menu item texts.\";s:7:\"default\";s:1:\"5\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:16:\"additionalParams\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:145:\"Additional parameters to the &lt;select&gt; box. Eg, you could set the width with a style-parameter like this:<br>style=&quot;width: 200px;&quot;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:10:\"firstLabel\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:48:\"Firt label in top of the menu (default is blank)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (113,7389802,'d5d05a1da766c8752918259cd571b75f','','tsref:(script).fe_adminLib','a:1:{s:4:\"rows\";a:31:{i:0;a:6:{s:8:\"property\";s:12:\"templateFile\";s:8:\"datatype\";s:8:\"resource\";s:11:\"description\";s:67:\"The template file, see examples in media/scripts/fe_user_admin.tmpl\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:15:\"templateContent\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:78:\"Alternatively you can set this property directly to the value of the template.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:5:\"table\";s:8:\"datatype\";s:9:\"tablename\";s:11:\"description\";s:289:\"The table to edit. <br>Notice: The ultimate lsit of fields allowed to be edited for the table is defined in TCA with the key [&quot;feInterface&quot;][&quot;fe_admin_fieldList&quot;] for each table in question. For an example, see the table definition for fe_users which is a good example.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:10:\"defaultCmd\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:86:\"Defines which action should be default (if &amp;cmd= is not set when calling the page)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:17:\"clearCacheOfPages\";s:8:\"datatype\";s:25:\"<i>[list of integers]</i>\";s:11:\"description\";s:114:\"This is a list of page-ids for which to clear the cache on any successfull operation be it EDIT, CREATE og DELETE.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:5:\"debug\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:86:\"If set, debug information will be output from fe_adminLib which helps to track errors.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:15:\"<b>Actions:</b>\";s:8:\"datatype\";N;s:11:\"description\";N;s:7:\"default\";N;s:12:\"column_count\";i:1;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:5:\"edit \";s:8:\"datatype\";s:21:\"boolean /actionObject\";s:11:\"description\";s:1880:\"If set, editing is basically allowed.<br>But you need to specify:<br>&nbsp;<br>&nbsp;<b>.fields </b>(list of fieldnames) which determines the fields allowed for editing. Every field in this list must be found as well in the [&quot;feInterface&quot;][&quot;fe_admin_fieldList&quot;] found in the TCA array which ultimately determines which fields can be edited by the fe_adminLib.<b><br>&nbsp;</b><br>&nbsp;<b>.overrideValues.[fieldname]</b> (value string) defines values for specific fields which will override ANY input from the form. Overriding values happens after the outside values has been parsed by the .parseValues-property of fe_adminLib but before the evaluation by .required and .evalValues below. For example this may be useful if you wish to hide a record which is being edited, because you want to preview it first.<b><br>&nbsp;</b><br>&nbsp;<b>.required </b>(list of fieldnames, subset of .fields) which determines which fields are required to return a true value. The valid fields entered here will have the subpart ###SUB_REQUIRED_FIELD_<i>[fieldname]</i>### removed from the templates if they evaluates to being true and thereby OK. See below for information about this subpart.<b><br>&nbsp;</b><br>&nbsp;<b>.evalValues.[fieldname]</b> (list of eval-codes) defines specific evaluation forms for the individual fiels of the form. See below.<b><br>&nbsp;</b><br>&nbsp;<b>.preview</b> (boolean) will enable the form submitted to be previewed first. This requires a template for preview to be found in the template file. See below for subpart marker names.<b><br>&nbsp;</b><br>&nbsp;<b>.menuLockPid</b> (boolean will force the menu of editable items to be locked to the .pid (edit only)<b><br>&nbsp;</b><br>&nbsp;<b>.userFunc_afterSave </b>(function-name) is called after the record is saved. The content passed is an array with the current (and previous) record in.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:7:\"create \";s:8:\"datatype\";s:21:\"boolean /actionObject\";s:11:\"description\";s:443:\"The same as .edit above except where otherwise stated.<br>Plus there is these additional properties:<b><br>&nbsp;</b><br>&nbsp;<b>.noSpecialLoginForm </b>(boolean) - if set, fe_adminLib does NOT look for the subpart marker TEMPLATE_CREATE_LOGIN but always for TEMPLATE_CREATE<b><br>&nbsp;</b><br>&nbsp;<b>.defaultValues.[fieldname]</b> (value string); Like .overrideValues but this sets the default values the first time the form is displayed.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:6:\"delete\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:218:\"Whether or not records may be deleted. Still regular authentication (ownership or email authCode) is required. Setting the var &#x201c;preview&#x201d; lets you make a delete-preview before actually deleting the record.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:8:\"infomail\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:374:\"Infomails are plaintext mails based on templates found in the template file. They may be used for such as sending a forgotten password to a user, but what goes into the infomail is totally up to your design of the template.<br>Normally you may have only a default infomail (infomail.default) for instance for sending the password. But you can use other keys also. See below.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:14:\"infomail.[key]\";s:8:\"datatype\";s:38:\"(configuration of infomail properties)\";s:11:\"description\";s:619:\"In order to make fe_adminLib send an infomail, you must specify these vars in your GET vars or HTML-form.<br>&nbsp;<br>&nbsp;<b>fetch</b> - if integer, it searches for the uid being the value of &#039;fetch&#039;. If not, it searches for the email-field (defined by a property of fe_adminLib, see below). <br>&nbsp;<br>&nbsp;<b>key</b> - points to the infomail.[key] configuration to use<b><br>&nbsp;</b><b><br>Properties:</b><br>&nbsp;<b>.dontLockPid</b> (boolean) - selects only records from the .pid of fe_adminLib.<br>&nbsp;<b>.label </b>(string) - The suffix for the markers, see &#039;Email Markers&#039; beneath.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:8:\"setfixed\";s:8:\"datatype\";s:19:\"boolean /properties\";s:11:\"description\";s:3167:\"Allows set-fixed input, probably coming from a link in an infomail or notification mail.<b><br>Syntax: </b><br>&nbsp;<br>&nbsp;<b>.[fix</b><b><i>key</b></i><b>].[</b><b><i>fieldname</b></i><b>] = </b><b><i>fieldvalue</b></i><i> </i>- is used to setup a setfixed-link insertable in the infomail by the SYS_SETFIXED_*-markers. See above (setfixed-property of fe_adminLib).<br>Special fixkey &#039;DELETE&#039; is just a boolean.<br>&nbsp;<br>&nbsp;<b>.userFunc_afterSave </b>(function-name) is called after the record is saved. The content passed is an array with the current (and previous) record in.<br>&nbsp;<b><br>Concept:</b><br>The &#039;setfixed&#039; concept is best explained by describing a typical scenario - in fact the most common situation of its use:<br>Imagine you have some users submitting information on your website. But before that information enters the database, you would like to moderate it - simply preview it and then either delete it or approve it. In the &#039;create&#039; configuration of fe_adminLib, you set up the hidden field of the record to be overridden to 1. Thus the record is hidden by default. Then you configure a setfixed-fixkey to set the hidden field to 0. This set up generates a list of parameters for use in an URL and those parameters are finally inserted by a corresponding marker in the email template. The link includes all necessary authentication to perform the change of values and thus a single click on that link is enough to change the field values. So this will - by a single click of a link in a notification mail sent to an admin - enable the record! Or of course a similar link with a cmd=delete link will delete it...<br>There is a special &#x201c;fieldname&#x201d; you can use, which is &#039;_FIELDLIST&#x201d; and that lets you specify a list of fields in the record to base the auth-code on. If nothing is specifyed the md5-hash is based on the whole record which means that any changes will disable the setfixed link. If on the other hand, you set _FIELDLIST = uid,pid then that record will be editable as long as the uid and pid values are intact.<b><br>Example:</b><br>This is a common configuration of the email-properties with a simple setfixed setting:<br>&nbsp;&nbsp;&nbsp;email.from = kasper@typo3.com<br>&nbsp;&nbsp;&nbsp;email.fromName = Kasper Sk&#xe5;rh&#xf8;j<br>&nbsp;&nbsp;&nbsp;email.admin = kasper@typo3.com<br>&nbsp;&nbsp;&nbsp;setfixed.approve {<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;hidden = 0<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;_FIELDLIST = uid,pid<br>&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;setfixed.DELETE = 1<br>&nbsp;&nbsp;&nbsp;setfixed.DELETE._FIELDLIST = uid<br>&nbsp;<br>Now, if you insert this marker in your email template <br>&nbsp;<br>&nbsp;&nbsp;&nbsp;###SYS_SETFIXED_approve### <br>&nbsp;<br>if will get substituted with something like these parameters:<br>&nbsp;<br>&amp;cmd=setfixed&amp;rU=9&amp;fD[hidden]=0&amp;aC=5c403d90 <br>&nbsp;<br>Now, all you need is to point that to the correct url (where fe_adminLib is invoked!), eg:<br>&nbsp;<br>###THIS_URL######FORM_URL######SYS_SETFIXED_approve###<br>&nbsp;<br>and for deletion:<br>&nbsp;<br>...###SYS_SETFIXED_DELETE###\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:13:\"<b>Others</b>\";s:8:\"datatype\";N;s:11:\"description\";N;s:7:\"default\";N;s:12:\"column_count\";i:1;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:15:\"authcodeFields \";s:8:\"datatype\";s:23:\"<i>[list of fields]</i>\";s:11:\"description\";s:1352:\"Comma separated list of fields to base the authCode generation on. Basically this list would include &#x201c;uid&#x201d; only in most cases. If the list includes more fields, you should be aware that the authCode will change when the value of that field changes. And then the user will have to re-send an email to himself with a new code.<br>&nbsp;<br>&nbsp;<b>.addKey </b>(string) adds the string to the md5-hash of the authCode. Just enter any random string here. Point is that people from outside doesn&#039;t know this code and therefore are not able to reconstruct the md5-hash solely based on the uid<br>&nbsp;<br>&nbsp;<b>.addDate</b> (date-config) You can use this to make the code time-disabled. Say if you enter &#x201c;d-m-Y&#x201d; here as value, the code will work until midnight and then a new code will be valid.<br>&nbsp;<br>&nbsp;<b>.codeLength</b> (int) Defines how long the authentication code should be. Default is 8 characters.<br>In any case TYPO3_CONF_VARS[SYS][encryptionKey] is prepended.<br>&nbsp;<b><br>Advice:</b><br>If you want to generate authCodes compatible with the standard authCodes (used by the direct mailer by t3lib_div::stdAuthCode()), please set TYPO3_CONF_VARS[SYS][encryptionKey] to a unique and secret key (like you should in any case) and add &#x201c;uid&#x201d; as authcodeField ONLY. This is secure enough.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:5:\"email\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:558:\"&nbsp;<b>.from </b>(string, email) Defines the sender email address of mails sent out<br>&nbsp;<br>&nbsp;<b>.fromName </b>(string) Defines the name of the sender. If set, this will be used on the form NAME &lt;EMAIL&gt;<br>&nbsp;<b><br>.admin</b><br>&nbsp;<br>&nbsp;<b>.field </b>(string/integer) Defines the fieldname of the record where the email address to send to is found. If the field content happens to be an integer, this is assumed to be the uid of the fe_user owning the record and the email address of that user is fetched for the purpose instead.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:3:\"pid\";s:8:\"datatype\";s:4:\"int+\";s:11:\"description\";s:42:\"The pid in which to store/get the records.\";s:7:\"default\";s:12:\"Current page\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:14:\"fe_userOwnSelf\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:361:\"If set, fe_users created by this module has their fe_cruser_id-field set to their own uid which means they &#039;own&#039; their own record and can thus edit their own data. <br>All other tables which has a fe_cruser_id field configured in the &#039;ctrl&#039; section of their $TCA-configuration will automatically get this field set to the current fe_user id.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:15:\"fe_userEditSelf\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:102:\"If set, fe_users - regardless of whether they own themselves or not - will be allowed to edit himself.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:19;a:6:{s:8:\"property\";s:13:\"allowedGroups\";s:8:\"datatype\";s:18:\"[list of integers]\";s:11:\"description\";s:141:\"List of fe_groups uid numbers which are allowed to edit the records through this form. Normally only the owner fe_user is allowed to do that.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:20;a:6:{s:8:\"property\";s:8:\"evalFunc\";s:8:\"datatype\";s:13:\"function-name\";s:11:\"description\";s:280:\"Function by which you can manipulate the dataArray before it&#039;s saved. <br>The dataArray is passed to the function as $content and MUST be returned again from the function.<br>The property &#x201c;parentObj&#x201d; is a hardcoded reference to the fe_adminLib object.<br>&nbsp;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:21;a:6:{s:8:\"property\";s:7:\"formurl\";s:8:\"datatype\";s:13:\"-&gt;typolink\";s:11:\"description\";s:66:\"Contains typolink properties for the URL (action tag) of the form.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:22;a:6:{s:8:\"property\";s:19:\"parseValues.[field]\";s:8:\"datatype\";s:20:\"[list of parseCodes]\";s:11:\"description\";s:1510:\"<b>ParseCodes:</b><br>&nbsp;<b>int </b>- returns the integer value of the input<br>&nbsp;<b>lower</b> - returns lowercase version of the input<br>&nbsp;<b>upper </b>- returns uppercase version of the input<br>&nbsp;<b>nospace</b> - strips all space<br>&nbsp;<b>alpha, num, alphanum, alphanum_x</b> - only alphabetic (a-z) and/or numeric chars. alphanum_x also allows _ and -<br>&nbsp;<b>trim </b>- trims whitespace in the ends of the string<br>&nbsp;<b>setEmptyIfAbsent </b>- will make sure the field is set to empty if the value is not submitted. This ensures a field to be updated an is handy with checkboxes<br>&nbsp;<b>random[x]</b> - Returns a random number between 0 and x<br>&nbsp;<b>files[semicolon-list(!) of extensions, none=all][maxsize in kb, none=no limit]</b> - Defining the field to hold files. See below for details!<br>&nbsp;<b>multiple </b>- Set this, if the input comes from a multiple-selector box (remember to add ...[] to the fieldname so the values come in an array!)<br>&nbsp;<b>checkArray</b> - Set this, if you want several checkboxes to set bits in a single field. In that case you must prepend every checkbox with [x] where x is the bitnumber to set starting with zero. The default values of the checkbox form elements must be false.<br>&nbsp;<b>uniqueHashInt[semicolon-list(!) of other fields]</b> - This makes a unique hash (32 bit integer) of the content in the specified fields. The values of those fields are first converted to lowercase and only alphanum chars are preserved. \";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:23;a:6:{s:8:\"property\";s:20:\"userFunc_updateArray\";s:8:\"datatype\";s:13:\"function-name\";s:11:\"description\";s:148:\"Points to a user function which will have the value-array passed to it before the value array is used to construct the update-JavaScript statements.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:24;a:6:{s:8:\"property\";s:29:\"evalErrors.[field].[evalCode]\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:165:\"This lets you specify the error messages inserted in the ###EVAL_ERROR_FIELD_[fieldname]### markers upon an evaluation error.<br>See description of evaluation below.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:25;a:6:{s:8:\"property\";s:22:\"cObjects.[marker_name]\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:389:\"This is cObjects you can insert by markers in the template.<br>&nbsp;<b><br>Example:</b><br>Say, you set up a cObject like this:<br>&nbsp;<br>cObject.myHeader = TEXT<br>cObject.myHeader.value = This is my header<br>&nbsp;<br>then you can include this cObject in most of the templates through a marker named ###CE_myHeader### or ###PCE_myHeader### (see below for details on the difference).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:26;a:6:{s:8:\"property\";s:5:\"wrap1\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:225:\"Global Wrap 1. This will be splitted into the markers ###GW1B### and ###GW1E###. Don&#039;t change the input value by the settings, only wrap it in something.<br>&nbsp;<b><br>Example:</b><br>wrap1.wrap = &lt;b&gt; |&lt;/B&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:27;a:6:{s:8:\"property\";s:5:\"wrap2\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:25:\"Global Wrap 2 (see above)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:28;a:6:{s:8:\"property\";s:6:\"color1\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:43:\"Value for ###GC1### marker (Global color 1)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:29;a:6:{s:8:\"property\";s:6:\"color2\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:43:\"Value for ###GC2### marker (Global color 2)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:30;a:6:{s:8:\"property\";s:6:\"color3\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:43:\"Value for ###GC3### marker (Global color 3)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (114,7389802,'63743e1b082d6f7bcb449ddeb484466e','','tsref:(script).fe_adminLib.evalErrors.(field).(evalCode)','a:1:{s:4:\"rows\";a:8:{i:0;a:6:{s:8:\"property\";s:12:\"uniqueGlobal\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:149:\"This requires the value of the field to be globally unique, which means it must not exist in the same field of any other record in the current table.\";s:7:\"default\";N;s:12:\"column_count\";i:2;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:11:\"uniqueLocal\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:193:\"This is like uniqueGlobal, but the value is required to be unique <i>only</i> in the PID of the record. Thus if two records has different pid values, they may have the same value of this field.\";s:7:\"default\";N;s:12:\"column_count\";i:2;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:5:\"twice\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:349:\"This requires the value of the field to match the value of a secondary field name [fieldname]_again sent in the incoming formdata. THis is useful for entering password. Then if your password field is name &#x201c;user_pass&#x201d; then you simple add a second field name &#x201c;user_pass_again&#x201d; and then set the &#039;twice&#039; eval code. \";s:7:\"default\";N;s:12:\"column_count\";i:2;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:5:\"email\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:91:\"Requires the field value to be an email address at least on the form [name]@*[domain].[tld]\";s:7:\"default\";N;s:12:\"column_count\";i:2;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:8:\"required\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:70:\"Just simple required (trimmed value). 0 (zero) will evaluate to false!\";s:7:\"default\";N;s:12:\"column_count\";i:2;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:17:\"atLeast<br>atMost\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:142:\"Specifies a minimum / maximum of characters to enter in the fields.<br>&nbsp;<b>Example</b>, that requires at least 5 characters: atleast [5] \";s:7:\"default\";N;s:12:\"column_count\";i:2;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:8:\"inBranch\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:320:\"inBranch requires the value (typically of a pid-field) to be among a list of page-id&#039;s (pid&#039;s) specified with the inBranch parameters. The parameters are given like <i>[root_pid; depth; beginAt]</i><br>&nbsp;<b>Example</b>, which will return a list of pids one level deep from page 4 (included): inBranch [4;1]\";s:7:\"default\";N;s:12:\"column_count\";i:2;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:10:\"unsetEmpty\";s:8:\"datatype\";s:6:\"&nbsp;\";s:11:\"description\";s:196:\"This evaluation does not result in any error code. Only it simply unsets the field if the value of the field is empty. Thus it&#039;ll not override any current value if the field value is not set.\";s:7:\"default\";N;s:12:\"column_count\";i:2;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (115,7389802,'63839135a1a4b8831ae951b0be3e42e0','','tsref:(script).tipafriend','a:1:{s:4:\"rows\";a:10:{i:0;a:6:{s:8:\"property\";s:12:\"templateFile\";s:8:\"datatype\";s:8:\"resource\";s:11:\"description\";s:88:\"The template-file. <br>See example in &#039;media/scripts/tipafriend_template.tmpl&#039;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:4:\"code\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:53:\"Code to define, what the script does. Case sensitive.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:11:\"defaultCode\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:110:\"The default code (see above) if the value is empty. By default it&#039;s not set and a help screen will appear\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:5:\"wrap1\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:225:\"Global Wrap 1. This will be splitted into the markers ###GW1B### and ###GW1E###. Don&#039;t change the input value by the settings, only wrap it in something.<br>&nbsp;<b><br>Example:</b><br>wrap1.wrap = &lt;B&gt; |&lt;/B&gt;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:5:\"wrap2\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:25:\"Global Wrap 2 (see above)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:6:\"color1\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:43:\"Value for ###GC1### marker (Global color 1)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:6:\"color2\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:43:\"Value for ###GC2### marker (Global color 2)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:6:\"color3\";s:8:\"datatype\";s:15:\"string /stdWrap\";s:11:\"description\";s:43:\"Value for ###GC3### marker (Global color 3)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:8:\"typolink\";s:8:\"datatype\";s:13:\"-&gt;typolink\";s:11:\"description\";s:129:\"TypoLink configuration for the TIPLINK to the TIPFORM page. .additionalParams is added the parameter &#x201c;&amp;tipUrl=&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:8:\"htmlmail\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:92:\"If set, the page is fetched as HTML and send in HTML (a plain text version is sent as well).\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (116,7389802,'824b1ba31d52dfeb00b9afbcb7790c9b','','tsref:(script).plaintextLib','a:1:{s:4:\"rows\";a:28:{i:0;a:6:{s:8:\"property\";s:7:\"siteUrl\";s:8:\"datatype\";s:3:\"url\";s:11:\"description\";s:16:\"Url of the site.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:13:\"defaultOutput\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:40:\"Default output if CType is not rendered.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:14:\"uploads.header\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:18:\"Header for uploads\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:13:\"images.header\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:17:\"Header for images\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:20:\"images.captionHeader\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:24:\"Header for imagecaptions\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:17:\"images.linkPrefix\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:22:\"Prefix for image-links\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:14:\"<b>.header</b>\";s:8:\"datatype\";N;s:11:\"description\";N;s:7:\"default\";N;s:12:\"column_count\";i:1;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:11:\"defaultType\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:28:\"Which type to use as default\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:4:\"date\";s:8:\"datatype\";s:11:\"date-config\";s:11:\"description\";s:15:\"For header date\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:10:\"datePrefix\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:22:\"Prefix for header date\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:10:\"linkPrefix\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:23:\"Prefix for header links\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:16:\"[1-5].preLineLen\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:28:\"Lenght of line before header\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:17:\"[1-5].postLineLen\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:27:\"Lenght of line after header\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:13;a:6:{s:8:\"property\";s:15:\"[1-5].preBlanks\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:35:\"Number of blank lines before header\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:14;a:6:{s:8:\"property\";s:16:\"[1-5].postBlanks\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:34:\"Number of blank lines after header\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:15;a:6:{s:8:\"property\";s:13:\"[1-5].stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:15:\"for header text\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:16;a:6:{s:8:\"property\";s:17:\"[1-5].preLineChar\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:21:\"Character to pre-line\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:17;a:6:{s:8:\"property\";s:18:\"[1-5].postLineChar\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:22:\"Character to post-line\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:18;a:6:{s:8:\"property\";s:19:\"[1-5].preLineBlanks\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:49:\"Number of blank lines between header and pre-line\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:19;a:6:{s:8:\"property\";s:20:\"[1-5].postLineBlanks\";s:8:\"datatype\";s:3:\"int\";s:11:\"description\";s:50:\"Number of blank lines between header and post-line\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:20;a:6:{s:8:\"property\";s:16:\"[1-5].autonumber\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:111:\"If set, a number is prepended every header. The number corresponds to the content element number in the select.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:21;a:6:{s:8:\"property\";s:12:\"[1-5].prefix\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:20:\"Header string prefix\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:22;a:6:{s:8:\"property\";s:23:\"bulletlist.[0-3].bullet\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:36:\"Bullet for bullet list, layout [0-3]\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:23;a:6:{s:8:\"property\";s:26:\"bulletlist.[0-3].secondRow\";s:8:\"datatype\";s:16:\"untrimmed string\";s:11:\"description\";s:65:\"If set, this is used for lines on the second row of bullet-lists.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:24;a:6:{s:8:\"property\";s:4:\"menu\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:137:\"cObject to render menu. The output is stripped for tags and the links is extracted. Further all &lt;BR&gt; chars are converted to chr(10)\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:25;a:6:{s:8:\"property\";s:8:\"shortcut\";s:8:\"datatype\";s:7:\"cObject\";s:11:\"description\";s:129:\"cObject to render other elements. See config below which simply uses this object to render more tt_content elements as plaintext.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:26;a:6:{s:8:\"property\";s:16:\"bodytext.stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:48:\"stdWrap for body-text. See config example below.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:27;a:6:{s:8:\"property\";s:8:\"userProc\";s:8:\"datatype\";s:13:\"function-name\";s:11:\"description\";s:199:\"Lets you proces the output of each content element before it finally is returned. Property &#x201c;parentObj&#x201d; of the conf-array holds a references to the plainText object calling the function.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
INSERT INTO static_tsconfig_help (uid, guide, md5hash, description, obj_string, appdata, title) VALUES (117,9021659,'f6d94840515a9e43422f184482e42e5c','','tsref:plugin.tx_indexedsearch','a:1:{s:4:\"rows\";a:13:{i:0;a:6:{s:8:\"property\";s:12:\"templateFile\";s:8:\"datatype\";s:8:\"resource\";s:11:\"description\";s:67:\"The template file, see examples in typo3/sysext/indexed_search/pi/.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:1;a:6:{s:8:\"property\";s:21:\"show.forbiddenRecords\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:131:\"Explicitely display search hits although the visitor has no access to it.<br>Notice: This behavior was different in TYPO3 &lt; 4.0.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:2;a:6:{s:8:\"property\";s:17:\"show.resultNumber\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:96:\"Display the numbers of search results.<br>Notice: This behavior was different in TYPO3 &lt; 4.0.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:3;a:6:{s:8:\"property\";s:23:\"show.advancedSearchLink\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:45:\"Display the link to the advanced search page.\";s:7:\"default\";s:1:\"1\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:4;a:6:{s:8:\"property\";s:18:\"search.rootPidList\";s:8:\"datatype\";s:11:\"list of int\";s:11:\"description\";s:514:\"A list of integer which should be root-pages to search from. Thus you can search multiple branches of the page tree by setting this property to a list of page id numbers.<br>If this value is set to less than zero (eg. -1) searching will happen in ALL of the page tree with no regard to branches at all.<br>&nbsp;<br>Notice that by &#x201c;root-page&#x201d; we mean a website root defined by <br>a TypoScript Template! If you just want to search in branches of your site, use the possibility of searching in levels.\";s:7:\"default\";s:24:\"The current root-page id\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:5;a:6:{s:8:\"property\";s:32:\"search.detect_sys_domain_records\";s:8:\"datatype\";s:7:\"boolean\";s:11:\"description\";s:86:\"If set, then the search results are linked to the proper domains where they are found.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:6;a:6:{s:8:\"property\";s:39:\"search.detect_sys_domain_records.target\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:25:\"Target for external URLs.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:7;a:6:{s:8:\"property\";s:16:\"search.mediaList\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:53:\"Restrict the file type list when searching for files.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:8;a:6:{s:8:\"property\";s:30:\"search.defaultFreeIndexUidList\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:136:\"List of Indexing Configuration Uids to show as categories in search form. The order determines the order displayed in the search result.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:9;a:6:{s:8:\"property\";s:15:\"specConfs.[pid]\";s:8:\"datatype\";s:1:\"-\";s:11:\"description\";s:967:\"&#x201c;specConfs&#x201d; is an array of objects with properties that can customize certain behaviours of the display of a result row depending on it&#039;s position in the rootline. For instance you can define that all results which links to pages in a branch from page id 123 should have another page icon displayed. Of you can add a suffix to the class names so you can style that section differently.<br>&nbsp;<b><br>Examples:</b><br>If a page &#x201c;Contact&#x201d; is found in a search for &#x201c;address&#x201d; and that &#x201c;Contact&#x201d; page is in the rootline &#x201c;Frontpage [ID=23] &gt; About us [ID=45] &gt; Contact [ID=77]&#x201d; then you should set the pid value to either &#x201c;77&#x201d; or &#x201c;45&#x201d;. If &#x201c;45&#x201d; then all subpages including the &#x201c;About us&#x201d; page will have similar configuration.<br>If the pid value is set to 0 (zero) it will apply to all pages.<br>&nbsp;<br>Please see the options below.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:10;a:6:{s:8:\"property\";s:24:\"specConfs.[pid].pageIcon\";s:8:\"datatype\";s:18:\"-&gt;IMAGE cObject\";s:11:\"description\";s:22:\"Alternative page icon.\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:11;a:6:{s:8:\"property\";s:25:\"specConfs.[pid].CSSsuffix\";s:8:\"datatype\";s:6:\"string\";s:11:\"description\";s:332:\"A string that will be appended to the class-names of all the class-attributes used within the result row presentation. The prefix will be like this:<br>&nbsp;<b><br>Example:</b><br>If &#x201c;...CSSsuffix = doc&#x201d; then eg. the class name &#x201c;tx-indexedsearch-title&#x201d; will be &#x201c;tx-indexedsearch-title-doc&#x201d;\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}i:12;a:6:{s:8:\"property\";s:14:\"whatis_stdWrap\";s:8:\"datatype\";s:12:\"-&gt;stdWrap\";s:11:\"description\";s:40:\"Parse input through the stdWrap function\";s:7:\"default\";s:6:\"&nbsp;\";s:12:\"column_count\";i:4;s:16:\"is_propertyTable\";i:1;}}}','');
